SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for game_info
-- ----------------------------
DROP TABLE IF EXISTS `game_info`;
CREATE TABLE `game_info`  (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '游戏名',
  `price` decimal(10, 2) NULL DEFAULT 0.00 COMMENT '价格',
  `cover_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '列表封面图(Header)',
  `developer` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '开发商',
  `release_date` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '发行日期',
  `short_description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '简短描述(用于列表)',
  `screenshots` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '存JSON数组：高清截图链接列表',
  `about_the_game` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '存HTML：关于游戏的详细图文介绍',
  `tags` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '标签',
  `status` int NULL DEFAULT 1,
  `game_features` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '游戏特性(逗号分隔)',
  `supported_languages` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '语言支持HTML原文本',
  `legal_notice` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '法律/分级信息(ESRB)',
  `website` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '官网链接',
  `metacritic_score` int NULL DEFAULT 0 COMMENT 'Metacritic评分',
  `metacritic_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT 'Metacritic链接',
  `steam_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '原版AppID(用于生成社区链接)',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 53 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of game_info
-- ----------------------------
INSERT INTO `game_info` VALUES (1, '黑神话：悟空', 59.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/header_schinese.jpg?t=1760601605', 'Game Science', '2024 年 8 月 19 日', '《黑神话：悟空》是一款以中国神话为背景的动作角色扮演游戏。你将扮演一位“天命人”，为了探寻昔日传说的真相，踏上一条充满危险与惊奇的西游之路。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/ss_86c4b7462bba219a0d0b89931a35812b9f188976.1920x1080.jpg?t=1760601605\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/ss_d9391ab31a4d15dddf7ba4949bfa44f5d9170580.1920x1080.jpg?t=1760601605\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/ss_524a39da392ee83dde091033562bc719d46b5838.1920x1080.jpg?t=1760601605\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/ss_968bbc9caceb7d798bd0c393e1e9b4c44ed6d835.1920x1080.jpg?t=1760601605\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/ss_415397426d4c939ebb8a93ac66831f28ee7199be.1920x1080.jpg?t=1760601605\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/ss_63477e8ce2c0582b81c6ed576377d78e692b5642.1920x1080.jpg?t=1760601605\"]', '《黑神话：悟空》是一款以中国神话为背景的动作角色扮演游戏。故事取材于中国古典小说“四大名著”之一的《西游记》。你将扮演一位“天命人”，为了探寻昔日传说的真相，踏上一条充满危险与惊奇的西游之路。<br><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/53c062bc38fc59440d6e9ae53e6e51f2.webp?t=1760601605\" width=1170 height=27 /></span><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/6d5d441bd83627bcbc6d2b8f8d15f0ff.poster.avif?t=1760601605\" width=1170 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/6d5d441bd83627bcbc6d2b8f8d15f0ff.webm?t=1760601605\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/6d5d441bd83627bcbc6d2b8f8d15f0ff.mp4?t=1760601605\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >• 雄奇壮丽，光怪陆离</h2>别有世间曾未见，一行一步一花新。欢迎来到亦真亦幻的东方魔幻世界！<br>天命人将前往多个引人入胜又风格迥异的西游故地，再次谱写前所未见的冒险史诗。<br><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/50a130565eba20a23c614617374844e6.poster.avif?t=1760601605\" width=1170 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/50a130565eba20a23c614617374844e6.webm?t=1760601605\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/50a130565eba20a23c614617374844e6.mp4?t=1760601605\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >• 妖魔鬼怪，卷土重来</h2>行者名声大，魔王手段强。《西游记》的一大看点，便是各有所长的厉害妖怪。<br>西行的旅途并非只有风光旖旎，天命人还将遭遇许多强大的敌人与可敬的对手，与他们豪快战斗，至死方休。<br><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/cac7e67395cc9fbe1463054a2004496a.poster.avif?t=1760601605\" width=1170 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/cac7e67395cc9fbe1463054a2004496a.webm?t=1760601605\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/cac7e67395cc9fbe1463054a2004496a.mp4?t=1760601605\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >• 山摇地动，各显神通</h2>炼就长生多少法，学来变化广无边。五光十色又相生相克的法术、变化与法宝，一直都是中国神话中最为招牌的战斗要素。<br>天命人除了精通不同棍术，更可自由搭配多样化的法术、变化、天赋、武器与披挂，找到最契合自身战斗风格的制胜之道。<br><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/717266ac34d8670ca14c77550aad5787.poster.avif?t=1760601605\" width=1170 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/717266ac34d8670ca14c77550aad5787.webm?t=1760601605\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2358720/extras/717266ac34d8670ca14c77550aad5787.mp4?t=1760601605\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >• 千形万象，荡气回肠</h2>六般体相六般兵，六样形骸六样情。敌人的凶恶狡猾只是表象，他们的来历、个性与动机，往往更加耐人寻味。<br>天命人在战斗之外，也会探寻各色人物背后的故事，了解他们的爱与恨，贪与嗔，前世与今生。', '动作,冒险,角色扮演', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,无需应对快速反应事件,立体声,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文<strong>*</strong>, 繁体中文, 意大利语, 土耳其语, 泰语, 捷克语<br><strong>*</strong>具有完全音频支持的语言', 'Copyright © Game Science Interactive Technology Co., Ltd. All Rights Reserved 游科互动科技有限公司', 'https://www.gamesci.cn/wukong', 0, '', '2358720');
INSERT INTO `game_info` VALUES (2, 'Street Fighter 6', 19.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/header.jpg?t=1761116384', 'CAPCOM Co., Ltd.', '2023 年 6 月 1 日', '系列最新作《Street Fighter 6》将于2023年6月2日发售！ 本作以RE ENGINE开发，并由系列前所未有的大型模式“环球游历”、“斗技战场”以及“格斗中心”构成。新的“Street Fighter”和前所未有的全新体验正在等待着你的到来。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_387137f8cccb048c35a8685634372e97785d40aa.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_a381f1b3b450c18900d47b991ce8e7456e9cdba5.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_f62ce93269a6d8e0027853358af4d6368e2c4b93.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_d186566a92ada8cdb08b04769a8c95cd1e380006.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_ed46aaa9ed94dd6f35a703070f8df16cee5aef61.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_10191fb5ab48cee99d7a43e636db9202938f143e.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_449d488d2edbe785c4e6010fede1c3b8dae8e388.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_2cfc2472e7ff99fd354935135d91e36e81951795.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_3ceb422dbef5f8495e690ed526f684fdcb1022ec.1920x1080.jpg?t=1761116384\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/ss_201488fc9f1aa5e29e2fc6d96ef2b66e41ddf32e.1920x1080.jpg?t=1761116384\"]', '系列最新作《Street Fighter 6》将于2023年6月2日发售！<br><br>本作以RE ENGINE开发，并由系列前所未有的大型模式“环球游历”、“斗技战场”以及“格斗中心”构成。<br><br>■极具个性的18位FIGHTERS<br>隆、春丽、卢克、杰米、金佰莉等从传奇到新世代的FIGHTER们，带着令人印象深刻的视觉效果和丰富多彩的招式，集结于此。<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/1403cce4f5ee33c3a5cd072aa8ef9a99.avif?t=1761116384\" width=600 height=338 /></span><br><br>■称霸这个战斗舞台吧<br>“斗技战场”中不仅有继承自系列的格斗模式，还提供打破指令输入这道墙壁的“Modern”类型操作，以及由AI根据情况作出支援的“动态方式”操作。此外，还配备对战自动实时旁述功能，让你在家中也能体验到犹如置身大赛中的临场感。<br>作为进攻和防守关键的“DRIVE SYSTEM”，能够有效地发动招式，感受华丽的演出和爽快的对战。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/65f6053f32d73fd63f096f6e02e2f935.poster.avif?t=1761116384\" width=600 height=254 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/65f6053f32d73fd63f096f6e02e2f935.webm?t=1761116384\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/65f6053f32d73fd63f096f6e02e2f935.mp4?t=1761116384\" type=\"video/mp4\"></video></span><br><br>■踏上追求强大的冒险<br>在单人游玩的沉浸式故事模式“环球游历”中，玩家可透过丰富的虚拟形象创建功能，创建一个将成为玩家分身的主角，与Street Fighter世界的居民互动，并向隆和春丽等拜师，学习招式，在与各样敌人对战的同时寻找“什么是强大？”这个问题的答案。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/0d370e1a388b09d604499934fea56399.poster.avif?t=1761116384\" width=600 height=254 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/0d370e1a388b09d604499934fea56399.webm?t=1761116384\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/0d370e1a388b09d604499934fea56399.mp4?t=1761116384\" type=\"video/mp4\"></video></span><br><br>■与来自世界各地的FIGHTER互相切磋<br>“格斗中心”是让在“环球游历”中锻炼了的虚拟形象聚首一堂，并可与来自世界各地的玩家互相交流的地方。除了只要坐在对战台就可展开令人热血沸腾的“格斗中心对战”和“极限对抗”外，还可在“街游中心”的游戏机上畅玩Capcom的街机游戏名作！<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/100a45536049ecba1cf3db1def2d0471.poster.avif?t=1761116384\" width=600 height=254 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/100a45536049ecba1cf3db1def2d0471.webm?t=1761116384\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1364780/extras/100a45536049ecba1cf3db1def2d0471.mp4?t=1761116384\" type=\"video/mp4\"></video></span><br><br>世界战士之路从这里开始。', '动作,冒险', 1, '单人,多人,玩家对战,线上玩家对战,同屏/分屏玩家对战,同屏/分屏,跨平台多人,Steam 成就,Steam 集换式卡牌,应用内购买,视角舒适度,自定义音量控制,可调整难度,可以仅用键盘,随时保存,立体声,环绕声,部分支持控制器,Steam 云,家庭共享', '英语<strong>*</strong>, 日语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 阿拉伯语, 葡萄牙语 - 巴西, 波兰语, 俄语, 简体中文, 繁体中文, 韩语, 西班牙语 - 拉丁美洲<br><strong>*</strong>具有完全音频支持的语言', '【有关线上游玩】<br />\r<br>CAPCOM将会为本游戏提供相关线上专用内容及各种线上服务。<br />\r<br>※如没有联网环境，某些功能可能无法使用，敬请注意。<br />\r<br>※如要使用线上专用内容，需要CAPCOM ID。<br />\r<br>※关于CAPCOM ID的服务条款及使用方法等，请浏览CAPCOM ID的官方网页 https://cid.capcom.com/ 。另外，使用CAPCOM ID时，可能设有年龄限制。<br />\r<br>※在逼不得已的情况下，CAPCOM将会暂停或中止线上服务。<br />\r<br><br />\r<br>©CAPCOM', 'https://www.streetfighter.com/6/en-us/', 92, 'https://www.metacritic.com/game/pc/street-fighter-6?ftag=MCD-06-10aaa1f', '1364780');
INSERT INTO `game_info` VALUES (3, 'EA SPORTS FC™ 26', 27.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/2d96aa1b06e453cd62dae9029d412f19e61932c3/header.jpg?t=1765797265', 'EA Canada', '2025 年 9 月 25 日', '在《EA SPORTS FC™ 26》中，俱乐部由您主宰。随心畅玩，享受采纳社区反馈后焕然一新的游戏体验、为新赛季带来全新剧情的经理实时挑战，以及灵感源自足坛传奇的原型系统。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/d01fbbd32769bd7cd70215dedd22e4ffb7bc42bc/ss_d01fbbd32769bd7cd70215dedd22e4ffb7bc42bc.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/b2bcda2e36966161937db451509f9922901b6145/ss_b2bcda2e36966161937db451509f9922901b6145.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/2048e95987d01af065fdcb67c380e7a0e53df069/ss_2048e95987d01af065fdcb67c380e7a0e53df069.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/bcf8cde56e46d969dfaff90c8a9d85a22b6342fc/ss_bcf8cde56e46d969dfaff90c8a9d85a22b6342fc.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/7570de99060a6abd39202cd3fe532b2e2d6f0f21/ss_7570de99060a6abd39202cd3fe532b2e2d6f0f21.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/bb68762a90b41d0f9f66d570a8cf11c73aa5f598/ss_bb68762a90b41d0f9f66d570a8cf11c73aa5f598.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/efeed8175e64c95a1a24d9f4b27bffab9a9101c3/ss_efeed8175e64c95a1a24d9f4b27bffab9a9101c3.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/e746dd5cc1e4c2b36bcc66d6b0780a02e7c55cea/ss_e746dd5cc1e4c2b36bcc66d6b0780a02e7c55cea.1920x1080.jpg?t=1765797265\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/a6bccb1a48725a97a2b3ce77435d50996e12e7f9/ss_a6bccb1a48725a97a2b3ce77435d50996e12e7f9.1920x1080.jpg?t=1765797265\"]', '<span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3405690/extras/d1e23476ed0472df820ee9467c10e328.avif?t=1765797265\" width=610 height=2134 /></span><br><br>这款游戏包括自愿的游戏内虚拟货币购买，虚拟货币可用于获取虚拟游戏内物品，包括各种随机的虚拟游戏内物品。', '模拟,体育', 1, '单人,多人,玩家对战,线上玩家对战,同屏/分屏玩家对战,合作,在线合作,同屏/分屏合作,同屏/分屏,跨平台多人,Steam 成就,完全支持控制器,应用内购买,聊天语音转文字,可调整难度,可以仅用键盘,立体声,字幕选项,可用 HDR', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 阿拉伯语<strong>*</strong>, 荷兰语<strong>*</strong>, 日语<strong>*</strong>, 韩语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 简体中文<strong>*</strong>, 西班牙语 - 拉丁美洲<strong>*</strong>, 土耳其语<strong>*</strong>, 捷克语, 丹麦语, 挪威语, 葡萄牙语 - 葡萄牙, 俄语, 瑞典语, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '需要拥有互联网连接、EA账户、Steam 账户、接受 EA 用户协议 (terms.ea.com/sc)、下载并安装 EA 客户端软件 EA app (ea.com/ea-app) 才能进行游戏。使用 EA 的服务须遵守 EA保密和Cookie协议。您同意将在您使用 EA 服务时收集到的个人数据传输至美国，如保密和Cookie协议中进行的深入阐述。您必须将 EA账户与 Steam 账户绑定，才能进行游戏；EA 将与 Steam 共享您的账户 ID 和个人游戏记录，以验证您的购买和/或退款申请。仅限使用一个 EA账户和一个 Steam 账户访问软件内容，购买后不可转让。您可能需要年满 13 或 16 周岁，才能注册 EA账户（年龄限制可能会有所不同，详情请参见 o.ea.com/ea/child-access）和终极版内容。某些内容可能需要进行游戏才能解锁。可能会自动下载必要的更新内容，这会产生宽带使用费用，并且需要额外储存空间或另外购买才能使用。EA可能会适时免费提供特定增量内容和/或更新。EA可在于www.ea.com/service-updates 发布通知30日后停止在线功能。<br />\r<br><br />\r<br>EA用户协议：德国居民请访问 terms.ea.com/de；所有其他地区居民请访问 terms.ea.com/sc。<br />\r<br>EA保密和Cookie协议：德国居民请访问 privacy.ea.com/de；所有其他地区居民请访问 privacy.ea.com/sc。<br />\r<br><br />\r<br>您必须兑换一次性代码（如需要代码），登录 Ultimate Team，登录俱乐部模式，并在连接到 EA 服务器时创建职业生涯模式存档（这些步骤统称为“兑换”）才能获取您的终极版内容（6000 FC Points、一（1）个球员进化栏位、一（1）件原型解锁消耗品、两（2）件 10 场比赛双倍原型经验值消耗品、三（3）位职业生涯超级巨星、一（1）名可在经理职业生涯模式中聘用的 5 星级教练、一（1）名可在经理职业生涯模式中聘用的 5 星级青年球员球探，以及经理实时挑战内容）。创建新的职业生涯模式存档时，您必须连接到 EA 服务器，即可使用三（3）位职业生涯超级巨星、一（1）名可在经理职业生涯模式中聘用的 5 星级教练、一（1）名可在经理职业生涯模式中聘用的 5 星级青年球员球探，以及经理实时挑战内容。FC Points 将分 3 期等量发放。第一期将在您首次登录产品中的 Ultimate Team 时发放，第二期将在下一个月底前发放，第三期将在后一个月底前发放。††您必须在 2026 年 6 月 15 日之前完成这些步骤，领取第一期发放的 FC Points，才能获得全部 6000 FC Points。如果您在 2026 年 9 月 15 日之后兑换，则将无法获得任何 FC Points。<br />\r<br><br />\r<br>‡适用条件和限制。请参阅 ea.com/games/ea-sports-fc/fc-26/game-disclaimers 了解详细信息。<br />\r<br><br />\r<br>除了英语和所选 Steam 用户端语言，其他语言的文字和音频解说需要单独下载。<br />\r<br><br />\r<br>比利时和韩国境内不提供终极版且未开放 FC Points 购买。<br />\r<br><br />\r<br>© 2025 Electronic Arts Inc.Electronic Arts、EA、EA SPORTS、EA SPORTS 标识、EA SPORTS FC、EA SPORTS FC 标识、Frostbite、Frostbite 标识、Football Ultimate Team 和 Ultimate Team 均为 Electronic Arts Inc. 的商标。', 'https://www.ea.com/en/games/ea-sports-fc/fc-26', 0, '', '3405690');
INSERT INTO `game_info` VALUES (4, '艾尔登法环 黑夜君临', 29.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/header.jpg?t=1764808750', 'FromSoftware, Inc.', '2025 年 5 月 29 日', '《艾尔登法环 黑夜君临》（ELDEN RING NIGHTREIGN）为基于《艾尔登法环》（ELDEN RING）的世界观， 重新构思游戏的核心设计，带给玩家全新体验的衍生作品。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_0b9594934db8a1457c915e200f9d0d9b447a3df4.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_1deefb0b7ea597f4227777239910b4990aa0cc77.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_802cd66236d951fba204fb9980e2c0c9213a264c.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_b3ed8ab522f5965e46bc7c090cad9d018f937ae2.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_edfd360b92d6f9b983b759fd837e664b86cd9563.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_7a6e385e299b4848943daf2c6bc8218c16cd9497.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_592648ca7867f7efc92c14166b254bb01ddab65a.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_123c13ef085accee034632b6ce5d67e017dabc73.1920x1080.jpg?t=1764808750\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/ss_4a88be86fecee192e41ed84607ce255e933cee3b.1920x1080.jpg?t=1764808750\"]', '《艾尔登法环 黑夜君临》（ELDEN RING NIGHTREIGN）为基于《艾尔登法环》（ELDEN RING）的世界观，<br>重新构思游戏的核心设计，带给玩家全新体验的衍生作品。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/extras/972bf4802f054dcecfdd9122f1c9ce41.poster.avif?t=1764808750\" width=616 height=250 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/extras/972bf4802f054dcecfdd9122f1c9ce41.webm?t=1764808750\" type=\"video/webm; codecs=vp9\"></video></span><h2 class=\"bb_tag\" >并肩而战</h2>与其他玩家合作，对抗逐步逼近的黑夜，以及潜藏其中的危险。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/extras/8e034953452e672d3d8578e8f7c4463c.poster.avif?t=1764808750\" width=616 height=250 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/extras/8e034953452e672d3d8578e8f7c4463c.webm?t=1764808750\" type=\"video/webm; codecs=vp9\"></video></span><h2 class=\"bb_tag\" >成为英雄</h2>可操作角色各个都有独特的能力；通过团队合作，能力的效果会相得益彰。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/extras/a8373c3a9b62c78a30d63c0bc8b77708.poster.avif?t=1764808750\" width=616 height=250 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2622380/extras/a8373c3a9b62c78a30d63c0bc8b77708.webm?t=1764808750\" type=\"video/webm; codecs=vp9\"></video></span><h2 class=\"bb_tag\" >挑战黑夜</h2>每次出击时的环境会有所变化──克服接连而来的威胁，打败夜里出现的强大头目吧。', '动作,角色扮演', 1, '单人,多人,合作,在线合作,Steam 成就,完全支持控制器,视角舒适度,自定义音量控制,无需应对快速反应事件,立体声,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 日语, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 阿拉伯语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 西班牙语 - 拉丁美洲, 泰语, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '©Bandai Namco Entertainment Inc. / ©2025 FromSoftware, Inc.', '', 0, '', '2622380');
INSERT INTO `game_info` VALUES (5, 'Counter-Strike 2', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/header_schinese.jpg?t=1749053861', 'Valve', '2012 年 8 月 21 日', '二十多年来，在全球数百万玩家的共同铸就下，Counter-Strike 提供了精湛绝伦的竞技体验。而如今，CS 传奇的下一章即将揭开序幕，那就是 Counter-Strike 2。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_796601d9d67faf53486eeb26d0724347cea67ddc.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_d830cfd0550fbb64d80e803e93c929c3abb02056.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_13bb35638c0267759276f511ee97064773b37a51.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_0f8cf82d019c614760fd20801f2bb4001da7ea77.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_ef82850f036dac5772cb07dbc2d1116ea13eb163.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_76f6730dbb911650ba1f41c8e5b4bac638b5beea.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_808cdd373d78c3cf3a78e7026ebb1a15895e0670.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_ef98db5d5a4d877531a5567df082b0fb62d75c80.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_2254a50f27951fb9028bc00b93a7f2ed7aac1e13.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_54b9c26b028c84d5f8a5316f31ae6203953ed84d.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_1b3b5fd437939a7ed00a2155269e78994cb998d3.1920x1080.jpg?t=1749053861\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/730/ss_352666c1949ce3966bd966d6ea5a1afd532257bc.1920x1080.jpg?t=1749053861\"]', '二十多年来，在全球数百万玩家的共同铸就下，Counter-Strike 提供了精湛绝伦的竞技体验。而如今，CS 传奇的下一章即将揭开序幕，那就是 Counter-Strike 2。<br><br>Counter-Strike 2 是 CS:GO 的免费升级版，展现了 Counter-Strike 历史上最大的技术飞跃。Counter-Strike 2 由 Source 2 引擎打造，具备基于物理的逼真渲染、最为先进的网络、经过升级的社区创意工坊工具，令人耳目一新。<br><br>Counter Strike 系列于 1999 年开创了以完成目标为重的游戏玩法，而 Counter Strike 2 除了将这一经典元素保留，还会呈现以下特色：<br><br><ul class=\"bb_ul\"><li>全新 CS 综合得分与经过更新的优先权模式<br></li><li>全球及区域排行榜<br></li><li>经过升级和大改的地图<br></li><li>革命性的动态烟雾弹<br></li><li>不受刷新频率阻碍的游戏体验<br></li><li>全新设计的声画效果<br></li><li>CS:GO 的所有物品均迁移至 CS2</li></ul>', '动作,免费开玩', 1, '多人,跨平台多人,Steam 集换式卡牌,Steam 创意工坊,应用内购买,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,无需应对快速反应事件,立体声,环绕声,已启用 Valve 反作弊保护,统计数据,在手机上远程畅玩,在平板上远程畅玩,在电视上远程畅玩,Steam 时间轴', '捷克语, 丹麦语, 荷兰语, 英语<strong>*</strong>, 芬兰语, 法语, 德语, 匈牙利语, 意大利语, 日语, 韩语, 挪威语, 波兰语, 葡萄牙语 - 葡萄牙, 葡萄牙语 - 巴西, 罗马尼亚语, 俄语, 简体中文, 西班牙语 - 西班牙, 瑞典语, 泰语, 繁体中文, 土耳其语, 保加利亚语, 乌克兰语, 希腊语, 西班牙语 - 拉丁美洲, 越南语, 印度尼西亚语<br><strong>*</strong>具有完全音频支持的语言', '', 'http://counter-strike.net/', 0, '', '730');
INSERT INTO `game_info` VALUES (6, 'Ready or Not', 29.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/header.jpg?t=1753984272', 'VOID Interactive', '2023 年 12 月 13 日', '洛斯苏埃诺斯市——LSPD报告称，泛洛斯苏埃诺斯地区的暴力犯罪事件急剧增加。警局已派遣SWAT小队应对包括人质危机、炸弹威胁、嫌犯固守在内的各类高危情形。建议广大市民非必要不外出。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/88aec4ee3fba8860070370032495e0c9ff87a607/ss_88aec4ee3fba8860070370032495e0c9ff87a607.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/8003fd0791fbaaef24bb3da768603eca92449a4d/ss_8003fd0791fbaaef24bb3da768603eca92449a4d.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/ss_795edc39907b6c6aba8db40fcb34cc0eb27f5547.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/29c4fc0ed7925fb34a32cbc95c4519311420f439/ss_29c4fc0ed7925fb34a32cbc95c4519311420f439.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/bbcb0ea9c2d360a3492af71ce3a4f07df2eb5b6e/ss_bbcb0ea9c2d360a3492af71ce3a4f07df2eb5b6e.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/ss_f0e0b11fa6f1bf6b436f1e17cd3f72f956180405.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/ss_873f74f54ca8ce5f4ac7d675563129417a1fd0c9.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/ss_4b928ab5fa37af0ef9d0cf0727e76d1a2cd5d6c1.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/987e58774c746af82402934262e23c887977ebe9/ss_987e58774c746af82402934262e23c887977ebe9.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/21c748dc43e90596677fac5e690ca2679c94bc7d/ss_21c748dc43e90596677fac5e690ca2679c94bc7d.1920x1080.jpg?t=1753984272\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/819d4272f95196ae9be6f1571fe03d9edccff17f/ss_819d4272f95196ae9be6f1571fe03d9edccff17f.1920x1080.jpg?t=1753984272\"]', '&quot;洛斯苏埃诺斯市——LSPD报告称，泛洛斯苏埃诺斯地区的暴力犯罪事件急剧增加。警局已派遣特种武器和战术（SWAT）小队以就包括高危人质危机、炸弹威胁、嫌犯固守在内的多种情形做出响应。建议广大市民谨慎出行，非必要不外出。<br><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1144200/extras/157226b5583a443005d3a372dff50069.avif?t=1753984272\" width=616 height=196 /></span><br><br>洛斯苏埃诺斯虽然仍是一座能够为部分居民带来财富的城市，但对更多人而言，生命中的美好事物正在变得越来越稀缺。加洛·阿尔瓦雷斯局长称：“这座城市里遍布着拥挤的高层公寓和破败的廉价住房，就像是滋生邪恶的温床。地下犯罪团伙如寄生虫一般利用着这些场所。这是一座人们要费尽心机才能勉强生存的城市，LSPD与LSPD SWAT的执法行动在其中发挥着不可或缺的作用——全靠这股力量，这里已几乎消磨殆尽的社会结构才能在混乱之下竭力维持。”<br><br>为应对洛斯苏埃诺斯市日益猖獗的暴力犯罪情形，LSPD局长阿尔瓦雷斯征募了人称“判官”的大卫·博蒙特前来担任LSPD SWAT小队指挥官一职。就在该消息发布后不久，LSPD还确认将积极招募更多人才加入这支专门的战术警员小组，肩负起重新为这座城市带来和平的使命。<br><br>博蒙特指挥官评论称：“这项任务需要有大无畏精神的警员。极端分子、奸猾政客、枪支泛滥、人口贩卖、非法毒品以及色情制品……摆在洛斯苏埃诺斯警务人员面前的现实状况真实而残酷，其严重程度超出普通民众的想象。这样的现实正是你和队员需要共同去面对的，还要在法律允许的范围内妥善处置，否则就会有后果产生并进一步激化已然十分动荡的现状。”<br> <br>洛斯苏埃诺斯警局正式公布了“判官”博蒙特在LSPD SWAT的最新任务细节：<br><br>新的期望：或许你已经不是第一次来到洛斯苏埃诺斯，但这座城市的现状已有一些改变。因此，我们开展工作时所使用的工具和方法也有所调整。那些你曾亲历过或听说过的出勤情形已今非昔比，所以我们也会在处理警情时选择不同的切入点。此外还有报告指出了4个新增的高风险警区，预计未来可能需要对这些区域进行战术干预，另外还有至少4个极为动荡的现有高风险警区需要注意。<br><br>临阵指挥：LSPD SWAT指挥官的职责，是从一大批具有独特才能的警员中选择合适的队员并组建专门的小队。同时，指挥官还需下达战术指令、精心策划行动以及高效执行任务。指挥官有义务在完成每项任务的过程中保持正直，关注队员的身心健康。如果得不到及时处理，心理状况恶化的警员将可能无法正常履行职责，甚至还会出现辞职离队的想法。在战斗过程中丧失行动能力的警员可能会暂时无法执行任务，沉痛的死亡则会导致无法挽回的警力损失。SWAT指挥官需要在面临特殊情形时基于个人经验做出抉择。如有致命错误，指挥官本人亦有可能永久死亡。<br><br>SWAT小队素养提高：LSPD在SWAT的训练流程、战法战术以及经验积累方面做出了较大改进，因此名册上警员的战术素养和数量均有全面提高。更多样的战术让警员能够有充分的信心迎接一切挑战，完美融合协调和独立这两种特质。SWAT队员现在还可接受额外的基础训练，确保基础战术知识与自身肌肉记忆处于最佳状态。<br><br>装备和定制：我们可以使用LSPD所能提供的最好武器和装备，包括诸多新购置的装备，来帮助我们在异常凶险的环境中履行职责。不过，定制选项并不会局限于日常使用的装备。在与战友并肩作战的过程中，逐步加深的同袍之情会带来一些额外奖励，包括可穿戴的衣物、皮肤上的艺术作品甚至是手腕上的计时器。最后，我们还对总部的训练设施进行了全方位改造，便于队员在出勤实战之前更好地测试各类装备配置。 &quot;', '动作,冒险,独立', 1, '单人,多人,合作,在线合作,Steam 成就,完全支持控制器,Steam 云', '英语<strong>*</strong>, 法语, 德语, 韩语, 简体中文, 意大利语, 西班牙语 - 西班牙, 日语, 波兰语, 葡萄牙语 - 葡萄牙, 土耳其语, 俄语<br><strong>*</strong>具有完全音频支持的语言', 'Ready or Not © VOID Interactive Ltd', 'https://voidinteractive.net', 0, '', '1144200');
INSERT INTO `game_info` VALUES (7, '《赛博朋克 2077：往日之影》', 20.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/d0d6f4824d0e819f68d46000e83fb9391d56d4b1/header_schinese.jpg?t=1759751039', 'CD PROJEKT RED', '2023 年 9 月 25 日', '《往日之影》是开放世界动作冒险角色扮演游戏《赛博朋克 2077》的全新谍战悬疑风格资料片。玩家扮演经过义体强化的雇佣兵 V，和秘密特工所罗门·李德一起，共同解开一片由忠诚、背叛，以及险恶政治阴谋交织而成的罗网。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_5c4b46c61d7d1d5903835a68eac6582b4e62367f.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_0976dd58d12a6faf07cbd2c7713a2fdb4d151896.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_2d24468f7ca6dfe4cb79017db119e14f76ad9886.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_3ab6296c6827b4f2fd825f52be317b748ccce079.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_02c440c80edb9b5ec5008bb9031530cce79a880e.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_7bbae8adf0c05471dfea2ebb1dc2611ee0f72835.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_e6d03e7adf4c9a0dceb86eddb876da28d06f56df.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_36dd158f7fb3d6a0ac30ad67dae3ce6cddfe1ac9.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_c8e6d3586c627e8502ccbcd8abcf8a6ed77657b5.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_0e506eb18c5b9c2a6adabaa763592a91114fa3d4.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_46268c01b7be460b204be19af6c2bef4e4d63e4e.1920x1080.jpg?t=1759751039\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/ss_e64b504d41cb859ad837712a9f91ab8b82408abd.1920x1080.jpg?t=1759751039\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/bca4ba37b766f369855e528202f1b349.poster.avif?t=1759751039\" width=1170 height=375 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/bca4ba37b766f369855e528202f1b349.webm?t=1759751039\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/5d95968dcc70dd98fb051c14b54fdcfa.poster.avif?t=1759751039\" width=1170 height=375 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/5d95968dcc70dd98fb051c14b54fdcfa.webm?t=1759751039\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/ae33f9552fc7b710441c3c091840b7ca.poster.avif?t=1759751039\" width=1170 height=375 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/ae33f9552fc7b710441c3c091840b7ca.webm?t=1759751039\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/d04068e80d0fae89405a9046040dcb26.poster.avif?t=1759751039\" width=1170 height=375 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/d04068e80d0fae89405a9046040dcb26.webm?t=1759751039\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/5f189ced1dc8b6b1cb8f5a5ee1c48105.poster.avif?t=1759751039\" width=1170 height=375 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2138330/extras/5f189ced1dc8b6b1cb8f5a5ee1c48105.webm?t=1759751039\" type=\"video/webm; codecs=vp9\"></video></span></p>', '角色扮演', 1, '单人,DLC,Steam 成就,完全支持控制器,Steam 集换式卡牌,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,可调整难度,随时保存,立体声,字幕选项,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 阿拉伯语, 捷克语, 匈牙利语, 日语<strong>*</strong>, 韩语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 俄语, 简体中文<strong>*</strong>, 西班牙语 - 拉丁美洲, 泰语, 繁体中文, 土耳其语, 乌克兰语<br><strong>*</strong>具有完全音频支持的语言', 'CDPROJEKT®，Cyber​​punk®，Cyber​​punk2077® 是 CD PROJEKT S.A.的注册商标。© CD PROJEKT S.A.保留所有权利。所有其他版权和商标均为其各自所有者的财产。', 'https://www.cyberpunk.net', 0, '', '2138330');
INSERT INTO `game_info` VALUES (8, 'ARC Raiders', 31.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/b26e2a315d32ef462a07cf0e36eb06a2b810d0c9/header_alt_assets_0.jpg?t=1765441443', 'Embark Studios', '2025 年 10 月 30 日', '《ARC Raiders》是一款多人合作撤离冒险游戏，背景设定在饱受名为 ARC 的神秘机械敌人摧残、充满致命威胁的未来地球。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/1763664f3ea80080867eafa751685b7feec950c8/ss_1763664f3ea80080867eafa751685b7feec950c8.1920x1080.jpg?t=1765441443\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/5188aa2b07920afe9753409ea9fe3d1324cfc294/ss_5188aa2b07920afe9753409ea9fe3d1324cfc294.1920x1080.jpg?t=1765441443\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/155fc161a45949e61f41e473282f9ed7ec221efe/ss_155fc161a45949e61f41e473282f9ed7ec221efe.1920x1080.jpg?t=1765441443\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/cb49dfbcd7175c86e297b35ffc54cf779708f0ae/ss_cb49dfbcd7175c86e297b35ffc54cf779708f0ae.1920x1080.jpg?t=1765441443\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/0b1fa1294fa269633112be2c3d8758df42baebe1/ss_0b1fa1294fa269633112be2c3d8758df42baebe1.1920x1080.jpg?t=1765441443\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/c411b8fa5b7bc12943e73a4818edb98c3c7c2fae/ss_c411b8fa5b7bc12943e73a4818edb98c3c7c2fae.1920x1080.jpg?t=1765441443\"]', '<h2 class=\"bb_tag\" >回收，生存，兴盛。</h2><p class=\"bb_paragraph\" >在《ARC Raiders》中，你将穿梭于由致命机器统治的地表和热闹非凡的地下社会“斯佩兰扎”之间。在前往上层探索那片满目疮痍却壮丽依旧的世界之前，你可以在自己的工坊中安心地制作、修理或升级装备。你可以独自冒险或与至多三名队友结伴，全力应对ARC机器的威胁以及幸存者同伴们出其不意的决定。到头来，只有你能定义自己是何种奇袭者，以及愿意为了胜利付出何种努力。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/f22cb839ef45f9106db05672412463f3.poster.avif?t=1765441443\" width=780 height=252 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/f22cb839ef45f9106db05672412463f3.webm?t=1765441443\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/f22cb839ef45f9106db05672412463f3.mp4?t=1765441443\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >探索沉浸式世界</h2><p class=\"bb_paragraph\" >游戏初期可探索四张独特地图，随着地下社会的发展与扩张，更多区域将逐渐开放。每一处目的地都铭刻着两次灾难的沉重记忆，以及新旧战火留下的伤痕。在废墟中搜寻有价值的战利品，逐渐拼凑出被大自然覆盖的往昔世界。在不断变迁的地图里，每次行动都将独一无二，天气、敌人和机制的差异会让你随时陷入意想不到的境地。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/138bc82b1d7cd44c7a8c1b40ff9351aa.poster.avif?t=1765441443\" width=780 height=252 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/138bc82b1d7cd44c7a8c1b40ff9351aa.webm?t=1765441443\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/138bc82b1d7cd44c7a8c1b40ff9351aa.mp4?t=1765441443\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >宣告身份</h2><p class=\"bb_paragraph\" >在这个崇尚胆识与气势的社会中，你需要向所有人彰显自己作为奇袭者的身份。你搜集到的战利品既可出售换取钱币，也能用来制作全新装备，使你能够做好准备，迎接层出不穷的危险挑战。无论胜利或磨难，你都将积累宝贵经验，解锁多样技能，开启全新玩法。此外，你还将为各怀心思的商人完成委托，在这个随时可能瓦解的地下社会中体验充满摩擦、但又相互扶持的社区情谊。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/cb5b1a7db7fc18a2443892b8f437a2e2.poster.avif?t=1765441443\" width=780 height=252 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/cb5b1a7db7fc18a2443892b8f437a2e2.webm?t=1765441443\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/cb5b1a7db7fc18a2443892b8f437a2e2.mp4?t=1765441443\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >警惕机器</h2><p class=\"bb_paragraph\" >地表由名为ARC的杀戮机器主宰，从穷追不舍的无人机蜂群到摧枯拉朽的庞然巨兽，它们无所不在。虽然无人知晓它们的来历，但每一步前行，你都感觉自己踩在挥之不去的危险阴影里。每一台机器都有独特的强项与战术，你必须识别其弱点，并在瞬息之间做出反应。记住：战斗的声响会传得很远。其他奇袭者或许正在暗中观望，准备瓜分你遗落的物资。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/bc2b5cb59b0bc6eb42ddc2ff26098545.poster.avif?t=1765441443\" width=780 height=252 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/bc2b5cb59b0bc6eb42ddc2ff26098545.webm?t=1765441443\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/bc2b5cb59b0bc6eb42ddc2ff26098545.mp4?t=1765441443\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >开拓自己的道路</h2><p class=\"bb_paragraph\" >奇袭者依靠拆解和重组回收材料生存，利用过往的技术与掠夺来的ARC零件制作武器、装备和各类小道具。升级你的工坊工作台，学习蓝图以打造更先进的物品，或在野外随机应变、快速修理，解决燃眉之急。随着你逐渐打响名号，可以通过试炼与其他奇袭者一较高下，冲击排行榜，赢取珍贵奖励。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/b85286f667febcbea2f732aa222f29ce.poster.avif?t=1765441443\" width=780 height=252 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/b85286f667febcbea2f732aa222f29ce.webm?t=1765441443\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/b85286f667febcbea2f732aa222f29ce.mp4?t=1765441443\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >任务</h2><p class=\"bb_paragraph\" >斯佩兰扎的每个人心里都有一把自己的算盘，那些商人自然也不例外。他们会支付报酬，委托你前往上层执行任务，在此过程中，你将渐渐了解他们的真实身份，以及他们对斯佩兰扎未来的规划。通过完成任务，你可以获取装备、制作材料及经验值，提升奇袭者等级并解锁新的技能点。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/3ed7c3a1bf71024f7b8938b1b10711f9.poster.avif?t=1765441443\" width=780 height=252 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/3ed7c3a1bf71024f7b8938b1b10711f9.webm?t=1765441443\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/3ed7c3a1bf71024f7b8938b1b10711f9.mp4?t=1765441443\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >技能树</h2><p class=\"bb_paragraph\" >ARC奇袭者的技能树共有三条分支：生存、机动性和强化。花费技能点来塑造独属于你的玩法风格。借助生存技能，你能加快搜刮速度，行动也更加悄无声息。利用机动性，你能巧妙甩开敌人，化解各类威胁。强化则能增强你的力量与耐力。自主规划成长路线，决定你的奇袭者将在战场上带来何种表现。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/b20e3d75010301520237f9b904105279.poster.avif?t=1765441443\" width=780 height=252 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/b20e3d75010301520237f9b904105279.webm?t=1765441443\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1808500/extras/b20e3d75010301520237f9b904105279.mp4?t=1765441443\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >打造军火库</h2><p class=\"bb_paragraph\" >无论是消灭特定的ARC目标，还是与敌对奇袭者对抗，每场战斗你都应当挑选最适合的武器上阵。根据个人作战风格选择武器，从冲锋枪、步枪、霰弹枪，到轨道炮和能量武器等高级装备一应俱全。手雷、陷阱、滑索和可部署装置可为你提供战术上的灵活性，而强化则能让你根据偏好自由调整配装，解锁专属物品栏栏位和额外玩法加成。</p>', '动作', 1, '多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,应用内购买,统计数据,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 繁体中文, 土耳其语<br><strong>*</strong>具有完全音频支持的语言', 'ARC RAIDERS © 2025 Embark Studios AB. ARC RAIDERS and EMBARK trademarks and logos are trademarks or registered trademarks of Embark Studios AB. NEXON trademark and logo are trademarks or registered trademarks of NXC Corporation. © 2025 Epic Games, Inc. All rights reserved. Unreal, Unreal Engine and their respective logos are trademarks or registered trademarks of Epic Games, Inc. in the U.S. Patent and Trademark Office and elsewhere.', 'https://arcraiders.com/', 0, '', '1808500');
INSERT INTO `game_info` VALUES (9, 'Apex Legends™', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1172470/6ae90b012e725a93622c3694d9d866b886cfd0f8/header.jpg?t=1762457261', 'Respawn', '2020 年 11 月 4 日', '由Respawn Entertainment开发制作，屡获殊荣的《Apex Legends》，是一款免费大逃杀英雄射击游戏。在这款革命性的新一代大逃杀英雄射击游戏中，掌控日益丰富且拥有强大技能的传奇角色，深度体验战术小队玩法及创新游戏元素。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1172470/d77ebf883a5d8c00f96d8952446a8698bbdbf611/ss_d77ebf883a5d8c00f96d8952446a8698bbdbf611.1920x1080.jpg?t=1762457261\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1172470/6420fddf1256a99313724a11cd132d72342ba101/ss_6420fddf1256a99313724a11cd132d72342ba101.1920x1080.jpg?t=1762457261\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1172470/0f0a7ea9ae48826aed8738c7196b614aa7b2ba21/ss_0f0a7ea9ae48826aed8738c7196b614aa7b2ba21.1920x1080.jpg?t=1762457261\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1172470/fdc782730844d21d2f14f62f513863302a877337/ss_fdc782730844d21d2f14f62f513863302a877337.1920x1080.jpg?t=1762457261\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1172470/089137347095bfc0d42361fa20d4acd0e58db818/ss_089137347095bfc0d42361fa20d4acd0e58db818.1920x1080.jpg?t=1762457261\"]', '在《Apex Legends》这款免费*英雄射击游戏中脱颖而出，掌控角色，征服群雄，集结技艺超群的强大传奇角色，组队在边境之地为名利而战。<br><br>掌控丰富的传奇角色群、深度战术小队玩法以及大胆革新的元素，获得前所未有的新一代大逃杀竞技射击游戏体验——在这崎岖的世界，一切皆有可能。欢迎来到新一代英雄射击游戏。<br><br><strong>主要特色：</strong><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1172470/extras/040375b4913cdc829ec1af05bf8a2825.avif?t=1762457261\" width=610 height=2884 /></span><br><br>本游戏含非必要的虚拟货币购买，用于获得游戏内虚拟物品，其中包括一些随机的游戏内虚拟物品。', '动作,冒险,免费开玩', 1, '多人,玩家对战,线上玩家对战,合作,在线合作,Steam 成就,完全支持控制器,Steam 集换式卡牌,应用内购买,聊天语音转文字,无需应对快速反应事件', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 日语<strong>*</strong>, 韩语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西, 俄语<strong>*</strong>, 简体中文<strong>*</strong>, 繁体中文, 西班牙语 - 拉丁美洲, 阿拉伯语<br><strong>*</strong>具有完全音频支持的语言', '仅限于德国、奥地利和瑞士： 适用EA保密和COOKIE协议(privacy.ea.com/de)。<br />\r<br>仅限于其他欧盟成员国、英国、挪威、冰岛、安道尔、波斯尼亚和黑塞哥维那、格鲁吉亚、科索沃、马其顿(FYROM)、摩尔多瓦、摩纳哥、黑山共和国、圣马力诺、塞尔维亚、土耳其、梵蒂冈（圣座）、列支敦士登： 需确认EA保密和COOKIE协议(privacy.ea.com/sc)方可开始游戏。<br />\r<br>所有其他居民： 需接受EA保密和COOKIE协议(privacy.ea.com/sc)方可开始游戏。<br />\r<br><br />\r<br>需要网络连接;EA账户；STEAM账户；接受EA用户协议(terms.ea.com/sc)方可开始游戏。连接EA账户和STEAM账户后，方可开始游戏；EA将与STEAM共享您的账户ID、个人游戏和比赛记录，便于验证您的购买和/或退款申请。仅限使用一个EA账户和一个STEAM账户访问软件内容，购买后不可转让。您可能需年满至少13或16周岁，方可注册EA账户（年龄限制可能会有所不同，详情请参见https://o.ea.com/ea/child-access）。有些内容可能需要通过进行游戏来解锁。系统可能会自动下载更新内容，可能需要额外存储空间，还可能会产生宽带使用费用。EA可能会适时免费提供特定增量内容和/或更新。EA 可能会在 www.ea.com/service-updates 上发布公告 30 天后撤除相关的在线游戏内容和服务。<br />\r<br><br />\r<br>EA用户协议： 德国居民请访问 terms.ea.com/de；所有其他地区居民请访问 terms.ea.com/sc。<br />\r<br>EA保密和COOKIE协议： 德国居民请访问 privacy.ea.com/de；所有其他地区居民请访问 privacy.ea.com/sc。', 'https://www.ea.com/games/apex-legends', 88, 'https://www.metacritic.com/game/pc/apex-legends?ftag=MCD-06-10aaa1f', '1172470');
INSERT INTO `game_info` VALUES (10, 'ARC Raiders - The Bonecrown Set', 19.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/4210150/dc6b046d3b256101e779cad74baf6485828f583c/header.jpg?t=1765878234', 'Embark Studios', '2025 年 12 月 16 日', '骨冠套装现已推出！利用我们全新的外观DLC拥抱上层的荒野，此套装以兽骨和再生布料锻造而成。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/4210150/260948b2da99c8421011192e9c4db0b494fa2e54/ss_260948b2da99c8421011192e9c4db0b494fa2e54.1920x1080.jpg?t=1765878234\"]', '<p class=\"bb_paragraph\" >冬季正式来到锈带，同时也带来了一套全新的外观套装，帮助你抵御严寒。此套装专为心性狂野、在危险逼近时感到特别如鱼得水的奇袭者打造。斯佩兰扎的居民之所以接受与他们共存，也正是因为他们就是如此强悍。</p><p class=\"bb_paragraph\" >此套装以兽骨和再生布料锻造而成，将荒野化作肩头披风。戴上它，在上层满目疮痍的地面上将战利品据为己有。</p><p class=\"bb_paragraph\" >骨冠套装包含以下内容：</p><p class=\"bb_paragraph\" >- 服装：骨冠</p><p class=\"bb_paragraph\" >- 背包：圣物匣</p><p class=\"bb_paragraph\" >- 背包附件：鼓</p><p class=\"bb_paragraph\" >- 背包挂饰：鹿头骨</p><p class=\"bb_paragraph\" >- 发型：编发丸子头</p><p class=\"bb_paragraph\" >- 脸部风格：彩绘面具</p><p class=\"bb_paragraph\" >- 2,400奇袭者代币。</p>', '动作', 1, '多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,DLC,Steam 成就,完全支持控制器,应用内购买,统计数据', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 繁体中文, 土耳其语<br><strong>*</strong>具有完全音频支持的语言', 'ARC RAIDERS © 2025 Embark Studios AB. ARC RAIDERS and EMBARK trademarks and logos are trademarks or registered trademarks of Embark Studios AB. NEXON trademark and logo are trademarks or registered trademarks of NXC Corporation. © 2025 Epic Games, Inc. All rights reserved. Unreal, Unreal Engine and their respective logos are trademarks or registered trademarks of Epic Games, Inc. in the U.S. Patent and Trademark Office and elsewhere.<br />\r<br><br />\r<br>Raider Tokens are an in-game currency in ARC Raiders. Raider Tokens can be used to get raider decks, bundles, or other cosmetic items, including outfits, backpacks, charms, emotes, Scrappy outfits, etc.', 'https://arcraiders.com/', 0, '', '4210150');
INSERT INTO `game_info` VALUES (11, 'War Thunder', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/cab234fd4c0ae2f049e34e7bcb2393679b02bf91/header.jpg?t=1766582455', 'Gaijin Entertainment', '2013 年 8 月 15 日', 'War Thunder上最全面的免费、跨平台大型多人在线军事类游戏，专注于二战和冷战时期的航空、装甲车辆和海军舰艇。现在就加入进来参与在地面、天空和海洋进行的主要战事，并和来自全球各地的几百万玩家们在不断变化的环境中战斗。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/fe3f2ae7e236f70c8280dd1836ea58f23d57a9e6/ss_fe3f2ae7e236f70c8280dd1836ea58f23d57a9e6.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/52df38f52c9981fd6b3e4ef2d501a9d8e9bfdad3/ss_52df38f52c9981fd6b3e4ef2d501a9d8e9bfdad3.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/38d63d92458c950a7d839f1c46f20d55fc60a66f/ss_38d63d92458c950a7d839f1c46f20d55fc60a66f.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_3801dc6ae62e793aee2f073c2ac755c85edf0bb7.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_15343f845523e07d5fc51c6208f362b7bcaa1fbe.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_8c28d914813b82f36d306e5ea322a70f9050f9bc.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_3b32e04e856b77472bff33d55ca6b5758d23a9ae.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_a2d9ec25f81e89139efaf49378e7b33b6f64e0cc.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_70249ce2d057b7594e2039b88adbdd700c4df1ac.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_ab307923f93a44acea6ef3314048c7404b1fe654.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_68bc95495ba9752b44d6655f07cf41a5c26306f6.1920x1080.jpg?t=1766582455\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/ss_fee9c41789ba7227b3cc66db04d8d8f72b3c15cb.1920x1080.jpg?t=1766582455\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/4dd974ef2221302188173d11f26f3b2a.poster.avif?t=1766582455\" width=780 height=400 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/4dd974ef2221302188173d11f26f3b2a.webm?t=1766582455\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" ><strong>War Thunder</strong>是Windows、Linux、Mac、PlayStation®4、PlayStation®5、Xbox One及Xbox Series S|X平台上最全面的免费、跨平台大型和Xbox One平台上最全面的免费、跨平台大型多人在线军事类游戏，专注于二战和冷战时期的航空、装甲车辆和海军舰艇。现在就加入进来参与在地面、天空和海洋进行的主要战事，并和来自全球各地的几百万玩家们在不断变化的环境中战斗。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/b4b9bbc29596dece5f7885b1308b639b.poster.avif?t=1766582455\" width=780 height=350 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/b4b9bbc29596dece5f7885b1308b639b.webm?t=1766582455\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >在War Thunder中，飞机、武装直升机、地面部队和海军舰艇将会在历史性的战场上协同作战，有超过2500种载具可供选择，其中有不少是独家载具，您可以操纵轰炸机的自卫炮塔内击毁追击您的敌机，驾驶高射炮保护您的队友免遭空袭，使用多种火箭发射器击落敌机，或用驾驶快艇用鱼雷击沉敌人的战舰。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/1c6134e20b3a9cf8c71db6adcc81de4f.poster.avif?t=1766582455\" width=780 height=350 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/1c6134e20b3a9cf8c71db6adcc81de4f.webm?t=1766582455\" type=\"video/webm; codecs=vp9\"></video></span></p><h2 class=\"bb_tag\" >游戏特色：</h2><p class=\"bb_paragraph\" ></p><ul class=\"bb_ul\"><li><p class=\"bb_paragraph\" >超过2500款细节丰富的飞机、直升机、坦克、军舰和其他载具，均通过历史文献和幸存资料来源精心制作。</p></li><li><p class=\"bb_paragraph\" >100多张表现历史上发生的主要战事的地图。</p></li><li><p class=\"bb_paragraph\" >玩家之间的高强度对抗，在全面的战斗任务中可以设置不同难度以体现多种多样的游戏风格和水平。</p></li><li><p class=\"bb_paragraph\" >丰富的人机对抗内容，其中包括动态的历史战役和个人任务。</p></li><li><p class=\"bb_paragraph\" >定期更新内容，包括新载具、地图、任务和国家。</p></li><li><p class=\"bb_paragraph\" >惊人的画质，真实的音效和优美的背景音乐创造了一个能够让您沉浸其中的世界。</p></li><li><p class=\"bb_paragraph\" >无缝衔接的跨平台游戏体验，Windows，Linux，Max，PlayStation®4, PlayStation®5, Xbox One 和 Xbox Series X|S，所有玩家都能在一个服务器上畅玩。</p></li><li><p class=\"bb_paragraph\" >为War Thunder定制优异的内容并在 War Thunder Live上面分享，并有机会获得现金作为奖励！</p></li></ul><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/411eb8940b5b505105f54734b5324d6e.poster.avif?t=1766582455\" width=780 height=350 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/236390/extras/411eb8940b5b505105f54734b5324d6e.webm?t=1766582455\" type=\"video/webm; codecs=vp9\"></video></span></p>', '动作,大型多人在线,模拟,免费开玩', 1, '单人,多人,大型多人在线,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,已支持 VR,Steam 集换式卡牌,应用内购买,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,无需应对快速反应事件,立体声,环绕声,在手机上远程畅玩,在平板上远程畅玩', '英语<strong>*</strong>, 德语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 简体中文, 俄语<strong>*</strong>, 日语<strong>*</strong>, 波兰语<strong>*</strong>, 捷克语<strong>*</strong>, 土耳其语<strong>*</strong>, 葡萄牙语 - 葡萄牙, 韩语, 葡萄牙语 - 巴西, 匈牙利语, 乌克兰语, 繁体中文, 罗马尼亚语<br><strong>*</strong>具有完全音频支持的语言', '© 2011—2025 Gaijin Games Kft. All trademarks, logos and brand names are the property of their respective owners.', 'http://warthunder.com/', 81, 'https://www.metacritic.com/game/pc/war-thunder?ftag=MCD-06-10aaa1f', '236390');
INSERT INTO `game_info` VALUES (12, '翼星求生 ICARUS', 3.49, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/header_schinese.jpg?t=1764892501', 'RocketWerkz', '2021 年 12 月 3 日', '《翼星求生ICARUS》是一款PvE生存游戏，可支持多达八名玩家。在地表改造失事的星球上体验蛮荒的外星荒野。在开放世界中顽强求生，执行限时任务，或建立您自己的前哨。您将在探索、收获、制作和狩猎的同时寻找奇异物质，寻求财富。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_71323ca651735d89764fa9e62c5d1756a9b64d4f.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_aa5a09717e6432d3bd61e6d7b20ac622c6f27502.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_30776e22c695c02c12a2bb28967e3dae52615e39.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_ea75b5076ae031f2a39ee4d5d0448d70ea8e54ed.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_d9da7ae25c3675277b9982cef8180469cb5081c4.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_c7178f83bce2cff9872632ec9bc83da9daf11355.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_639d27fd3e963f7713db60b353ad1f5d1c3559b9.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_575ee577c444afc65f8bbf8f7f18d705eacbca15.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_dbac63f61c41aaeedc7cf205cfb2d027f5601fc5.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_08ebc715b3c3ba231dd6168abd9bb24fb48322fb.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_a5acdbe4baacd87e75ccc5666ca610a4892cb5a9.1920x1080.jpg?t=1764892501\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/ss_f86b346e41e90b6fa8ffc68b5f0403b58ba59329.1920x1080.jpg?t=1764892501\"]', '<span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/bb2530300d7140bd8db57e3ebe678820.avif?t=1764892501\" width=616 height=234 /></span><br><strong><i>我们于近期推出了开放世界模式——您可尽情享受传统生存游戏体验</i></strong><br><br><strong>《翼星求生 ICARUS》是一款PVE生存游戏，具有基于任务的单局游戏模式和永久保留的开放世界模式，皆可支持至多八名玩家合作同乐。</strong><br><br>翼星是一颗与地球类似，经历过地表改造的行星，而现在却被当作是人类历史上最大的错误。您需要在翼星残酷而蛮荒的地表上生存下来。勘探者会从轨道空间站下降到星球表面，在探索、收获、制作和狩猎的同时寻求财富。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/93adc686d0bae16d7ad71b99d7abd2ce.poster.avif?t=1764892501\" width=600 height=200 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/93adc686d0bae16d7ad71b99d7abd2ce.webm?t=1764892501\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/93adc686d0bae16d7ad71b99d7abd2ce.mp4?t=1764892501\" type=\"video/mp4\"></video></span><br><br>翼星曾经注定要成为第二个地球，但当地球化改造失败后，空气变得有毒，人类殖民新世界的希望也破灭了。翼星成了一个笑话：人类伸手摘星，但却落得竹篮打水一场空。地外工程师们匆匆回收了尚可回收的资源，释放了经过改造的胚芽，并中止了地表改造计划。  <br><br>可他们却发现，失败的原因乃是“异质”——原本已经被磨灭的兴趣再次燃起。这些“异质”的价值不可估量，不仅引发了淘金热，还加剧了地球上的政治紧张局势。<br><br>勘探者们纷纷涌入翼星，接受萃取异质的合约，并帮助各大派系开展研究工作。那些幸存下来并返回轨道的识途老马，将寻获的异质转化为先进技术，并用新发现的知识获取更大的收获。而那些未能返回的人……便永远失落于翼星之攫。<br><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/7ca2fa761be39c316fd350cf09ed52b8.poster.avif?t=1764892501\" width=600 height=226 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/7ca2fa761be39c316fd350cf09ed52b8.webm?t=1764892501\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/7ca2fa761be39c316fd350cf09ed52b8.mp4?t=1764892501\" type=\"video/mp4\"></video></span><br><br><strong>这是个一心想要将您摧残殆尽的残酷PvE世界。鼓起勇气，面对它，征服它。</strong><br><br>大气环境具有毒性，野生动物残忍无情，顶端猎食者眈眈而视，风暴与林火席卷而来。翼星是一颗暴躁莫名的星球。无论是寻找氧气来源、狩猎下一顿餐食或是为长途跋涉储备物资，准备和计划就是一切。<br><br>您可以独自生存，也可以邀请至多八名Steam好友加入您的游戏，或是在专用服务器上自由游玩（即将推出，敬请期待）。<br><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/7c790688c24020584c6e5bc5c3059db1.poster.avif?t=1764892501\" width=600 height=226 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/7c790688c24020584c6e5bc5c3059db1.webm?t=1764892501\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/7c790688c24020584c6e5bc5c3059db1.mp4?t=1764892501\" type=\"video/mp4\"></video></span><br><br><strong>沉浸在这个看似熟悉却又陌生的世界之中。</strong><br><br>首批勘探者被授予了权限超过128平方公里土地的初始采矿许可证。两张地图（奥林匹斯和冥河）的每一寸土地都经过手工打磨，罕有人迹的洞窟星罗棋布，生态圈各有特色，只待您探索的脚步。您可以采伐每一棵树木，以体素的形式挖开每一枚石头，狩猎这里的每一种生物。您更能驯养并骑乘坐骑，制作先进的科技产品来提高萃取资源的速度，为更加艰险的挑战做准备。<br><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/cda6404ae27b034466418d9f4a348fa7.poster.avif?t=1764892501\" width=600 height=226 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/cda6404ae27b034466418d9f4a348fa7.webm?t=1764892501\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/cda6404ae27b034466418d9f4a348fa7.mp4?t=1764892501\" type=\"video/mp4\"></video></span><br><br><strong>掌握三种独立的科技树：行星科技、玩家天赋和轨道工坊。</strong><br><br>游戏中的科技分为四阶：从手工制作的物品到先进的复合材料与电子器件，更有七种不同的建筑材料。<br><br>术业有专攻，您可以任意挑选各大领域的百余种天赋：探索、建造、耕种、狩猎、驯服、战斗，等等等等。 <br><br>回到轨道工坊，用异质换取具有独特性质的先进科技和物品，它们会在下一次降落之时为您提供助力。<br><br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/6ceb9c0a0df4bd7def8bb634db6cd7c7.poster.avif?t=1764892501\" width=600 height=226 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/6ceb9c0a0df4bd7def8bb634db6cd7c7.webm?t=1764892501\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1149460/extras/6ceb9c0a0df4bd7def8bb634db6cd7c7.mp4?t=1764892501\" type=\"video/mp4\"></video></span><br><br><strong>三种游戏模式：开放世界基地、限时任务和发挥您创想的前哨。</strong><br><br>您可以按自己的喜好体验翼星生活。 <br><br>在开放世界中扎根求存，与环境搏斗，与顶端猎食者一较高下，测试您生存本能的极限。 <br><br>为那些监管这颗行星的权力中介出生入死，接受限时任务，采集异质，获取Ren币报酬。以身无一物的状态降落到行星地表，从头开始建立基地，消磨闲暇时光。<br><br>在前哨地区发挥您最狂野的创想，这里的资源会不断刷新，环境也安全无虞。<br><br>---<br><br><i>《翼星求生》由一支生存游戏开发老手团队——RocketWerkz工作室精心制作，工作室位于美丽的新西兰奥克兰。</i>', '动作,冒险,模拟', 1, '单人,多人,合作,在线合作,Steam 成就,Steam 集换式卡牌,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 德语, 葡萄牙语 - 巴西, 俄语, 西班牙语 - 拉丁美洲, 日语, 韩语, 简体中文, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '© 2023 RocketWerkz Studio Limited', 'https://SurviveIcarus.com', 0, '', '1149460');
INSERT INTO `game_info` VALUES (13, '命运2', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/header_schinese.jpg?t=1765904462', 'Bungie', '2019 年 10 月 1 日', '命运2是发生在一个独立进化的世界中的一款免费的大型多人线上动作游戏。你可以在任何时间与任何地点和你的朋友们进行游戏。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_7fcc82f468fcf8278c7ffa95cebf949bfc6845fc.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_c142f5078ace9f5e2eb2c80aa3bf768e156b4ee9.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_a9642404e586be28f856e8f02d038828f691a5ba.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_01fdd090ed1dd70112ce2c6d6fc208df0a008ac7.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_d230d30da82a7b92c842e7447ee5a8458b866251.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_5065e2bb5de91535dc5e390e27e391a8b9b367ee.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_afc1f202f7ab71adca7d1e503138d89c5689e716.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_324e79ed624f3abd7a2eea2d187d6f616be17102.1920x1080.jpg?t=1765904462\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1085660/ss_a35aaa73e605c0738a53a74abbcb53768e8f39e1.1920x1080.jpg?t=1765904462\"]', '进入命运2的免费游戏世界来探索我们太阳系的隐秘并体验第一人称射击战斗。解锁强大的元素技能并收集独特的装备来个性化你的外观和游戏风格。独自或与其他朋友一起体验命运2的影片故事，进行合作任务，或与其他守护者在PvP模式中进行竞争。今天免费下载，在星空中书写你的传奇。<h2 class=\"bb_tag\" >引人入胜的故事</h2>你是一名守护者，在太阳系中守护人类圣城不被邪恶敌人侵害的保护者。守望星空与暗影正面相抗。你的传说现在开始。<h2 class=\"bb_tag\" >守护者职业</h2>从重装的泰坦、神秘的术士和灵动的猎人中选择。<br><br><br><u><strong>泰坦</strong></u><br>训练有素，傲然群立，泰坦既能激进攻击，又可坚守阵地。点燃你巨锤上的火焰，若闪电划过天空，正面冲击你的敌人。你的队伍将在你的坚强屏障后傲然挺立。<br><br><u><strong>术士</strong></u><br>术士将宇宙奥秘化作自己的武器，保护自己，毁灭敌人。让战场上降下毁灭之雨，眨眼间清除成群敌人。你的同伴将会知道光能真正的力量。<br><br><u><strong>猎人</strong></u><br>灵活且勇猛，猎人行动敏捷，枪法如神。舞动你的金枪，在敌人中如旋风闪过，或是在阴影中给予敌人致命一击。找到敌人，瞄准，然后在战斗开始之前就结束一切。<h2 class=\"bb_tag\" >合作或竞技类多人游戏</h2>在多样的PvE和PvP游戏模式中和你的朋友与其他守护者一起合作或竞争<br><br><br><u><strong>合作类多人游戏</strong></u><br>充满稀有强力奖励的合作探险。利用任务、分支任务和巡逻来体验故事情节。纠集一只小的火力战队，并在打击的最后获取宝箱中的奖励。或是在数小时的突袭推进中检验你的火力战队的技术——这对于任何火力战队都是终极的考验。你决定你的传奇从哪里开始。<br><br><u><strong>竞技类多人游戏</strong></u><br>在无限制交战、团队竞技场和PvE/PvP混合竞赛中与其他玩家正面相抗。在你的日历上标注出特殊的比赛，比如铁旗。并在活动结束前领取限时奖励。<h2 class=\"bb_tag\" >异域武器和护甲</h2>上千种武器，百万种选择。发掘全新的装备搭配并决定你自己的个人风格。完美军械库的狩猎现在开始。', '动作,冒险,免费开玩', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,Steam 成就,应用内购买,可选颜色,自定义音量控制,字幕选项,部分支持控制器', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 日语<strong>*</strong>, 韩语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 俄语<strong>*</strong>, 简体中文<strong>*</strong>, 西班牙语 - 拉丁美洲<strong>*</strong>, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '预计需至少占用105GB的硬盘存储空间（截至2020年11月11日）。储存空间需求可能会有所增加。2020年11月11日后，请在购买前查看当前最新需求：www.destinythegame.com/size-requirements。相关设置、功能和更新都可能需要额外空间，包括下载继续进行游戏所需的更新等。游戏需要连接宽带网络，相应费用需由用户自行承担。在线内容和功能可能需要额外收费。<br />\r<br><br />\r<br>命运2可能包含闪光效果和图片，导致小部分对此敏感的人产生不良反应。<br />\r<br> <br />\r<br>使用本软件即表示您接受《命运》软件许可协议，详情参见：www.bungie.net/sla。Bungie有权随时修改或终止在线服务，而不另行通知。<br />\r<br> <br />\r<br>© 2025 Bungie, Inc.。版权所有。《命运》、《命运》标志、Bungie和Bungie标志均属Bungie, Inc.商标。', 'https://www.bungie.net/', 83, 'https://www.metacritic.com/game/pc/destiny-2?ftag=MCD-06-10aaa1f', '1085660');
INSERT INTO `game_info` VALUES (14, '天国：拯救2', 29.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/header.jpg?t=1766074183', 'Warhorse Studios', '2025 年 2 月 4 日', '走进 15 世纪的欧洲，体验丰富多彩的开放世界，畅玩这款精彩的叙事驱动型角色扮演游戏，开启究极中世纪冒险，扮演年轻的亨利，踏上一场史诗级的旅程。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_935ddb59f90bc2c21c706132cb9b446fe7851c19.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_709f3329562cd2cfe6db70bac908b3e72eaef83b.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_bd668f4d12300c019fa3fa73108aa62929fbf862.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_5580d672302cdc5f8b7161dc94f266341e61a182.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_27e006df1aef43380ae7db0d0b08538b26b49daa.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_e9a693d1027ec19c4fdbe5156abc80adac7039f8.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_293abf266a1847da65bb633b9e76e851715a4b58.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_77abcbc203685b38a50a8cee383bd7f47488f090.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_9013f64f459b341d91a4e65bcad2e9c78a9f61af.1920x1080.jpg?t=1766074183\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/ss_b863918a1655050ee6200846cf8abb71137fa13e.1920x1080.jpg?t=1766074183\"]', '<span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/00d03039da88e060ba12a87ed067411c.avif?t=1766074183\" width=1560 height=988 /></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/1d3bd8dd90e3dd90f5edda67fdffdf5b.poster.avif?t=1766074183\" width=606 height=186 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/1d3bd8dd90e3dd90f5edda67fdffdf5b.webm?t=1766074183\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/1d3bd8dd90e3dd90f5edda67fdffdf5b.mp4?t=1766074183\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/aa72fa6a19a7c0dd9d4e79f39b13f145.poster.avif?t=1766074183\" width=606 height=186 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/aa72fa6a19a7c0dd9d4e79f39b13f145.webm?t=1766074183\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/aa72fa6a19a7c0dd9d4e79f39b13f145.mp4?t=1766074183\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/3a73880f8915e1379094e138ff48e61b.poster.avif?t=1766074183\" width=606 height=186 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/3a73880f8915e1379094e138ff48e61b.webm?t=1766074183\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/3a73880f8915e1379094e138ff48e61b.mp4?t=1766074183\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/6fc497148ef7ee1b072788875207a6b0.poster.avif?t=1766074183\" width=606 height=186 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/6fc497148ef7ee1b072788875207a6b0.webm?t=1766074183\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/6fc497148ef7ee1b072788875207a6b0.mp4?t=1766074183\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/13a61cd2e32dd6876487d441c4729071.poster.avif?t=1766074183\" width=606 height=186 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/13a61cd2e32dd6876487d441c4729071.webm?t=1766074183\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1771300/extras/13a61cd2e32dd6876487d441c4729071.mp4?t=1766074183\" type=\"video/mp4\"></video></span>', '动作,冒险,角色扮演', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,自定义音量控制,无需应对快速反应事件,立体声,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 意大利语, 法语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 捷克语<strong>*</strong>, 日语<strong>*</strong>, 韩语, 波兰语, 葡萄牙语 - 巴西, 简体中文, 繁体中文, 土耳其语, 俄语, 乌克兰语<br><strong>*</strong>具有完全音频支持的语言', '© 2025 Warhorse Studios s.r.o. Published by Deep Silver. Deep Silver is a division of PLAION. Deep Silver and Plaion and their respective logos are trademarks of Plaion GmbH, Embracer Platz 1, 6604 Hoefen, Austria. Warhorse and Kingdom Come: Deliverance are registered trademarks of Warhorse Studios s.r.o. Portions of this software are included under license © 2019-2025 Crytek GmbH. All rights reserved. Crytek, Cryengine and their respective logos are trademarks of Crytek GmbH. All other trademarks, logos and copyrights are property of their respective owners. All rights reserved.', 'https://www.kingdomcomerpg.com/', 88, 'https://www.metacritic.com/game/pc/kingdom-come-deliverance-ii?ftag=MCD-06-10aaa1f', '1771300');
INSERT INTO `game_info` VALUES (15, 'Rust', 19.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/header.jpg?t=1761667247', 'Facepunch Studios', '2018 年 2 月 8 日', '在《Rust》的世界里，生存即是唯一目标。万事万物都想要置你于死地：岛上的野生动物、其他居民、环境以及其他幸存者。尽一切可能，再熬过一个晚上吧。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_271feae67943bdc141c1249aba116349397e9ba9.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_e825b087b95e51c3534383cfd75ad6e8038147c3.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_0e646f1a70e5cb8eed00efef8adb9579d40d5b2e.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_1c2d0d1eefee54f0c67626c74eb21699bbb0ef52.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_d0fdacaeef5a28a7cee525fd73376adfe083c964.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_827f1bb38361eb3f7de91cff9be5b7176a05a9ac.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_bbf6c96e490326ec877ae548cb148e53516b5f83.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_2a8518810024a5fbf9c714e697a43a1201b5d53e.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_c88939db635d959b25eb1bcf9b4c4dcdec04b3fe.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_eafe26f0577f9fc25d7a89884ea6f40381973c8a.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_9652dbaf2de41b8c8f8305af714ee258564c453d.1920x1080.jpg?t=1761667247\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/ss_9d2ad1cd00376605d6f9a778eb7bd1cddfd68ee1.1920x1080.jpg?t=1761667247\"]', '<p class=\"bb_paragraph\" >在《Rust》的世界里，生存即是唯一目标。万事万物都想要置你于死地：岛上的野生动物、其他居民、环境以及其他幸存者。尽一切可能，再熬过一个晚上吧。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/extras/a7b04d13c945e945c7c813ac1a5a7706.poster.avif?t=1761667247\" width=616 height=230 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/extras/a7b04d13c945e945c7c813ac1a5a7706.webm?t=1761667247\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/extras/a7b04d13c945e945c7c813ac1a5a7706.mp4?t=1761667247\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >《Rust》如今已步入持续扩展的第11年，发布了超过385次内容更新，且承诺每月都将推出内容补丁。其中包括定期修复平衡性、改进人工智能和图像、更新生态环境和频繁推出新内容，例如新地图、更多武器、额外载具和大量设施物品。留意定期推出的季度活动，因为岛上总会有些趣事或险情（或有趣的险情）发生。 <br>游戏更新包括：</p><ul class=\"bb_ul\"><li><p class=\"bb_paragraph\" >程序生成的世界，支持社区地图编辑器</p></li><li><p class=\"bb_paragraph\" >主持你自己的服务器，可定制服务器端</p></li><li><p class=\"bb_paragraph\" >交通工具：马匹、模组化汽车、热气球、直升机、自行车、火车、游船等</p></li><li><p class=\"bb_paragraph\" >错综复杂的电力、农业和自动化系统</p></li><li><p class=\"bb_paragraph\" >建设与洗劫基地</p></li><li><p class=\"bb_paragraph\" >全新派系、敌人和野生动物</p></li><li><p class=\"bb_paragraph\" >可供探索的全新纪念碑、地形和区域</p></li><li><p class=\"bb_paragraph\" >可供探索的进阶水下区域</p></li><li><p class=\"bb_paragraph\" >演奏乐器，在玩家们面前尽情表演</p></li><li><p class=\"bb_paragraph\" >大型地面和地下列车网络</p></li><li><p class=\"bb_paragraph\" >近期的图像大升级和环境改造</p></li><li><p class=\"bb_paragraph\" >2025年新增内容：原始游戏模式、武器、载具 </p></li><li><p class=\"bb_paragraph\" >……还有更多精彩</p></li></ul><h2 class=\"bb_tag\" >探索</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/extras/fca5ac19c2c7a1690fb0d2a52279c12c.avif?t=1761667247\" width=628 height=200 /></span></p><p class=\"bb_paragraph\" >你在某座神秘岛屿上醒来，身上一丝不挂，随身物品只有一块石头和一支火炬。你需要勇敢面对大自然中的危险和挑战（从炎热和寒冷，再到口渴和饥饿）、建造庇护所、进食（或成为别人的盘中餐），以及搜刮素材。探索岛屿及其废弃的定居点、神秘的纪念碑和霸占它们的派系。 <br>但最关键的是，密切关注其他幸存者的动向……</p><h2 class=\"bb_tag\" >建造<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/extras/3c3add8a8c81131fc72d637ecbb61e51.avif?t=1761667247\" width=628 height=200 /></span></h2><p class=\"bb_paragraph\" >设计和建造基地来存放你的战利品、捍卫你的领土并控制岛上的资源。研究科技树、发展农场、分配电力并征用各种交通工具穿越岛屿。尽一切可能夺得先机。</p><h2 class=\"bb_tag\" >求生<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/252490/extras/3e32097a3c30052cf4ef0f0abb2be54b.avif?t=1761667247\" width=628 height=200 /></span></h2><p class=\"bb_paragraph\" >《Rust》的世界里没有规则，规则由你来定。制作工具、盔甲和武器来保护自己。与朋友合作、结盟，共同建立定居点。探索岛屿，了解岛上动态，与其他玩家交易，或将他们洗劫一空，突袭他们的基地并抢走他们来之不易的货物。</p><p class=\"bb_paragraph\" ></p>', '动作,冒险,独立,大型多人在线,角色扮演', 1, '多人,大型多人在线,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,Steam 集换式卡牌,Steam 创意工坊,应用内购买,已启用 Valve 反作弊保护,统计数据,在平板上远程畅玩', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 日语<strong>*</strong>, 韩语<strong>*</strong>, 俄语<strong>*</strong>, 简体中文<strong>*</strong>, 乌克兰语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 葡萄牙<strong>*</strong>, 土耳其语<strong>*</strong>, 阿拉伯语, 捷克语, 丹麦语, 荷兰语, 芬兰语, 希腊语, 挪威语, 葡萄牙语 - 巴西, 西班牙语 - 拉丁美洲, 瑞典语, 繁体中文, 越南语<br><strong>*</strong>具有完全音频支持的语言', '', 'http://rust.facepunch.com/', 69, 'https://www.metacritic.com/game/pc/rust?ftag=MCD-06-10aaa1f', '252490');
INSERT INTO `game_info` VALUES (16, 'HELLDIVERS™ 2', 31.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/a52865002e1efa61a4eb56fd79c12f3066df2e25/header_alt_assets_0_schinese.jpg?t=1766051960', 'Arrowhead Game Studios', '2024 年 2 月 8 日', '银河系最后的进攻力量。投入这款紧张刺激且疯狂的第三人称射击游戏，应征成为绝地潜兵，穿越战火肆虐的星系，为自由而战。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_0c79f56fc7be1bd0102f2ca1c92c8f0900daf4fb.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_33e684e9cb2517af1599f0ca2b57d65ee82c2e51.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_8949ed7dd24a02d5ea13b08fc5c04fab400dc4bd.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_50afbbc4d811c38fe9f64c1fc8d7eb9d9da6d24c.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_cb276fe9f0b09683bdbc496f82b405dbe0ffa1f1.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_d0ac3830833a68d19d4a97c70aef0fba20bf0761.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_3b08a991443164a65f84f1bd9f1363e6c2ec4581.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_8f4af550ecbee08abb616b12cbb3896174dce153.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_3d075daf49e21731cd768ee2ea3adf63857f798c.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_bed14617fc317c2d2b59fdc4d8c4f29b8ddf7ed2.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_e45cb071779848564b3d2dcd7c3d38b7bd81cf9c.1920x1080.jpg?t=1766051960\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/ss_5dde7ea6922800dfece2a3a319cb68e4f6f67949.1920x1080.jpg?t=1766051960\"]', '<span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/fb8876ccfdce9b8ec3b4e594c903b1fa.poster.avif?t=1766051960\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/fb8876ccfdce9b8ec3b4e594c903b1fa.webm?t=1766051960\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/fb8876ccfdce9b8ec3b4e594c903b1fa.mp4?t=1766051960\" type=\"video/mp4\"></video></span><br>银河系最后的进攻力量。<br><br>投入这款紧张刺激且疯狂的第三人称射击游戏，应征成为绝地潜兵，穿越战火肆虐的星系，为自由而战。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/4fb677a524eaf9318620399defcc00b4.poster.avif?t=1766051960\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/4fb677a524eaf9318620399defcc00b4.webm?t=1766051960\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/4fb677a524eaf9318620399defcc00b4.mp4?t=1766051960\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >紧急广播 – 超级地球武装力量</h2>自由、和平、民主。<br>这是超级地球公民与生俱来的权利，是我们文明的重要支柱，<br>也是我们存在的基石。<br>但战争愈演愈烈，这一切再次受到威胁。<br>加入银河系有史以来最强大的军事力量，重新恢复这里的安宁与自由。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/35ca28196240c0a23e7b3f1e9f21104f.poster.avif?t=1766051960\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/35ca28196240c0a23e7b3f1e9f21104f.webm?t=1766051960\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/35ca28196240c0a23e7b3f1e9f21104f.mp4?t=1766051960\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >创造传奇</h2>您将与最多三名绝地潜兵组建小队，执行战略任务。<br>互相照应 – 虽然友军误伤是战争中难以避免的不幸，但唯有团队合作才能最终获胜。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/ed035f1ca25e13a4b37838ad0a93c7ad.poster.avif?t=1766051960\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/ed035f1ca25e13a4b37838ad0a93c7ad.webm?t=1766051960\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/ed035f1ca25e13a4b37838ad0a93c7ad.mp4?t=1766051960\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >武器配置</h2>呼叫自由炮火从天而降，潜入敌方领地，亦或是咬紧牙关，一马当先，冲锋陷阵。<br>您可以自行选择实现解放大业的方式；各种爆炸性火力、救命盔甲以及扭转战局的战略配备，应有尽有……这是每个绝地潜兵的军械库瑰宝。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/073978e84f89d3c2cb173bf4e92facaa.poster.avif?t=1766051960\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/073978e84f89d3c2cb173bf4e92facaa.webm?t=1766051960\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/073978e84f89d3c2cb173bf4e92facaa.mp4?t=1766051960\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >申购点</h2>为了奖励您卓越的战功，超级地球将为您提供宝贵的申购点。利用申购点获取不同的奖励，武装您自身、您的小队和您的驱逐舰，提升己方整体战力。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/6d14a34ac01cedb5822b1786c3a00b61.poster.avif?t=1766051960\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/6d14a34ac01cedb5822b1786c3a00b61.webm?t=1766051960\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/6d14a34ac01cedb5822b1786c3a00b61.mp4?t=1766051960\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >威胁</h2>每颗外星球上的一切生物都想置您于死地，这就是我们不得不面对的局面。<br>每个敌人都具有独特且难以预测的特征、战术和行为 – 但他们都有共同点：战斗凶狠、无畏无惧、不受任何道德的约束。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/2f228b6f119f7d9207d351d5393cabea.poster.avif?t=1766051960\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/2f228b6f119f7d9207d351d5393cabea.webm?t=1766051960\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/553850/extras/2f228b6f119f7d9207d351d5393cabea.mp4?t=1766051960\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >星系战争</h2>占领敌方星球，抵御入侵，完成任务，推进整体战局。<br>这场战争的胜负成败取决于每个参与者的每一分努力。<br>如不能共同获胜，就只能一同落败。', '动作', 1, '多人,合作,在线合作,Steam 成就,应用内购买,可调整文字大小,视角舒适度,聊天语音转文字,聊天文字转语音,可选颜色,自定义音量控制,可调整难度,朗读游戏菜单,无需应对快速反应事件,立体声,字幕选项,环绕声,部分支持控制器,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 日语<strong>*</strong>, 韩语, 葡萄牙语 - 巴西<strong>*</strong>, 西班牙语 - 拉丁美洲<strong>*</strong>, 波兰语, 葡萄牙语 - 葡萄牙, 俄语, 简体中文, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '©2023 Sony Interactive Entertainment LLC. Developed by Arrowhead Game Studios AB. HELLDIVERS is a registered trademark of Sony Interactive Entertainment LLC and related companies in the U.S. and other countries.', 'https://www.playstation.com/games/helldivers-2/pc/', 0, '', '553850');
INSERT INTO `game_info` VALUES (17, 'Red Dead Redemption 2', 14.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1174180/header.jpg?t=1759502961', 'Rockstar Games', '2019 年 12 月 5 日', '亚瑟·摩根和范德林德帮众是一群逃亡在外的亡命之徒。联邦侦探和赏金猎人在他们的身后穷追不舍，亚瑟一行人必须在广袤蛮荒的腹地上四处劫掠、挣扎求生。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1174180/ss_66b553f4c209476d3e4ce25fa4714002cc914c4f.1920x1080.jpg?t=1759502961\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1174180/ss_bac60bacbf5da8945103648c08d27d5e202444ca.1920x1080.jpg?t=1759502961\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1174180/ss_668dafe477743f8b50b818d5bbfcec669e9ba93e.1920x1080.jpg?t=1759502961\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1174180/ss_4ce07ae360b166f0f650e9a895a3b4b7bf15e34f.1920x1080.jpg?t=1759502961\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1174180/ss_d1a8f5a69155c3186c65d1da90491fcfd43663d9.1920x1080.jpg?t=1759502961\"]', '美国，1899 年。亚瑟·摩根和范德林德帮众是一群逃亡在外的亡命之徒。联邦侦探和全国顶尖的赏金猎人在他们的身后穷追不舍，亚瑟一行人必须在广袤蛮荒的美国腹地上四处劫掠、挣扎求生。而帮派内部的矛盾分化日渐加深，摆在亚瑟面前的将是他无法避免的抉择：究竟是选择自己的理想，还是选择效忠于抚养了自己的帮派。<br />\r<br><br />\r<br>Red Dead Redemption 2 已荣获超过 175 项年度游戏奖项且获得超过 250 个满分评价，游戏以 19 世纪的最后岁月为背景，述说一个关于荣誉与忠诚的史诗故事。<br />\r<br><br />\r<br>Red Dead Redemption 2 还包含 Red Dead 在线模式，一款以 Red Dead Redemption 2 生活世界为背景的多人游戏体验，玩家可以独自骑行或组建团队，酿造私酒，与执法人员、不法帮派和凶猛的野生动物战斗，在美洲边境开辟自己的道路。', '动作,冒险', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,Steam 成就,应用内购买,部分支持控制器,在手机上远程畅玩,在平板上远程畅玩', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 俄语, 简体中文, 西班牙语 - 拉丁美洲, 繁体中文, 葡萄牙语 - 巴西<br><strong>*</strong>具有完全音频支持的语言', '软件授权条款请参阅游戏内说明及 rockstargames.com/zh/eula；在线游戏账户使用条款请参阅 zh-cn.socialclub.rockstargames.com/。使用不得转让的特殊功能，例如独家提供、可解锁、可下载、多人游戏、在线与额外内容、服务、功能时，可能需要单次使用的序号、额外负担费用和/或不可转让的在线注册账号（需年满十三岁）。使用特别游戏功能可能需要配备因特网联机，并非所有用户或在任何时间内皆能使用，并且可能不经通知而终止、修改或以不同条款提供。违反 EULA、 行为准则或是其他政策，可能导致使用本游戏或在线账号的权利遭到限制或终止。如需信息、客户及技术支持，请前往 support.rockstargames.com/zh。本游戏纯属虚构。游戏中可能出现与现实生活中相似的人物、地点、公司、团体、活动、建筑等内容；它们与本游戏无任何关系，且相关内容亦非事实。本游戏的制造商、发行商和授权人并未批准、容忍或鼓励任何内容。严禁未经授权进行拷贝、修改、逆向工程、反编译、传播、公共展示、租借、盈利性游玩或制售盗版和违反 EULA 等行为。某些限制适用于购买，使用和兑换。 更多详情，敬请查看 EULA（www.rockstargames.com/zh/eula）和服务条款（www.rockstargames.com/zh/legal）。<br />\r<br><br />\r<br>Rockstar Games, Inc.  ©2005-19. Rockstar Games、Red Dead Redemption、R*、Redemption、Red Dead 和 Dead Eye 是 Take-Two Interactive 的标志/徽标/版权。杜比和双 D 符号为杜比实验室的商标。所有其他标志和商标均为其各自所有者的产权。保留所有权利。', 'https://www.rockstargames.com/reddeadredemption2/', 93, 'https://www.metacritic.com/game/pc/red-dead-redemption-2?ftag=MCD-06-10aaa1f', '1174180');
INSERT INTO `game_info` VALUES (18, 'Ghost of Tsushima导演剪辑版', 35.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/header.jpg?t=1763409398', 'Sucker Punch Productions', '2024 年 5 月 16 日', '风暴将至，在PC上体验完整的《Ghost of Tsushima导演剪辑版》，在这场开放世界动作冒险中开辟自己的道路，发现隐藏的奇景。本游戏由Sucker Punch Productions、Nixxes Software和PlayStation Studios为您呈现。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_51eb1a47a41271abb0aa781de576f704d95b601b.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_d60e16422fc1605181e0fedee3e2cfc1e02c750e.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_11409331d57966ef193d66ffcded569d02a4e034.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_bd593bab36968778b95ed4a1a12ec82d2350d351.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_63cc7ea967bf808c73129792d9ae8e3fdb2962bf.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_b94e296457324be29466facc1fd93a91b6189a9b.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_21d0dcbd4c37f7d3fadfafad3c6cd729d616ec7d.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_461f13ee2717ddf401e2c0c2320d8e847935e65b.1920x1080.jpg?t=1763409398\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2215430/ss_4fe2e0938135765483d8ee5942be5562d8f31912.1920x1080.jpg?t=1763409398\"]', '<i>境井仁的旅途首次在PC端展开，在此导演剪辑版中体验完整的《Ghost of Tsushima》。</i><br><br>13世纪末，蒙古帝国东征，铁蹄所到之处皆成焦土。残暴狡诈的蒙军统帅赫通汗率领舰队大举入侵，对马岛是横亘在侵略者与日本本土之间的最后一道防线。<br><br>当对马岛被蒙军首波攻势的战火点燃时，勇敢的武士境井仁坚定地站了出来。作为氏族中为数不多的幸存者之一，仁下定决心要不惜任何代价、竭尽所能地保护同胞，夺回家园。他必须放弃毕生秉承的武士之路，开辟一条全新的“战鬼”之道，以非常之法，为对马岛的自由而战。<br><br><ul class=\"bb_ul\"><li>解锁全新帧率和专为各类硬件定制的画质选项，从高端电脑到便携电脑游戏设备，尽情体验《Ghost of Tsushima》。*<br></li><li>新增对超宽屏 (21:9)、极宽屏 (32:9) 及三屏模式 (48:9) 的支持，让你可以欣赏更完整的动作画面。*<br></li><li>通过NVIDIA DLSS 3、AMD FSR 3和Intel XeSS等图像增强和帧生成技术提高性能，同时也支持NVIDIA Reflex和用于画质加强的NVIDIA DLAA。** <br></li><li>日语发音口型同步：通过电脑实时渲染出的过场动画，带来更为逼真还原的日语发音口型效果。<br></li><li>选择动作控制方式：使用有线DualSense™控制器，体验触觉反馈及自适应扳机……***<br></li><li>……也可使用鼠标和键盘，体验可全局定制的控制项。<br></li><li>触觉反馈：通过DualSense™控制器沉浸式的触觉反馈控制挥刀。***<br></li><li>自适应扳机：利用自适应扳机的阻力，提高弓箭的准确性。***</li></ul><br><i>* 需要兼容的电脑和显示设备。<br>** 需要兼容的电脑和显卡。<br>*** 需要将控制器进行有线连接，方可在游戏内体验其全部功能。</i>', '动作,冒险', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,Steam 成就,完全支持控制器,Steam 集换式卡牌,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,可调整难度,无需应对快速反应事件,随时保存,立体声,字幕选项,环绕声,Steam 云,可用 HDR,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 阿拉伯语, 捷克语, 丹麦语, 荷兰语, 芬兰语, 希腊语, 匈牙利语, 日语<strong>*</strong>, 韩语, 挪威语, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 葡萄牙语 - 葡萄牙<strong>*</strong>, 俄语<strong>*</strong>, 简体中文, 西班牙语 - 拉丁美洲<strong>*</strong>, 瑞典语, 泰语, 繁体中文, 土耳其语, 克罗地亚语<br><strong>*</strong>具有完全音频支持的语言', '©2024 Sony Interactive Entertainment LLC. Developed by Sucker Punch Productions LLC. Ghost of Tsushima is a registered trademark of Sony Interactive Entertainment LLC in the United States and other countries.', 'https://www.playstation.com/en-us/games/ghost-of-tsushima/pc/', 0, '', '2215430');
INSERT INTO `game_info` VALUES (19, 'The Sims™ 4', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/header.jpg?t=1765476116', 'Maxis', '2020 年 6 月 18 日', '在一个无拘无束的虚拟世界中，享受创造和控制人生的奇妙力量。 得到力量和自由，享受快乐，游戏人生！', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/ss_537683e5e29c2d6d02c64aa7321dcb26166f7d82.1920x1080.jpg?t=1765476116\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/ss_2fc938d99a1e87893852cb2d2113478190607941.1920x1080.jpg?t=1765476116\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/ss_b447d6436d2d00cd5e7c170e509b2246dfc879e8.1920x1080.jpg?t=1765476116\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/ss_e1f0ce3ca2c9a538e382cc8b040944fe05af4bb2.1920x1080.jpg?t=1765476116\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/ss_7d9baea1aedeeb41cb197a9e45a30f0b91b4baf9.1920x1080.jpg?t=1765476116\"]', '释放您的想象力，创造一个独特的模拟市民世界，秀出您的独特风格。免费下载游戏，并自定义模拟市民及其住家的每个细节，还有更多内容。选定模拟市民的外观、行为和衣着，并决定他们将如何度过每一天。为每个家庭设计和建造超棒的家，并用您最喜爱的家具和装饰进行装扮。前往不同社区，结识其他模拟市民，了解他们的生活。发掘环境独特的美丽地点，踏上随心的冒险之旅。管理模拟市民日常生活的起起落落，看看将您现实生活中的场景演绎出来会发生什么。在发展人际关系、追求职业目标和人生抱负过程中，用自己的方式讲述自己的故事，沉浸在这个非凡卓越的游戏里，体验其中的无尽可能。<br><br><strong>免费下载</strong> — 《The Sims™ 4》主游戏可免费下载。您有多到用不完的选项，可用于建造家园、打扮模拟市民和自定义他们的个性。创作他们的人生故事，探索充满活力的世界，发掘更多活出自我的方式。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/5dc12edc46e061d1cd0711413a1a3ad5.poster.avif?t=1765476116\" width=610 height=342 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/5dc12edc46e061d1cd0711413a1a3ad5.webm?t=1765476116\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/5dc12edc46e061d1cd0711413a1a3ad5.mp4?t=1765476116\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/0b59ec42f6f78f6edd77ca85c4d64463.poster.avif?t=1765476116\" width=610 height=342 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/0b59ec42f6f78f6edd77ca85c4d64463.webm?t=1765476116\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/0b59ec42f6f78f6edd77ca85c4d64463.mp4?t=1765476116\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/f085b7e7088c0992e873acd16a6043aa.poster.avif?t=1765476116\" width=610 height=342 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/f085b7e7088c0992e873acd16a6043aa.webm?t=1765476116\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/f085b7e7088c0992e873acd16a6043aa.mp4?t=1765476116\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/6cf507ce28bd2dd3e898edb494ea3279.poster.avif?t=1765476116\" width=610 height=342 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/6cf507ce28bd2dd3e898edb494ea3279.webm?t=1765476116\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/6cf507ce28bd2dd3e898edb494ea3279.mp4?t=1765476116\" type=\"video/mp4\"></video></span><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/c73dfbd300abfdbfd3b68ef33cbbd2dd.poster.avif?t=1765476116\" width=610 height=342 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/c73dfbd300abfdbfd3b68ef33cbbd2dd.webm?t=1765476116\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1222670/extras/c73dfbd300abfdbfd3b68ef33cbbd2dd.mp4?t=1765476116\" type=\"video/mp4\"></video></span><br><br><strong>通过 EA Play 获取更多内容</strong> — EA Play 会员可以通过《The Sims 4 来去上班》资料片拓展他们职业生涯可能性。', '冒险,休闲,模拟,免费开玩', 1, '单人,Steam 集换式卡牌,可以仅用鼠标,无需应对快速反应事件,随时保存,立体声,环绕声,在平板上远程畅玩', '英语, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 捷克语, 丹麦语, 荷兰语, 日语, 挪威语, 波兰语, 葡萄牙语 - 巴西, 俄语, 瑞典语, 芬兰语, 繁体中文, 简体中文, 韩语', '仅限于德国、奥地利和瑞士： 适用EA保密和COOKIE协议(privacy.ea.com/de)。<br />\r<br>仅限于其他欧盟成员国、英国、挪威、冰岛、安道尔、波斯尼亚和黑塞哥维那、格鲁吉亚、科索沃、马其顿(FYROM)、摩尔多瓦、摩纳哥、黑山共和国、圣马力诺、塞尔维亚、土耳其、梵蒂冈（圣座）、列支敦士登： 需确认EA保密和COOKIE协议(privacy.ea.com/sc)方可开始游戏。<br />\r<br>所有其他居民： 需接受EA保密和COOKIE协议(privacy.ea.com/sc)方可开始游戏。<br />\r<br><br />\r<br>网络连接；EA账户；STEAM账户；接受EA用户协议(terms.ea.com/sc)和ORIGIN最终用户许可协议(ea.com/zh-cn/legal)；同时需下载并安装ORIGIN客户端软件(origin.com/download)方可开始游戏。连接EA账户和STEAM账户后，方可开始游戏；EA将与STEAM共享您的账户ID、个人游戏和比赛记录，便于验证您的购买和/或退款申请。仅限使用一个EA账户和一个STEAM账户访问软件内容，购买后不可转让。您可能需年满至少13或16周岁，方可注册EA账户（年龄限制可能会有所不同，详情请参见https://o.ea.com/ea/child-access）。有些内容可能需要通过进行游戏来解锁。系统可能会自动下载更新内容，可能需要额外存储空间，还可能会产生宽带使用费用。EA可能会适时免费提供特定增量内容和/或更新。EA 可能会在 www.ea.com/service-updates 上发布公告 30 天后撤除相关的在线游戏内容和服务。<br />\r<br><br />\r<br>EA用户协议： 德国居民请访问 terms.ea.com/de；所有其他地区居民请访问 terms.ea.com/sc。<br />\r<br>EA保密和COOKIE协议： 德国居民请访问 privacy.ea.com/de；所有其他地区居民请访问 privacy.ea.com/sc。<br />\r<br>Origin最终用户许可协议： 德国居民请访问 ea.com/de-de/legal；所有其他地区居民请访问 ea.com/zh-cn/legal。', 'https://www.ea.com/games/the-sims/the-sims-4', 0, '', '1222670');
INSERT INTO `game_info` VALUES (20, 'Call of Duty®: Black Ops 7', 48.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/90b499c62dae31db5ebebb680e0b943bd0d0f900/header_schinese.jpg?t=1766101946', 'Treyarch', '2025 年 11 月 13 日', '在《使命召唤®：黑色行动 7》中，Treyarch和Raven Software将为玩家带来最大规模的《黑色行动》。充满创新的合作战役，刺激的多人游戏和传奇的波次制丧尸模式。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/10be1d8a9b68f19a7b0321f4adc819374955268d/ss_10be1d8a9b68f19a7b0321f4adc819374955268d.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/6781fd040d6b20a45602b316cabc65720ae63d1f/ss_6781fd040d6b20a45602b316cabc65720ae63d1f.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/7bce43350428d4b8859481263113e30d169451d9/ss_7bce43350428d4b8859481263113e30d169451d9.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/ac2402cb806a974535c32f2f9969f1790d47d039/ss_ac2402cb806a974535c32f2f9969f1790d47d039.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/f683d84b549e783eaa9b3e7c4cfb9f3b06fc26fa/ss_f683d84b549e783eaa9b3e7c4cfb9f3b06fc26fa.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/7978a092f83fe88764edd258dc746175c27895ab/ss_7978a092f83fe88764edd258dc746175c27895ab.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/315eadbae30a21d15a2b4e87eee87525324f8edb/ss_315eadbae30a21d15a2b4e87eee87525324f8edb.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/9db53830261a37d02464756903f9b985dea26f5e/ss_9db53830261a37d02464756903f9b985dea26f5e.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/3c559b48b89293eb3f2d598d4fa865e6c90e0f74/ss_3c559b48b89293eb3f2d598d4fa865e6c90e0f74.1920x1080.jpg?t=1766101946\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/54f46854240a218fa3e4d678f9b6bbc2061a2fe1/ss_54f46854240a218fa3e4d678f9b6bbc2061a2fe1.1920x1080.jpg?t=1766101946\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/extras/2001f03e52464000ef7874a2781a0e68.poster.avif?t=1766101946\" width=1170 height=658 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/extras/2001f03e52464000ef7874a2781a0e68.webm?t=1766101946\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3606480/extras/2001f03e52464000ef7874a2781a0e68.mp4?t=1766101946\" type=\"video/mp4\"></video></span>在《使命召唤®：黑色行动 7》中，Treyarch和Raven Software将为玩家带来最大规模的《黑色行动》。</p><p class=\"bb_paragraph\" >故事设定于2035年，世界正处于混乱的边缘。全球陷入了暴力冲突与心理战的深渊。大卫·梅森率领一支精英联合特战司令部小队，执行一项在宏大都市阿瓦隆的秘密任务。他们揭露了一个阴谋，不只将世界拖入混沌，还将他们自己卷入各自噩梦般的过往。</p><p class=\"bb_paragraph\" >可以组队，也可以单人作战。在一场重新定义的黑色行动创新合作战役中踏上征程。</p><p class=\"bb_paragraph\" >多人火力全开，16张劲爆的6对6地图和2张20对20地图将在首发上线。掌握尖端武器库，并通过进化版的全向移动系统巧妙击败敌人。</p><p class=\"bb_paragraph\" >在Treyarch传奇的波次制丧尸模式中，噩梦在现实终结之处开始。小队被困于黑暗以太核心，投身于黑色行动最大规模波次制丧尸地图，一片广袤、不断变化的地狱。</p>', '动作', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,支持字幕,应用内购买,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,无需应对快速反应事件,立体声,字幕选项,环绕声,部分支持控制器,可用 HDR', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 韩语, 波兰语, 俄语, 繁体中文, 日语<strong>*</strong>, 西班牙语 - 拉丁美洲<strong>*</strong>, 阿拉伯语, 简体中文<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong><br><strong>*</strong>具有完全音频支持的语言', '需要启用TPM 2.0和安全启动，可能还会强制执行其他安全措施。请访问https://support.activision.com/tpm了解详情。<br />\r<br><br />\r<br>*此处的战斗通行证/黑色组织仅对应《黑色行动 7》中的单一赛季战斗通行证。<br />\r<br><br />\r<br>内容、功能、服务、在线游戏和客服并非在所有地区均可使用，且可能有所不同、变更或终止。<br />\r<br><br />\r<br>需要拥有动视账号，并接受动视软件许可和服务协议。可能需要将手机号码和您的动视账号关联，才能进入《黑色行动 7》。<br />\r<br><br />\r<br>必要的游戏更新可能需要额外的储存空间。<br />\r<br><br />\r<br>请前往www.callofduty.com了解详情。<br />\r<br><br />\r<br>©/TM/® 2025 Activision Publishing, Inc. 本产品内有获得Id Software (\'Id Technology\')许可使用的软件技术。Id Technology © 1999-2025 Id Software, Inc.', 'https://www.callofduty.com/', 0, '', '3606480');
INSERT INTO `game_info` VALUES (21, '光与影：33号远征队', 39.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/b3cb63bf5aa3ffd7790310e4361050c368569314/header_schinese.jpg?t=1766050625', 'Sandfall Interactive', '2025 年 4 月 24 日', '率领第33号远征队踏上消灭绘母的征途，竭尽全力夺下她绘就死亡的画刷。置身这款采用即时机制的回合制角色扮演游戏，探索奇妙世界，感受法兰西19世纪末“美好年代”时期的风采，对抗超乎想象的敌人。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_483a27df5072beb3a4650634a764bda750fbcb82.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_e49800e906e8a0f00707458c836567c933603bac.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_b8089016095e6a16e324a59c45b2f24a439bd0b3.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_8439c07d7b1f2fcfc6449db5f051f8d0867f4785.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_9e050e6a61a4d9f4fe54bc62c8c73da38e9a63b0.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_d3a10809f5cc2a8df7773f41acd1493f4fb900ec.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_ec16f873c7d14fc4a4f17966b25f9712dc486b4a.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_c130295c9e1169dc0c63eaae0618e64d06a88c92.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_0902af724ac45a9093dee022a7893dc50cbf555e.1920x1080.jpg?t=1766050625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/ss_49ba857fef972f2584e41fa0b46cddbcda8aa30f.1920x1080.jpg?t=1766050625\"]', '<span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/6d2ade3c867f7e3c30f130354fb715fb.poster.avif?t=1766050625\" width=780 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/6d2ade3c867f7e3c30f130354fb715fb.webm?t=1766050625\" type=\"video/webm; codecs=vp9\"></video></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/14184edb30018a47759300c6d1427a91.avif?t=1766050625\" width=780 height=68 /></span><br>每一年，沉睡的绘母都会苏醒一次，在纪石上画下一个新的数字，所有年龄大于该数字的人，都会烟消云散。石头上的数字逐年变小，生者逐年减少。明天，她将再次醒来，画上数字“33”。明天，我们也将启程，执行最后的任务——消灭绘母，让她无法继续绘就死亡。<br><br>我们是第33号远征队。<br><br>《光与影：33号远征队》这款回合制角色扮演游戏采用了特别的即时机制，开创了品类先河，战斗将带来前所未有的沉浸体验，必定让玩家欲罢不能。探索奇妙世界，感受法兰西19世纪末“美好年代”时期的风采，与强大的敌人展开殊死战斗。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/ab5b88e5bf9ce1f2f9dd8d0d38784cee.poster.avif?t=1766050625\" width=780 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/ab5b88e5bf9ce1f2f9dd8d0d38784cee.webm?t=1766050625\" type=\"video/webm; codecs=vp9\"></video></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/1864e3653952fef86c1161a22010ef33.avif?t=1766050625\" width=780 height=68 /></span><br>在这款开拓创新的日式角色扮演游戏中，即时行动将令回合制战斗更加激烈。根据自己的游戏风格，调整装备、属性、技能以及角色之间的协同配合，为你的远征队员设计只属于你的构筑。挑战瞬息万变的战斗系统：看准时机及时闪避、格挡、反击，掌握攻击节奏，发动连锁打击，善用自由瞄准系统，精准打击敌人的弱点部位。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/6c60128dacdac672ae90d333a93e66bc.poster.avif?t=1766050625\" width=780 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/6c60128dacdac672ae90d333a93e66bc.webm?t=1766050625\" type=\"video/webm; codecs=vp9\"></video></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/a3fb3942e8b9c45a8bc051479bbbd717.avif?t=1766050625\" width=780 height=68 /></span><br>大家只剩下一年的寿命，跟随古斯塔夫、玛埃尔以及其他远征队员背水一战，踏上征途，无论如何都要打破绘母的死亡循环。追寻其他远征队的踪迹，了解他们最后的命运。逐渐认识33号远征队的成员，看他们最终能够携手并进，战胜绝望的命运。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/6cb7b05eb03c20e77a87ab84837efede.poster.avif?t=1766050625\" width=780 height=438 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/6cb7b05eb03c20e77a87ab84837efede.webm?t=1766050625\" type=\"video/webm; codecs=vp9\"></video></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1903340/extras/33e010a8fb669bf29277369f453acea3.avif?t=1766050625\" width=780 height=68 /></span><br>探索迷人的世界，面对奇形怪状的敌人。走过壮美的土地，从千面之岛到遗落的战场，一路发掘秘密和隐蔽的任务。结识传奇生物，结交忠诚的战友，招募特殊的伙伴，发现全新的跋涉方式，探索世界地图的秘密区域。<br><br>感受Sandfall Interactive完全通过Unreal Engine 5打造的处女作，欣赏让人叹为观止的精美画面和动人心弦的原声音轨。', '动作,角色扮演', 1, '单人,Steam 成就,完全支持控制器,可调整文字大小,自定义音量控制,可调整难度,立体声,字幕选项,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 繁体中文, 捷克语, 印度尼西亚语, 西班牙语 - 拉丁美洲, 泰语, 土耳其语, 乌克兰语, 越南语<br><strong>*</strong>具有完全音频支持的语言', 'Developed by Sandfall Interactive. Published by Kepler Interactive. All rights reserved.', 'https://www.expedition33.com/', 91, 'https://www.metacritic.com/game/pc/clair-obscur-expedition-33?ftag=MCD-06-10aaa1f', '1903340');
INSERT INTO `game_info` VALUES (22, '全面战争：战锤3', 14.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/header.jpg?t=1764870033', 'CREATIVE ASSEMBLY', '2022 年 2 月 16 日', '《Total War: WARHAMMER》三部曲的终末已经到来。集结你的军力，踏入混沌魔域！在这个变幻莫测的恐怖维度中，世界的命运将尘埃落定。于是你会诛灭那些恶魔…还是转而驾驭他们呢？', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_6149b0570c42cda0e40b4de71e6f48bbe5c4c577.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_320f5373c16e33f50c0ee2ccc822344c0e0d5f8d.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_4afac709f1bfe992122e315852cf6ea2f58b28ba.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_086d9d777d14d64c9e8bc1713b088381033dfeeb.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_7c75e458f64a5d1c6d7360fd1e30c73c4f655f0b.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_77d1f2d9a162b8ddf6f84a210ee39c3b74ee576a.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_a55fad2780446067c6eb8079c92a5bd218e1b542.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_b92530cc6fc9c4cb0f066eb42983bf7ca17b478b.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_f2876f3cb6f313d8f1271596b259fc47a69d4c15.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_8c43a21dcb0af8114168c97159361dee76b39c32.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_096cc26ed1c7a96e8e4fb7c2df71472c1b8b951a.1920x1080.jpg?t=1764870033\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/ss_2217d0c20782e838586373036c6abb60dcdc7059.1920x1080.jpg?t=1764870033\"]', '<span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/4399b64b745c1a2f0c3a90f5c8958dcd.avif?t=1764870033\" width=616 height=346 /></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/868c3d11f98ec6a6d25501bded60d988.avif?t=1764870033\" width=616 height=83 /></span><br>垂死的神明最后一声咆哮打破了世界之间的界限，开启了通向混沌魔域的传送门。恐虐、纳垢、奸奇、色孽，这四股毁灭之力从漩涡之中涌现了出来，它们四处散布着黑暗与绝望。<br><br>基斯里夫坚毅的勇士和幅员辽阔的震旦天朝就在风口浪尖之上，而一位恶魔亲王正在寻仇，他发誓要消灭那些腐蚀他的人。即将到来的风波将会吞噬一切。你会征服你的恶魔，还是让它们听令于你？ <br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/cb704e9e94a3a9f82c2795fa3a47d53c.avif?t=1764870033\" width=616 height=83 /></span><br>你可以控制七个各具特色的种族以及数百种部队来打造一支军队，在那令人叹为观止的壮观世界里，纵横于史诗般的即时战斗之中。<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/53594a7d48dcf7a7ec302bb0addd31d1.avif?t=1764870033\" width=616 height=344 /></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/f7005d773cb763558e117e4495dcca65.avif?t=1764870033\" width=616 height=83 /></span><br>这是迄今为止最具雄心也是最具新意的《Total War》游戏，那引人入胜的叙事将让你代入到游戏之中，这会让你进入魔幻的混沌魔域之中，然后再回到凡世之中。在战略沙盒之中打造你的帝国，这沙盒之中每一场战役都是独一无二的。<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/0d04020f1bac73bca6cdb2710c63d3f0.avif?t=1764870033\" width=616 height=344 /></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/ca096cd20c8595f5e46d8c5e391f0cc5.avif?t=1764870033\" width=616 height=83 /></span><br>有10位传奇领主可供扮演！你会选择站在混沌诸神的一边进行他们的恶魔战役，还是选择扮演震旦天朝的远古化形神龙？是与冰雪女王一起守护基斯里夫的冰封国度，还是扮演恶魔亲王来成为终极反派？<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/0e90bfa944229bb57cd64e99e3720ecf.avif?t=1764870033\" width=616 height=344 /></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/953e6a3b797e02253b64bab09565aab4.avif?t=1764870033\" width=616 height=83 /></span><br>用海量的身体部件和力量定制你独一无二的恶魔亲王，你有无数种潜在的组合方式来配合你自己的游戏风格<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/694fe09d6942684f8f104b301ed29250.avif?t=1764870033\" width=616 height=344 /></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/ad9631a5859bf15fa8b691a1bd7b3232.avif?t=1764870033\" width=616 height=83 /></span><br>多人游戏的规模现在比以往更加庞大。混沌魔域的宏大多人战役可容纳8名玩家，还有紧张刺激的一对一君临模式，以基斯里夫和震旦为基础的剧情多人战役，还有排名与自定义战斗等等，想要与朋友来几场大混战，办法有的是。<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1142710/extras/344243864c93aecc3521c90c0528c6da.avif?t=1764870033\" width=616 height=344 /></span>', '动作,策略', 1, '单人,多人,玩家对战,线上玩家对战,局域网玩家对战,合作,在线合作,局域网合作,Steam 成就,Steam 集换式卡牌,视角舒适度,可选颜色,自定义音量控制,可调整难度,无需应对快速反应事件,随时保存,立体声,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 捷克语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 繁体中文, 土耳其语<br><strong>*</strong>具有完全音频支持的语言', '© Copyright Games Workshop Limited 2022. All rights reserved. Warhammer, the Warhammer logo, GW, Games Workshop, The Game of Fantasy Battles, the twin-tailed comet logo, and all associated logos, illustrations, images, names, creatures, races, vehicles, locations, weapons, characters, and the distinctive likeness thereof, are either ® or TM, and/or © Games Workshop Limited, variably registered around the world, and used under licence. Developed by Creative Assembly and published by SEGA. Creative Assembly, the Creative Assembly logo, Total War and the Total War logo are either registered trademarks or trademarks of The Creative Assembly Limited. SEGA and the SEGA logo are either registered trademarks or trademarks of SEGA CORPORATION. SEGA is registered in the U.S. Patent and Trademark Office. All other trademarks, logos and copyrights are property of their respective owners.', 'https://warhammer3.totalwar.com/', 86, 'https://www.metacritic.com/game/pc/total-war-warhammer-iii?ftag=MCD-06-10aaa1f', '1142710');
INSERT INTO `game_info` VALUES (23, 'SILENT HILL f', 41.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/7e5d923ac622bd1775ebc9b5d4b5b0a24bf5ed40/header.jpg?t=1766449377', 'NeoBards Entertainment Ltd.', '2025 年 9 月 24 日', '主人公是居住在群山环绕的日本乡村小镇·戎之丘的高中生·深水雏子。 她的日常生活因为神秘白色“怪物”的出现突然崩塌。 她一边在面目全非的熟悉小镇中探索、战斗、解谜，一边迎来属于她的“结局”。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/93e420a6665b42229735a3495b86bd35b7cb8c53/ss_93e420a6665b42229735a3495b86bd35b7cb8c53.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/fdfc1171e15f271eb16750cd0e5575e5e312a6af/ss_fdfc1171e15f271eb16750cd0e5575e5e312a6af.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/212938a6419ccb6f2d2b6bdfe9c09a2600b7d846/ss_212938a6419ccb6f2d2b6bdfe9c09a2600b7d846.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/596675f3269d51ef397a7db04a08199fe1987ff1/ss_596675f3269d51ef397a7db04a08199fe1987ff1.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/c2a0f8efcb4cc01d3046a5c68675f9c811ee90e4/ss_c2a0f8efcb4cc01d3046a5c68675f9c811ee90e4.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/0052fc9d54c4b4f1588b798a6638556c6b5d4db2/ss_0052fc9d54c4b4f1588b798a6638556c6b5d4db2.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/c044355340e36cfbfa67b7e386b71318a1ff75f1/ss_c044355340e36cfbfa67b7e386b71318a1ff75f1.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/60a9221ab0c30b57921644c0e0419d65e7909755/ss_60a9221ab0c30b57921644c0e0419d65e7909755.1920x1080.jpg?t=1766449377\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/b9add2b5ab7080498ef30b2ebe3da94ce09c9637/ss_b9add2b5ab7080498ef30b2ebe3da94ce09c9637.1920x1080.jpg?t=1766449377\"]', '依恋、纠结、后悔、避无可避的选择，以及自身的罪孽。<br>无法正视这些的人们，将迷失在这个迷雾重重的世界……<br><br>以扭曲的内心景象为主题塑造的怪物和世界观，<br>充满恐惧与疯狂的心理恐怖游戏《寂静岭》系列最新作。<br><br>以日本为舞台的全新“心理恐怖故事”即将上演。<br><br><br>在旧日的昭和时期，位于某处偏远山区的萧条乡镇·戎之丘，住着名为深水雏子的高中生。<br>尽管她的生活稍显单调，也终究与多数平凡的青春期孩子别无二致。<br><br>然而，习以为常的平静却在顷刻间崩塌。<br>熟悉的小镇被浓雾环绕，逐渐变得阴森可怖。<br>镇上的烟火气消失不见，取而代之的是浓雾中蠢蠢欲动的奇怪生物。<br><br>在变得面目全非的小镇中探索、解谜。为了自保，为了生存而战斗。<br><br>为了面对那避无可避的选择。<br>以及，为了将该死之人置于死地……<br><br>她将做出美丽的选择，<br>亦或是令人毛骨悚然的选择？<br><br>本作将为您呈献一个美丽而又令人战栗的抉择。<h2 class=\"bb_tag\" >独立于《SILENT HILL》系列的外传作品</h2>保留《SILENT HILL》系列特有的“心理恐怖”魅力的同时，采用了对初次接触系列作品的玩家也独具吸引力的故事构成。<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/0290a6e0c9a0d46f54336df159a8f283.avif?t=1766449377\" width=718 height=404 /></span><h2 class=\"bb_tag\" >以1960年代昭和时期的日本为舞台</h2>本作的舞台是1960年代日本名为“戎之丘”的小镇。通过与以往作品不同的角色和舞台，编织出全新的故事。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/59d083fd4a3208368941956af95f8888.poster.avif?t=1766449377\" width=640 height=360 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/59d083fd4a3208368941956af95f8888.webm?t=1766449377\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/59d083fd4a3208368941956af95f8888.mp4?t=1766449377\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >由龙骑士07先生打造的迷雾重重的故事</h2>包括主角雏子的朋友们的台词在内，全篇以充满谜团的展开推进故事。眼前发生的现象究竟是现实，还是雏子的妄想，抑或是本土信仰所引发的怪异现象？<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/d984e87c8c029a631bd544cc65c47668.poster.avif?t=1766449377\" width=640 height=360 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/d984e87c8c029a631bd544cc65c47668.webm?t=1766449377\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/d984e87c8c029a631bd544cc65c47668.mp4?t=1766449377\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >美丽与战栗共存的视觉效果，以及融入雅乐的音效</h2>以4K精美画面再现尽管怪诞又诡异，却依然美丽优雅的世界。<br>3D环绕音效，能让玩家清晰感知到暗处和背后的细微响动，加剧不安感与危机感。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/93221cd9aaad487698bcc3e36a51529f.poster.avif?t=1766449377\" width=640 height=360 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/93221cd9aaad487698bcc3e36a51529f.webm?t=1766449377\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/93221cd9aaad487698bcc3e36a51529f.mp4?t=1766449377\" type=\"video/mp4\"></video></span><h2 class=\"bb_tag\" >多结局</h2>一周目的结局固定。但从二周目起，会根据玩家的行动，到达不同的结局和发展。共存在5种结局。通过达成不同结局，揭开故事的全貌吧！<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/01ff2a3c9ecc4cdd61161a7a4b91502b.poster.avif?t=1766449377\" width=640 height=360 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/01ff2a3c9ecc4cdd61161a7a4b91502b.webm?t=1766449377\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2947440/extras/01ff2a3c9ecc4cdd61161a7a4b91502b.mp4?t=1766449377\" type=\"video/mp4\"></video></span>', '动作,冒险', 1, '单人,Steam 成就,完全支持控制器,可选颜色,自定义音量控制,无需应对快速反应事件,立体声,字幕选项,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 西班牙语 - 拉丁美洲, 葡萄牙语 - 巴西, 波兰语, 俄语, 简体中文, 繁体中文, 日语<strong>*</strong>, 韩语<br><strong>*</strong>具有完全音频支持的语言', '© Konami Digital Entertainment', 'https://www.konami.com/games/silenthill/f/', 0, '', '2947440');
INSERT INTO `game_info` VALUES (24, 'Dispatch', 26.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/header.jpg?t=1764278303', 'AdHoc Studio', '2025 年 10 月 22 日', '《Dispatch》是一款超级英雄职场喜剧游戏，其中的故事发展将因你的选择而变化。你将管理一队由不合群的异类英雄所组成的功能失调团队，策略性地派遣不同的英雄到城市中各紧急事故现场处理事故，同时还要平衡办公室政治、人际关系，以及追寻你自己的英雄旅程。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_395c301af733dd84b526d5d6e0e9c4c3446b4a18.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_e821251aca2d2f5a6ac255b788cbf6c02904f379.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_a2fad5dd7e22186afb390cacbb4071d43a52678b.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_6aa03b03ee710fe218e3be4bc54975e4c34d7d8a.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_3a9be55a630dc9c9553eda8a1ec5a91b7d46ae01.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_f0ee4c607d1594f9482525dd266aac0205206c54.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_8a835666501b6569a2f58d8c2583d0e03dcdd828.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_926f6369ee5c76038026d83af2d59e49236aac7b.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_32dbaa5dabe09fbdef5373c229a1a1b76971328e.1920x1080.jpg?t=1764278303\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/ss_63fae45adf446403f79e87bd910a108b5738d21e.1920x1080.jpg?t=1764278303\"]', '<h2 class=\"bb_tag\" >《<strong><i>Dispatch</i></strong>》是一款超级英雄职场喜剧游戏，场景设定在现代的洛杉矶。它的编剧和导演的作品包括《<strong><i>Tales from the Borderlands</i></strong>》、《<strong><i>The Wolf Among Us</i></strong>》。</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/08bd8194caff314b3877e8a53cb01029.poster.avif?t=1764278303\" width=676 height=226 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/08bd8194caff314b3877e8a53cb01029.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/08bd8194caff314b3877e8a53cb01029.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >你将扮演罗伯特·罗伯逊（Robert Robertson），绰号机甲人（Mecha Man）。你的机甲服在一次与死敌的战斗中被摧毁了，不得已接受了超级英雄派遣中心的工作。这工作不是做英雄，而是担任派遣员。你的责任是改造、管理一群前超级壞蛋组成的团队，处理办公室关系，同时重建你的机甲服，为复仇做准备。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/586d83627f93a9724013ba68b1f8b71e.poster.avif?t=1764278303\" width=676 height=52 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/586d83627f93a9724013ba68b1f8b71e.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/586d83627f93a9724013ba68b1f8b71e.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/2f392a8c480bd1ae2342363aa9997653.poster.avif?t=1764278303\" width=800 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/2f392a8c480bd1ae2342363aa9997653.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/2f392a8c480bd1ae2342363aa9997653.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >在《<i>Dispatch</i>》中，你所做的每个决定都会影响故事的发展。从休息室里的玩笑到事故现场的生死攸关的情况，你的选择都会影响你与英雄们的关系及他们的忠诚度，以及你自己的故事的走向。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/dceb7af16b92e4c3117aafbaa9b7bb1e.poster.avif?t=1764278303\" width=676 height=52 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/dceb7af16b92e4c3117aafbaa9b7bb1e.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/dceb7af16b92e4c3117aafbaa9b7bb1e.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/b41e710d4ffd2988cf4b442825468221.poster.avif?t=1764278303\" width=600 height=336 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/b41e710d4ffd2988cf4b442825468221.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/b41e710d4ffd2988cf4b442825468221.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >使用战略地图查看正在發生中的紧急事故，并派遣合适（或不合适）的英雄来处理这些事故。在做战术决定时，要衡量风险与报酬，要知道每个选择都可能对你的团队和城市产生长期的后果。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/99ae1052cfeb656ae47aa0e2dc0dea5a.poster.avif?t=1764278303\" width=676 height=52 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/99ae1052cfeb656ae47aa0e2dc0dea5a.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/99ae1052cfeb656ae47aa0e2dc0dea5a.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/ef321860d8b0c1d8b02cbb91c12527e3.poster.avif?t=1764278303\" width=800 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/ef321860d8b0c1d8b02cbb91c12527e3.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/ef321860d8b0c1d8b02cbb91c12527e3.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >管理英雄有时不仅仅是考量他们的本领。每个英雄都有自己的怪癖、缺陷和包袱，你需要细腻地管理英雄們的差异，才能把团队凝聚在一起。你要提升他们的技能及启发他们的潜力，才能增强他们在紧急事故现场所展现的威力。</p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >《<i>Dispatch</i>》融合了故事、策略和幽默，它探索了英雄的真正意义：你可以是穿着披风的英雄，也可以是坐在办公桌后的英雄。</h2><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/95cbe9a2e0621fa0cf3fc2bbf49d68f7.poster.avif?t=1764278303\" width=676 height=226 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/95cbe9a2e0621fa0cf3fc2bbf49d68f7.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/95cbe9a2e0621fa0cf3fc2bbf49d68f7.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/c98ce3cbd204205ce440b9dc641176b6.poster.avif?t=1764278303\" width=676 height=52 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/c98ce3cbd204205ce440b9dc641176b6.webm?t=1764278303\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/c98ce3cbd204205ce440b9dc641176b6.mp4?t=1764278303\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2592160/extras/9f8915e3019e7b4bcab0dc59873cb9e6.avif?t=1764278303\" width=678 height=682 /></span></p><h2 class=\"bb_tag\" >汇聚了来自娱乐各界的明星阵容</h2><ul class=\"bb_ul\"><li><p class=\"bb_paragraph\" ><strong>Aaron Paul（《Breaking Bad》，《Westworld》，《Black Mirror》）</strong></p></li><li><p class=\"bb_paragraph\" ><strong>Laura Bailey（《The Legend of Vox Machina》，《The Last of Us II》，《Marvel\'s Spider-Man》）</strong></p></li><li><p class=\"bb_paragraph\" ><strong>Erin Yvette（《Hades II》，《The Wolf Among Us》，《Armored Core VI: Fires of Rubicon》）</strong></p></li><li><p class=\"bb_paragraph\" ><strong>MoistCr1TiKaL（Charles White）</strong></p></li><li><p class=\"bb_paragraph\" ><strong>Jacksepticeye（《Sonic Prime》，《River City Girls 1 &amp; 2》，《Bendy and the Ink Machine》）</strong></p></li><li><p class=\"bb_paragraph\" ><strong>Travis Willingham（《The Legend of Vox Machina》，《Critical Role》，《Lego Avengers》）</strong></p></li><li><p class=\"bb_paragraph\" ><strong>Alanah Pearce（《V/H/S Beyond》，《Cyberpunk 2077》，《Gears 5》）</strong></p></li><li><p class=\"bb_paragraph\" ><strong>Lance Cantstopolis</strong> (空手道，舞者，演员）</p></li><li><p class=\"bb_paragraph\" ><strong>Joel Haver</strong> (电影制作人、演员、YouTuber)</p></li><li><p class=\"bb_paragraph\" ><strong>THOT SQUAD</strong> (作曲家：《Pound Cake》，《Hoes Depressed》)</p></li><li><p class=\"bb_paragraph\" ><strong>Yung Gravy</strong>（作曲家：《Betty（Get Money）》，《oops!》）</p></li><li><p class=\"bb_paragraph\" ><strong>Matthew Mercer</strong> (《Critical Role》，《Overwatch》，《Resident Evil 6》)</p></li><li><p class=\"bb_paragraph\" ><strong>Jeffrey Wright</strong> （《American Fiction》，《The Batman》，《Casino Royale》）</p></li></ul><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p>', '动作,冒险,休闲,独立,策略', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,可调整文字大小,可选颜色,自定义音量控制,可调整难度,可以仅用鼠标,无需应对快速反应事件,字幕选项,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 简体中文, 葡萄牙语 - 巴西, 俄语, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '', '', 88, 'https://www.metacritic.com/game/pc/dispatch?ftag=MCD-06-10aaa1f', '2592160');
INSERT INTO `game_info` VALUES (25, 'The Outlast Trials', 11.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/e3b40443eb87d075818ec45883d85c1c47d15c00/header.jpg?t=1765832625', 'Red Barrels', '2024 年 3 月 5 日', 'Red Barrels invites you to experience mind-numbing terror, this time with friends. Whether you go through the trials alone or in teams, if you survive long enough and complete the therapy, Murkoff will happily let you leave… but will you be the same?', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_dcf2a8284d687f9319ac91a02b534dc91971754c.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_6efe75be88bdfe0100f4b67ffe7e1034f1cf0cfe.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_0576920dd310fd71cf63ff3035ff46137634febf.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_dc46aa1b812001788a915f9088148356a697b305.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_da9e1f8fe8ee47b129950570aa1baca12480ae63.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_0a9b163b1458d64c79299353d485160176ca899c.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_aa75eba5255570bb22c96dcb761189f5391d8d57.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_50d3587cb71a649850ff07a146579d5c853f55d1.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_4e190ff9b470b9628251f3c118c4479ed65f11b9.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_72f0d9286b1ecad437c1b68bf57bef6324a4357a.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_6326863cb2d4eb480a1ab07510eada281dc4344a.1920x1080.jpg?t=1765832625\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/ss_e66c85a1a0c999dbb19a153b2cdcc1e431d56e27.1920x1080.jpg?t=1765832625\"]', 'Red Barrels invites you to experience mind-numbing terror, this time with friends. Whether you go through the trials alone or in teams, if you survive long enough and complete the therapy, Murkoff will happily let you leave… but will you be the same?<h2 class=\"bb_tag\" ><strong>SYNOPSIS</strong></h2><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/dde7d2581a50011c158efdf29a880093.avif?t=1765832625\" width=616 height=294 /></span><br>Set in the era of the Cold War, human guinea pigs are involuntarily recruited by the good folks at the Murkoff Corporation to test advanced methods of brainwashing and mind control. In a world of distrust, fear, and violence, your morals will be challenged, your endurance tested, and your sanity crushed. All in the name of progress, science, and profit.<h2 class=\"bb_tag\" ><strong>SURVIVE: SOLO OR COOP</strong></h2><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/a0353ea8b7c187e21e213fe69dff2375.avif?t=1765832625\" width=616 height=294 /></span><br>You’re all prisoners of Murkoff trapped inside their facility. You can choose to complete the trials by yourself or you can tackle them in a team of 2, 3, or 4 players.<br>Coop is never forced when it comes to mandatory objectives but working together can be highly beneficial.<br>Whatever the number of players in your party, it’s about surviving and getting out.<br><br>Ultimately, your goal is to complete Murkoff\'s therapy and earn the right to be released back into society.  In order to do so, you must complete the Trials and the MK-Challenges.  <br>Trials are immersive story driven therapies that take a certain time to complete.  Mk-Challenges are shorter therapies occurring in modified or redesigned section of an existing map.<h2 class=\"bb_tag\" ><strong>HIDE OR FLIGHT</strong></h2><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/ef5dff68ec093aab8becf14243559d2f.avif?t=1765832625\" width=616 height=294 /></span><br>In pure Outlast fashion, the core gameplay involves avoiding enemies, hiding from them, and trying to run away. Murkoff will provide tools you can use to increase your stealth capabilities, create opportunities to flee, slow down enemies, and more. However, you will have to earn those tools and work hard to improve them.<br>The tools will allow you to <br><ul class=\"bb_ul\"><li>Stun enemies<br></li><li>Blind them<br></li><li>See through walls<br></li><li>Heal yourself and people around you.</li></ul>Murkoff will ask you to perform disturbing tasks in each trials or mk-challenges.  Only after completing those, will you be allowed to exit the Trials or the MK-Challenges.<h2 class=\"bb_tag\" ><strong>CHARACTER CUSTOMIZATION</strong></h2><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/3740e1cc100681a7154c9d8c9e685426.avif?t=1765832625\" width=616 height=294 /></span><br>Customize and create your own trial patient.<br>Your cell is your home while you complete the therapy, you can decorate it however you wish.<br>Choose your tools, upgrades, and perks to fit your playstyle. <br>Meet the Denizens of Murkoff’s Sinyala Facility in The Outlast Trials.  Each one of them will try and help you get through the Trials in one piece! Think you’ll make it?<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/fd083cc22f6d176724bd933f35592073.avif?t=1765832625\" width=616 height=294 /></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/ee0aeb2d18495ce0fbbba3732928783f.avif?t=1765832625\" width=616 height=294 /></span><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/c8fbcb779c3eec181ebdd8fcc413595f.avif?t=1765832625\" width=616 height=294 /></span><h2 class=\"bb_tag\" ><strong>INVESTIGATE</strong></h2><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/399c74dc99a66d5ab30177993d18bbe5.avif?t=1765832625\" width=616 height=294 /></span><br>You’ve been abducted by the Murkoff Corporation to undergo unethical experiments that will modify your psyche and behavior throughout the Trials. <br>Collect documents and items that will help prove Murkoff’s malpractice against its patients.<br>The Process:<br><ul class=\"bb_ul\"><li>Break the Self: Murkoff aims to break down the test subject’s fundamental beliefs. Aspects of self-identity are systematically attacked. Like boot camp or indoctrination, the goal is to force a person to purge themselves of anything held sacred or taboo, the things that limit their behavior. The constant assault on identity, guilt and self-betrayal eventually leads to them breaking down.<br></li><li>Build the Self: It’s a bad cop, good cop principle. The rejection of the old ideology leaves a vacuum into which the new ideology can be introduced. As the antithesis of the old ideology, it forms a perfect attraction point as the person flees the old in search of a contrasting replacement.  This progress is accelerated as the new ideology is portrayed as harmonious and ideally suited to the person\'s needs. Collegiality and calm replaces pain and punishment. Just at the point when the person is fearing annihilation of the self, they are offered a small kindness, a brief respite from the assault on their identity.<br></li><li>Salvation: Faced with the stark contrast of the pain of the past with the rosy glow of the future that the new ideology presents, the person sheds any the final allegiance to the old ideology, confessing any remaining deep secrets, and takes on the full mantle of the new ideology.  This often feels, and has been described by many, as a form of rebirth. It may be accompanied by rites of passage as the person is accepted and cemented into the new order. The rituals will typically include strong statements made by the person about accepting the new ideology fully and completely, swearing allegiance to its leaders. Saluting flags, kissing other artefacts and other symbolic acts, all solemnly performed, all anchor them firmly in the new ground.</li></ul><h2 class=\"bb_tag\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1304930/extras/b3a84c20fa59582e954822debc2b3858.webp?t=1765832625\" width=616 height=54 /></span><br><u>Content and Photosensitivity Warning:</u></h2>The Outlast Trials contain strobing and flashing lights that could cause a small percentage of people to experience seizures when exposed to certain lights, patterns, or images.<br>This game also contains intense violence, gore, graphic sexual content, and strong language. Please enjoy.', '动作,冒险,独立', 1, '单人,多人,合作,在线合作,完全支持控制器,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 西班牙语 - 拉丁美洲, 土耳其语, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', 'Outlast, Outlast: Whistleblower, Outlast 2 & The Outlast Trials © 2017 Red Barrels Inc.<br />\r<br>Developed by Red Barrels, Inc.<br />\r<br>Outlast and all characters, their distinctive likenesses, and related elements are trademarks and material protected by copyright of Red Barrels Inc.© 2017. All Rights Reserved.', 'https://redbarrelsgames.com/games/the-outlast-trials/', 0, '', '1304930');
INSERT INTO `game_info` VALUES (26, '黎明杀机', 7.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/header.jpg?t=1766160502', 'Behaviour Interactive Inc.', '2016 年 6 月 14 日', '&quot;永久困于邪恶的幻境之中，即使死亡也无法逃脱，这是一场需要勇气和智慧的较量，四名意志坚定的逃生者在残忍对局中面对嗜血杀手。选择一方，进入多人非对称竞技游戏世界，获得最佳恐怖游戏体验。 &quot;', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_659500624438a4aa77bfdf304cba3ecebcd92ed9.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_ca6b39f2fcac8feb75d23976b1be31290d58d159.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_4075aac79adfe1a5b71665d2cc5ff7d52122650b.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_d3778cc9576bf3457f4ba896a443a114c0455753.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_b142095e4f9e5d9db978270ea09e8b9149db9f18.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_430577c364a68dbe24e8a1d895bd678ea04b87d5.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_969a7841466e12f063c2d9a72520cce1c3b2f331.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_cd57ce3a42d66d90164534ad71388527f1e0cf7b.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_bd49ddb8318bf9d54cb185b57c7fccfe7cb609c0.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_6f14934aaec5f564c8092c85dca236e04935db9d.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_b20e7868e4a80f619fb2e4ef69f7228048a68e99.1920x1080.jpg?t=1766160502\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/ss_42f2866b00cf92cbe84bc8bb32c1fe65911c074c.1920x1080.jpg?t=1766160502\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/extras/e1a939fea29a88b11847b43e7fcb86a6.poster.avif?t=1766160502\" width=618 height=214 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/extras/e1a939fea29a88b11847b43e7fcb86a6.webm?t=1766160502\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/extras/e1a939fea29a88b11847b43e7fcb86a6.mp4?t=1766160502\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >死亡并不是解脱。[/h2]</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >永久困于邪恶的幻境之中，即使死亡也无法逃脱，这是一场需要勇气和智慧的较量，四名意志坚定的逃生者在残忍对局中面对嗜血杀手。选择一方，进入多人非对称竞技游戏世界，获得最佳恐怖游戏体验。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >逃生者们将以第三视角进行游戏，其优势在于对周围环境拥有更开阔的视野。而杀手则更着眼于眼前的猎物，他将以第一视角进行游戏。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >逃生者的目标固然就是在不被杀手抓到的情况下安全地逃离杀戮场——但相信我，这件事做起来真的没有听上去这么容易，特别是周围环境瞬息万变。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >游戏特色</h2><p class=\"bb_paragraph\" >•	<strong>要一起活下去吗…</strong> - 作为逃生者的你既可以和其他人合作，也可以选择单打独斗。当然你活下来的几率会根据是否与他人合作而改变。那么问题来了，你有信心以智慧取胜并且逃离他们的杀戮场吗？</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >•	<strong>我到底在哪？</strong> - 每一个关卡的位置都由程序随机生成，这意味着你永远不知道是什么正在黑暗中等待着你。随机的重生点和环境的改变让你永远不会感觉到一丝的安全。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >•	<strong>一场杀戮的盛宴</strong> - 黎明杀机集合了各种恐怖元素，让地图上每一个角落都散发着恐怖的气息。作为杀手的你可以随心所欲地选择角色，从“形态可憎的杀人魔”到“无法洞悉的超自然实体”。熟悉你的杀戮场，精通每个杀手的特定技能，尽情地捕捉并献祭你的猎物吧。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/extras/86ce185b4e1bac2e800bc48df9784b1a.poster.avif?t=1766160502\" width=616 height=224 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/extras/86ce185b4e1bac2e800bc48df9784b1a.webm?t=1766160502\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/381210/extras/86ce185b4e1bac2e800bc48df9784b1a.mp4?t=1766160502\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >•	<strong>更深层的战术</strong> - 每个杀手和逃生者都拥有一套自己独立的进度体系和大量的可解锁物品，从而能够更深层的制定自己的战术和策略。经验、技能和对周围环境的认知将是你能否精准捕捉杀手动态并智胜他们的关键。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >•	<strong>真实的玩家，真实的恐惧</strong> - 随机生成的关卡和玩家对恐惧的真实反应为每场游戏都带来了意料之外的展开。你永远无法预料到接下来会发生什么。气氛、音乐加上令人不寒而栗的场景，带给你绝对的恐怖体验。如果有足够的时间，你甚至可以发现隐藏在迷雾背后的深层谜团。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><strong>警告：光敏性癫痫发作 - 请在游玩之前仔细阅读</strong></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >極少數人可能會在接觸特定種類的閃光或燈光圖案時發生癲癇性發作或暈厥。這些人，甚至是未曾發生癲癇或癲癇發作的人，可能會在遊玩電玩時發生癲癇症狀或是癲癇發作。如果您或是您的親戚有癲癇病症，或是曾經發生過任何類別的癲癇發作，請在遊玩電玩之前先諮詢您的醫生。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >若您或您的孩童發生任何以下症狀，請「立即停止」遊戲並諮詢醫生：暈眩、視線模糊、眼部或肌肉抽搐、不自主運動、失去意識、失去方向感或痙攣。家長應該注意或詢問孩童是否有以上症狀。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >您可以透過採取以下預防措施來降低光敏感性癲癇發作的風險：坐在離螢幕較遠的地方、使用較小的螢幕、在燈光充足的房間遊玩、不在疲憊或疲勞的狀況下遊玩。</p>', '动作', 1, '多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,Steam 集换式卡牌,Steam 云,在手机上远程畅玩,在平板上远程畅玩,在电视上远程畅玩,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语, 德语, 西班牙语 - 西班牙, 俄语, 简体中文, 葡萄牙语 - 巴西, 泰语, 繁体中文, 日语<strong>*</strong>, 韩语, 波兰语, 西班牙语 - 拉丁美洲, 土耳其语<br><strong>*</strong>具有完全音频支持的语言', '© 2015-2024 and BEHAVIOUR, DEAD BY DAYLIGHT and other related trademarks and logos belong to Behaviour Interactive Inc. All rights reserved.', 'http://www.deadbydaylight.com', 0, '', '381210');
INSERT INTO `game_info` VALUES (27, 'Avatar: Frontiers of Pandora™', 20.09, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/c69c3ae84062706d59daacb0aedc587f7bd76427/header_alt_assets_0_schinese.jpg?t=1766182858', 'Massive Entertainment – A Ubisoft Studio', '2024 年 6 月 17 日', '《阿凡达：潘多拉边境™》是款第一人称动作冒险游戏，背景设在西部边境。找回你失去的传承，从中发现身为纳威人的真正意义，然后联合其他部族，为守护潘多拉而战。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/5d96569712304414f21cddbedbe82d5d2b598bcb/ss_5d96569712304414f21cddbedbe82d5d2b598bcb.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/75b20da8e11285fb3f33f550dafb7d842b70e31f/ss_75b20da8e11285fb3f33f550dafb7d842b70e31f.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/b441eb8a77c901dae889a8605d2266b6defd7851/ss_b441eb8a77c901dae889a8605d2266b6defd7851.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/f75613a079f078740df1d898d55e0aebf8031fb9/ss_f75613a079f078740df1d898d55e0aebf8031fb9.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/3ea537adac955cda608c07dd428e2cd1388cbd70/ss_3ea537adac955cda608c07dd428e2cd1388cbd70.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/894849eb55a6870c0ad3cf6dfe1d8b772abe578c/ss_894849eb55a6870c0ad3cf6dfe1d8b772abe578c.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/36d908bb5f643c5b7e9c450b168bcc021fce9db0/ss_36d908bb5f643c5b7e9c450b168bcc021fce9db0.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/aae462048e7aefe5e05108b3202527dc2a417a1b/ss_aae462048e7aefe5e05108b3202527dc2a417a1b.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/570c150bd2e003e3b623820f1842525fb5abba4c/ss_570c150bd2e003e3b623820f1842525fb5abba4c.1920x1080.jpg?t=1766182858\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/5337d86a544274c8798ba18dd54b901b02ce5d3a/ss_5337d86a544274c8798ba18dd54b901b02ce5d3a.1920x1080.jpg?t=1766182858\"]', '<h2 class=\"bb_tag\" >游玩《浴火重生》扩展内容，奋起反抗灰烬族</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/55ecd3f62d234e615da57f4d8b4491f1.poster.avif?t=1766182858\" width=1170 height=658 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/55ecd3f62d234e615da57f4d8b4491f1.webm?t=1766182858\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >《阿凡达：潘多拉边境™》是款第一人称动作冒险游戏，背景设在西部边境。在遭到 RDA 绑架后，你扮演的纳威人接受了同化，为对方的目的服务。十五年后，你自由了，却发现在自己的出生地成了陌生人。找回你失去的传承，从中发现身为纳威人的真正意义，然后联合其他部族，为守护潘多拉而战。</p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >探索充满奇迹和危险的世界</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/dd8b692747dc3b380f1c1966331b3ab4.poster.avif?t=1766182858\" width=600 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/dd8b692747dc3b380f1c1966331b3ab4.webm?t=1766182858\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >体验西部边境的壮美奇观，领略从未展现在世人面前的潘多拉大陆。在美丽而多变的开放世界区域中畅游，但要当心：在这里放松警惕便有可能招致杀身之祸。</p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >化身纳威人</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/0609738389bc8062b45fdb5537dd805c.poster.avif?t=1766182858\" width=600 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/0609738389bc8062b45fdb5537dd805c.webm?t=1766182858\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >你的角色生来便有无与伦比的力量和敏捷，同时你还可对其尽情自定义：制作新装备，升级技能和武器，打造属于自己的游戏风格。</p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >翱翔天际</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/1f7021c9348fa603ec247ffb94920531.poster.avif?t=1766182858\" width=600 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/1f7021c9348fa603ec247ffb94920531.webm?t=1766182858\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >和自己的魔鬼鸟结成灵伴，一同制霸空战，并且还能通过骑乘来穿越广阔的西部边境。</p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >掌握多种战斗方式</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/a7be18b72e54c4b8ab6843ee7bda291b.poster.avif?t=1766182858\" width=600 height=345 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/a7be18b72e54c4b8ab6843ee7bda291b.webm?t=1766182858\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >玩家可以使用精准的纳威武器（包括弓箭和投矛器），或是利用曾经受过的人类训练，使用更具破坏性的人类武器（如突击步枪和霰弹枪），以此来抵御潘多拉的危险。</p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >在完整的战役合作模式中分享冒险</h2><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/abfcc748278254a1a09be780c900f0d1.poster.avif?t=1766182858\" width=600 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2840770/extras/abfcc748278254a1a09be780c900f0d1.webm?t=1766182858\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >玩家可以选择体验单人模式，或是在双人合作模式下与朋友在线合作，畅玩完整的剧情战役。</p>', '动作,冒险', 1, '单人,多人,合作,在线合作,Steam 成就,支持字幕,应用内购买,部分支持控制器,可用 HDR', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 阿拉伯语, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西<strong>*</strong>, 俄语, 简体中文, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', 'Avatar: Frontiers of Pandora TM & © 2023 20th Century Studios. Game software © 2023 Ubisoft Entertainment. All Rights Reserved.', 'https://www.ubisoft.com/en-us/game/avatar/frontiers-of-pandora', 0, '', '2840770');
INSERT INTO `game_info` VALUES (28, '燕云十六声', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/6d94b048393d5358690a04a7db99f2c9739c703c/header.jpg?t=1765434865', 'Everstone Studio', '2025 年 11 月 14 日', '《燕云十六声》是一款以武侠文化为核心的史诗级开放世界动作冒险角色扮演游戏。 故事背景设定在动荡的十世纪中国，你将化身一名年轻的剑客，揭开被遗忘的真相与自身身世的谜团。 当清风拂过山河之际，你的传奇也将随之展开。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/cb9ab142c8533cd7b1318eaa3847fe9897b4db6b/ss_cb9ab142c8533cd7b1318eaa3847fe9897b4db6b.1920x1080.jpg?t=1765434865\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/c707014226b644e425ade8c1ad8c75a934d79789/ss_c707014226b644e425ade8c1ad8c75a934d79789.1920x1080.jpg?t=1765434865\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/3af2fd466bbd7b8fb1fecf67669f81f23daf2838/ss_3af2fd466bbd7b8fb1fecf67669f81f23daf2838.1920x1080.jpg?t=1765434865\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/ca97521036520d2cb194bf85038fd86211b217fb/ss_ca97521036520d2cb194bf85038fd86211b217fb.1920x1080.jpg?t=1765434865\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/e7a3abbf70cbc0c9abc58c652d249c87ebb14865/ss_e7a3abbf70cbc0c9abc58c652d249c87ebb14865.1920x1080.jpg?t=1765434865\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/07757a74ec60810eed375f65b919f67f25f847f6/ss_07757a74ec60810eed375f65b919f67f25f847f6.1920x1080.jpg?t=1765434865\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/5d22f076a170f8fa6ec9bec319e2eba20b2d1e57/ss_5d22f076a170f8fa6ec9bec319e2eba20b2d1e57.1920x1080.jpg?t=1765434865\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/02f84c49226bcad052fa80d0e22897837fd41ece/ss_02f84c49226bcad052fa80d0e22897837fd41ece.1920x1080.jpg?t=1765434865\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/a6fb0d6471ace90610a4f994c07b0454.poster.avif?t=1765434865\" width=780 height=160 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/a6fb0d6471ace90610a4f994c07b0454.webm?t=1765434865\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/a6fb0d6471ace90610a4f994c07b0454.mp4?t=1765434865\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >《燕云十六声》是一款以武侠文化为核心的史诗级开放世界动作冒险角色扮演游戏。</p><p class=\"bb_paragraph\" >故事背景设定在动荡的十世纪中国，你将化身一名年轻的剑客，揭开被遗忘的真相与自身身世的谜团。</p><p class=\"bb_paragraph\" >当清风拂过山河之际，你的传奇也将随之展开。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/8b567c9795fbb82eccce5ea12aa9c380.poster.avif?t=1765434865\" width=780 height=336 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/8b567c9795fbb82eccce5ea12aa9c380.webm?t=1765434865\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/8b567c9795fbb82eccce5ea12aa9c380.mp4?t=1765434865\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>濒临崩乱的时代，英雄崛起之时</strong></h2><p class=\"bb_paragraph\" >中国五代十国时期，政治阴谋与权力斗争交织，史诗般的战斗铸就了历史的洪流。从繁华帝都到荒野角落，每一条道路都隐藏着秘密与故事，等待你来探索。</p><p class=\"bb_paragraph\" >在这里，你的每个选择都有其分量：是成为引发混乱的使者，面对追捕与入狱；还是结交乡民、建立联盟，成为武侠世界的英雄？在这动荡时代，铸就你的传奇，点燃变革的火</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/157fedf1116d56e63d1e56b7bface205.poster.avif?t=1765434865\" width=780 height=336 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/157fedf1116d56e63d1e56b7bface205.webm?t=1765434865\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/157fedf1116d56e63d1e56b7bface205.mp4?t=1765434865\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>无限可能的开放世界</strong></h2><p class=\"bb_paragraph\" >在这片生机盎然的天地间，从熙攘的开封都城到林深古刹与亭台楼阁，世界随晨昏气象而变幻，你的每一次行动都将影响一切。骑马游历山河：轻功腾跃于连绵檐角，乘风掠过旷野，或借驿站网络瞬息千里。</p><p class=\"bb_paragraph\" >二十余处风土各异的地域等你探索，数千个可交互场景中邂逅武侠世界的百态众生。无论是探寻古城秘辛、闯入禁忌陵墓，还是在柳荫下独奏、万家灯火前畅饮——每一次驻足与远行，都在书写你的武侠传奇。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/4512e65341c3f9af9f299986e0af4a5c.poster.avif?t=1765434865\" width=780 height=336 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/4512e65341c3f9af9f299986e0af4a5c.webm?t=1765434865\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/4512e65341c3f9af9f299986e0af4a5c.mp4?t=1765434865\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>掌握属于你的武侠战斗之道</strong></h2><p class=\"bb_paragraph\" >打造属于你的独门战法——从近身酣战、远程突袭到阴影中隐匿，皆可自由定义。运用经典武侠兵器与策略，开启行云流水的对决：挥舞剑、枪、双刀、绳镖、折扇与伞等兵器，在战斗中灵活切换武器，也可运用金玉手，摄星拿月等奇数，以智取胜。</p><p class=\"bb_paragraph\" >在这纷乱武侠世界中，你既可自定义角色属性与成长路线，也可加入不同门派、探索多样职业。每一次抉择与行动，都将铸就你独一无二英雄传说</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/01175b31de4efa8fdc7c91f82dd3fbd8.poster.avif?t=1765434865\" width=780 height=336 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/01175b31de4efa8fdc7c91f82dd3fbd8.webm?t=1765434865\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/01175b31de4efa8fdc7c91f82dd3fbd8.mp4?t=1765434865\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>独行冒险，或共筑江湖</strong></h2><p class=\"bb_paragraph\" >在这片武学与自由交织的世界中，展开一段丰富的叙事驱动冒险。独行山河，修炼心法、探索秘境，也可以与最多四位伙伴并肩闯荡，体验百余小时的沉浸式冒险。从刀光剑影到命运抉择，每一步都在塑造你的传奇。</p><p class=\"bb_paragraph\" >加入或创建百业公会，参与阵营战争、团队副本，在合作与对抗中证明你的实力。在这片不断变化的世界中，用你的足迹书写新的传奇。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/9f0b97f838f4a585966645b1687875fa.poster.avif?t=1765434865\" width=780 height=336 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/9f0b97f838f4a585966645b1687875fa.webm?t=1765434865\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3564740/extras/9f0b97f838f4a585966645b1687875fa.mp4?t=1765434865\" type=\"video/mp4\"></video></span></p>', '动作,冒险,角色扮演,免费开玩', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,应用内购买', '英语<strong>*</strong>, 日语, 简体中文<strong>*</strong>, 繁体中文<strong>*</strong>, 韩语, 法语, 德语, 西班牙语 - 拉丁美洲, 葡萄牙语 - 巴西<br><strong>*</strong>具有完全音频支持的语言', '© 2025 WHERE WINDS MEET, EVERSTONE STUDIO and other related trademarks and logos belong to NetEase, Inc. All rights reserved. ', 'https://www.wherewindsmeetgame.com/', 0, '', '3564740');
INSERT INTO `game_info` VALUES (29, '七日世界', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/51580da7b3e7aa2f4c44f287296d679a3ddb5544/header_schinese.jpg?t=1765780581', 'Starry Studio', '2024 年 7 月 9 日', '《七日世界》是一款多人在线开放世界生存游戏，游戏发生在一个怪诞的末世背景下。你需要和好友并肩为了生存资源而战，建造你们的家园领地，并一起战胜恐怖的畸变体，揭露世界沦陷的真相。曾经，你是人类中的一分子。现在，你仍是吗？', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/3e2189bc8f677f2ebee62c773af7423ab886293b/ss_3e2189bc8f677f2ebee62c773af7423ab886293b.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_626ba55e262fe857b8f0d2810603d2268e2df003.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_ea488d150e5ce0ab51a33ec27787db1befeb7610.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_001526dade6e9f28c6711932250e3734c45bea01.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_c22678575e3790f85d1ee047022bfef5c3555056.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_78c7278d10652c5369891393782d84b6934176c5.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_ce690aa878d1d6bb8ccb0840b45d86fcb7508ee5.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_bab7e8dff3944d83d08310b47b8facb64a7abca9.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_1ae7debe7f68a4cdde727d984d9e724f65767d66.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_2d7faf83b6942dcf69a4035f71ce4f1662826bc6.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_ea57d1446c92dbbfc273d7c69c01994562793061.1920x1080.jpg?t=1765780581\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/ss_63e6bef4f24438fc84ac9385ca340d307d1c8bcc.1920x1080.jpg?t=1765780581\"]', '<h2 class=\"bb_tag\" ><strong>星球陨落，你是荒野中的求生者</strong></h2><p class=\"bb_paragraph\" >你在一片荒野中醒来，饥渴难耐，周遭的瓜果与流水触手可及，却隐现诡异的蓝光——这些被星尘污染的饮食不仅会损害健康，还会使你的理智逐渐崩溃……更多的危险蛰伏在暗处，等待着你力竭倒地后扑上来将你分食。超越者，别让它们如愿。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/0b4e45fcad2456b08efd6ffd6a95bcda.poster.avif?t=1765780581\" width=600 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/0b4e45fcad2456b08efd6ffd6a95bcda.webm?t=1765780581\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/0b4e45fcad2456b08efd6ffd6a95bcda.mp4?t=1765780581\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>银之门洞开，你是挑战异位面生物的战士</strong></h2><p class=\"bb_paragraph\" >星尘浩劫滋生出难以计量的畸变体，这些恐怖的怪物占据了几乎整个世界。必须从它们手中收复人类的故土！虽然畸变体一直将人类视作猎物，但现在，超越者，这世界有了你…该换我们成为猎人了。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/9fb19adaf01135b5c74d4545182271aa.poster.avif?t=1765780581\" width=600 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/9fb19adaf01135b5c74d4545182271aa.webm?t=1765780581\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/9fb19adaf01135b5c74d4545182271aa.mp4?t=1765780581\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>火力是末日的唯一真理，你是运用枪械的大师</strong></h2><p class=\"bb_paragraph\" >7大枪械分类，超100种枪械蓝图等待你收集打造，每一把武器都能给你带来全新体验。要是现成的武器无法满足你的战斗欲望，你还可以使用配件来自由改造枪械，并组建你的专属枪械天赋，让每一场遭遇战都成为你多变战术的检验场！</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/fb837f5127d3dc85caf546f36a7e4a19.poster.avif?t=1765780581\" width=600 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/fb837f5127d3dc85caf546f36a7e4a19.webm?t=1765780581\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/fb837f5127d3dc85caf546f36a7e4a19.mp4?t=1765780581\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>文明倾颓，你是人类文明的重铸者</strong></h2><p class=\"bb_paragraph\" >在荒野上，任何一个位置，建造你专属的家园吧！你可以建造一座极具生存主义风格的城寨：把一切你收集的好东西都塞到金属墙壁后面，再在外围布满防御设施；或者你也可以在确保安全的前提下，建造一所功能齐全的大型生存点：拥有露台、厨房、车库……等的大房子！</p><p class=\"bb_paragraph\" > </p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/45c03fae8ce89d9cb0e2e487eb85ccc4.poster.avif?t=1765780581\" width=600 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/45c03fae8ce89d9cb0e2e487eb85ccc4.webm?t=1765780581\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/45c03fae8ce89d9cb0e2e487eb85ccc4.mp4?t=1765780581\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" ><strong>往日不再，你是世界沦陷真相的探寻者</strong></h2><p class=\"bb_paragraph\" >在探寻浩劫真相的旅途中，你永远不是孤身一人。荒野上存在着大大小小的幸存者势力，有的充满了暴力和敌意，有些则愿意谨慎地与外来者进行接触。前往幸存者的据点去深入了解他们的故事吧——是在解决威胁当地的畸变体后不留姓名地离去？还是讨价还价获得你应得的报酬？选择权在你手上。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/6a93a33aceaa3696addb891058fae574.poster.avif?t=1765780581\" width=600 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/6a93a33aceaa3696addb891058fae574.webm?t=1765780581\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2139460/extras/6a93a33aceaa3696addb891058fae574.mp4?t=1765780581\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" ></p>', '动作,冒险,角色扮演,模拟,策略', 1, '多人,大型多人在线,玩家对战,线上玩家对战,合作,在线合作,完全支持控制器,Steam 集换式卡牌,应用内购买', '英语<strong>*</strong>, 简体中文<strong>*</strong>, 法语, 德语, 日语, 韩语, 繁体中文, 葡萄牙语 - 巴西, 西班牙语 - 拉丁美洲, 俄语<br><strong>*</strong>具有完全音频支持的语言', '©1997-2022 NetEase, lnc. All Rights Reserved.', 'https://t.co/gRX2BR4tj9', 0, '', '2139460');
INSERT INTO `game_info` VALUES (30, '战地风云™ 6', 48.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/c12d12ce3c7d217398d3fcad77427bfc9d57c570/header.jpg?t=1765583938', 'Battlefield Studios', '2025 年 10 月 10 日', '尽享终极全面战争体验。在坦克、战斗机和大量武器横行的战场上，最致命的武器就是你的小队。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/6b43d1ae44748a7df141e9f485a5e574f8fce5bd/ss_6b43d1ae44748a7df141e9f485a5e574f8fce5bd.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/68914f2848e24b76a25e22029686394aa1ac94d4/ss_68914f2848e24b76a25e22029686394aa1ac94d4.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/ca29b92919b323934f6199b8c3fe8454ea697973/ss_ca29b92919b323934f6199b8c3fe8454ea697973.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/18b0109d89afa9c1b6afce2cabb9429da7ab9968/ss_18b0109d89afa9c1b6afce2cabb9429da7ab9968.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/eb944cda3be9eafb4c989cd54a637842d8adafe6/ss_eb944cda3be9eafb4c989cd54a637842d8adafe6.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/0574a4ae2aa5c5e2863561ba9d179cfc0581b7ba/ss_0574a4ae2aa5c5e2863561ba9d179cfc0581b7ba.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/ba5a595aee41922964bab3203af3e9edad2537c0/ss_ba5a595aee41922964bab3203af3e9edad2537c0.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/d4c4abaf37abcb8f3d8da8b1328fe9911a9ad328/ss_d4c4abaf37abcb8f3d8da8b1328fe9911a9ad328.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/ed9601f6aa237b267c2e320b44eaa8ae9f491814/ss_ed9601f6aa237b267c2e320b44eaa8ae9f491814.1920x1080.jpg?t=1765583938\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/81783b7679d1f36651cf3987721c16a3b975337d/ss_81783b7679d1f36651cf3987721c16a3b975337d.1920x1080.jpg?t=1765583938\"]', '尽享终极全面战争体验。投入高强度步兵作战。在近距离空战中划破天空。摧毁周围环境，赢得战略优势。使用动觉战斗系统完全控制每一个动作和行动。在坦克、战斗机和大量武器横行的战场上，最致命的武器就是你的小队。这就是《战地风云 6》。<br><br><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/extras/ac2247af6b7c6003eb5d04f5535c25d4.poster.avif?t=1765583938\" width=610 height=1338 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/extras/ac2247af6b7c6003eb5d04f5535c25d4.webm?t=1765583938\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/extras/ac2247af6b7c6003eb5d04f5535c25d4.mp4?t=1765583938\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/extras/b3d6c0f2ea6a2451caa905ced40e0647.poster.avif?t=1765583938\" width=610 height=1380 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/extras/b3d6c0f2ea6a2451caa905ced40e0647.webm?t=1765583938\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2807960/extras/b3d6c0f2ea6a2451caa905ced40e0647.mp4?t=1765583938\" type=\"video/mp4\"></video></span></p><br>这款游戏包括自愿的游戏内虚拟货币购买，虚拟货币可用于获取虚拟游戏内物品。', '动作', 1, '单人,多人,玩家对战,线上玩家对战,跨平台多人,Steam 成就,完全支持控制器,应用内购买,视角舒适度,聊天语音转文字,聊天文字转语音,可选颜色,自定义音量控制,可调整难度,可以仅用键盘,无需应对快速反应事件,立体声,字幕选项,环绕声,Steam 云,可用 HDR', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 阿拉伯语, 日语<strong>*</strong>, 韩语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 简体中文<strong>*</strong>, 西班牙语 - 拉丁美洲<strong>*</strong>, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '需要拥有互联网连接、EA账户、Steam 账户并接受 EA 用户协议 (terms.ea.com/sc)，才能进行游戏。使用 EA 服务时适用 EA 保密和Cookie协议。您同意将在您使用 EA 服务时收集到的个人数据传输至美国，如保密和Cookie协议中进行的深入阐述。您必须将EA账户与 Steam 账户绑定，才能进行游戏；EA 将与 Steam 共享您的账户 ID 和个人游戏记录，以验证您的购买和/或退款申请。仅限使用一个EA账户和一个 Steam 账户访问软件内容，购买后不可转让。您可能需要年满 13 或 16 周岁，才能注册EA账户（年龄限制可能会有所不同，详情请参阅 o.ea.com/ea/child-access）。某些内容可能需要进行游戏才能解锁。可能会自动下载内容更新，这需要额外储存空间并会产生宽带使用费用。EA 可能会适时免费提供特定增量内容和/或更新。EA 可在于 www.ea.com/service-updates 发布公告 30 日后停止在线功能。<br />\r<br><br />\r<br>EA 用户协议：德国居民请访问 terms.ea.com/de；所有其他地区居民请访问 terms.ea.com<br />\r<br>EA 保密和Cookie协议：德国居民请访问 privacy.ea.com/de；所有其他地区居民请访问 privacy.ea.com<br />\r<br><br />\r<br>*年初至今，截至 2025 年 11 月 15 日的汇总数据。<br />\r<br><br />\r<br>©2025 Electronic Arts Inc.《战地风云》是 Electronic Arts Inc. 的商标。<br />\r<br>任何武器、车辆或装备制造商都与本游戏无关联，也未对本游戏提供赞助或支持。', 'https://www.ea.com/games/battlefield/battlefield-6', 82, 'https://www.metacritic.com/game/pc/battlefield-6?ftag=MCD-06-10aaa1f', '2807960');
INSERT INTO `game_info` VALUES (31, '艾尔登法环', 38.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/header.jpg?t=1748630546', 'FromSoftware, Inc.', '2022 年 2 月 24 日', '艾尔登法环是以正统黑暗奇幻世界为舞台的动作RPG游戏。 走进辽阔的场景与地下迷宫探索未知，挑战困难重重的险境，享受克服困境时的成就感吧。 不仅如此，登场角色之间的利害关系谱成的群像剧，更是不容错过。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_943bf6fe62352757d9070c1d33e50b92fe8539f1.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_dcdac9e4b26ac0ee5248bfd2967d764fd00cdb42.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_3c41384a24d86dddd58a8f61db77f9dc0bfda8b5.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_e0316c76f8197405c1312d072b84331dd735d60b.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_ef61b771ee6b269b1f0cb484233e07a0bfb5f81b.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_b1b91299d7e4b94201ac840aa64de54d9f5cb7f3.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_510a02cf3045e841e180f2b77fb87545e0c8b59d.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_c494372930ca791bdc6221eca134f2270fb2cb9f.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_fa6b881ef7c30522012ab2b2b83001e79baee093.1920x1080.jpg?t=1748630546\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/ss_c2baf8aada6140beee79d701d14043899e91af47.1920x1080.jpg?t=1748630546\"]', '<span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/extras/7055e75b2d0bfcc44f2514e9f1280021.poster.avif?t=1748630546\" width=616 height=260 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/extras/7055e75b2d0bfcc44f2514e9f1280021.webm?t=1748630546\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1245620/extras/7055e75b2d0bfcc44f2514e9f1280021.mp4?t=1748630546\" type=\"video/mp4\"></video></span><br>・充满刺激的辽阔世界<br>无缝连接的辽阔世界──尽情探索状况多变的开放场景，构造复杂、立体的巨大地下迷宫。<br>在探索新发现时感到欣喜，在征服强敌时感到成就感十足，丰富内容等你体会。<br><br>・专属于你的角色<br>不只能自订操纵角色的外表，更能自行搭配武器、装备，与魔法──<br>想成为力量取胜的顽强战士，或选择在魔法领域登峰造极，都能照自己的游玩风格决定。<br><br>・源自神话的群像剧<br>片段破碎，却又多彩的故事──以“交界地”为舞台登场的角色们各有主张，<br>于是在利害关系之下谱成的群像剧，就此展开。<br><br>・与其他玩家维持非即时连系的独特系统<br>除了能与其他玩家一同游玩的多人联机游玩以外，<br>本游戏也有非即时联机系统，能借此得知其他玩家的存在。', '动作,角色扮演', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,Steam 成就,完全支持控制器,Steam 集换式卡牌,视角舒适度,自定义音量控制,无需应对快速反应事件,随时保存,立体声,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 西班牙语 - 拉丁美洲, 泰语, 繁体中文, 阿拉伯语<br><strong>*</strong>具有完全音频支持的语言', 'ELDEN RING™ & ©BANDAI NAMCO Entertainment Inc. / ©2022 FromSoftware, Inc.', '', 94, 'https://www.metacritic.com/game/pc/elden-ring?ftag=MCD-06-10aaa1f', '1245620');
INSERT INTO `game_info` VALUES (32, 'The Outer Worlds 2', 48.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/fe273e39cbaf4997d1c8e53879800f07dba0ee03/header.jpg?t=1761760147', 'Obsidian Entertainment', '2025 年 10 月 29 日', '《The Outer Worlds 2》是屡获奖项的第一人称科幻RPG《天外世界》的续作，由Obsidian Entertainment打造。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/aed9141f12c197a06fb1d6a68b06ac9251f80370/ss_aed9141f12c197a06fb1d6a68b06ac9251f80370.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/28c22a02a2c6341d94372826b24e163e59d4154c/ss_28c22a02a2c6341d94372826b24e163e59d4154c.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/ab26c9f54d15037f18b4dd39f9c12b790d5bfb2f/ss_ab26c9f54d15037f18b4dd39f9c12b790d5bfb2f.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/08a867275ba289445985a9249d519f264284735b/ss_08a867275ba289445985a9249d519f264284735b.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/fb0e7634f9cf831965d80a19746c4d53ecb5beea/ss_fb0e7634f9cf831965d80a19746c4d53ecb5beea.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/ea30bd993c3ba23d01d46fa57cdb2d16039ca71e/ss_ea30bd993c3ba23d01d46fa57cdb2d16039ca71e.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/da4b70de38dba072ece5eb8abe22e45044210ab0/ss_da4b70de38dba072ece5eb8abe22e45044210ab0.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/6a0b9078c00fe0259a0458d47800970157c04bff/ss_6a0b9078c00fe0259a0458d47800970157c04bff.1920x1080.jpg?t=1761760147\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/f875b050dee09ec4790e9dc87be2f75edce84156/ss_f875b050dee09ec4790e9dc87be2f75edce84156.1920x1080.jpg?t=1761760147\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/0455d33cebdfc665eb10cdbf68af8a04.poster.avif?t=1761760147\" width=616 height=346 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/0455d33cebdfc665eb10cdbf68af8a04.webm?t=1761760147\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/0455d33cebdfc665eb10cdbf68af8a04.mp4?t=1761760147\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >玩家们翘首以盼的《The Outer Worlds 2》由Obsidian Entertainment打造，是屡获奖项的第一人称科幻RPG《天外世界》的续作（有生之年了属于是！）。腾出时间，准备好踏上紧张刺激的新冒险之旅，你将在一处新殖民地里结识新船员，找到新武器并遭遇新敌人！好多新内容啊！</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/3c28af2210fbe1185c96a65e206c4d1b.poster.avif?t=1761760147\" width=616 height=346 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/3c28af2210fbe1185c96a65e206c4d1b.webm?t=1761760147\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/3c28af2210fbe1185c96a65e206c4d1b.mp4?t=1761760147\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >可怕的裂隙大有毁灭全人类的势头，作为一名敢于冒险且颜值在线（应该吧）的地球理事会干员，你必须找出裂隙的源头。调查过程中你将前往跃进式驱动器技术的诞生地阿卡迪亚。在那里，你的长处、你的缺点、你的船员、你选择信任的派系以及你的每个决定都将影响这个殖民地，乃至整个银河系的命运。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >探索新边界</p><p class=\"bb_paragraph\" >阿卡迪亚殖民地饱受派系战争之苦，保护国的所谓德政遭受着其宗教团体的叛乱以及企业入侵的挑战。随着毁灭性的裂隙在殖民地中蔓延，各个派系都各怀目的地争夺裂隙的控制权或关闭权。在各种各样的区域中漫游，发现隐藏的背景故事，并塑造这个岌岌可危的恒星系的命运！</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/152325753e23924431ca055ff805ae0c.poster.avif?t=1761760147\" width=614 height=346 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/152325753e23924431ca055ff805ae0c.webm?t=1761760147\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/152325753e23924431ca055ff805ae0c.mp4?t=1761760147\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >你的指挥官你做主</p><p class=\"bb_paragraph\" >按照你的游玩风格，通过习得能力和做出选择来打造你的角色。殖民地会对你的每个决定做出反馈。无论你是处世圆融的沟通者、城府极深的老狐狸、带来混乱的改革者还是完全不一样的角色，你都可以创造出属于自己的叙事风格。啊对，你还可以当个智障！</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/e97cc0b282fa8f6cfe5b406156f2194d.poster.avif?t=1761760147\" width=616 height=346 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/e97cc0b282fa8f6cfe5b406156f2194d.webm?t=1761760147\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/e97cc0b282fa8f6cfe5b406156f2194d.mp4?t=1761760147\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >征募你的同伴</p><p class=\"bb_paragraph\" >招募拥有独特特性、背景和目标的同伴。你可以帮助他们实现自己的抱负，也可以引导他们达成你的目的。你的影响力能塑造他们的成长（也可能害他们丧命），让其变成你们共同谱写的沉浸式故事中不可或缺的部分。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/8976f8f80d5dc1a5e6e2f6ab4b897770.poster.avif?t=1761760147\" width=616 height=346 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/8976f8f80d5dc1a5e6e2f6ab4b897770.webm?t=1761760147\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1449110/extras/8976f8f80d5dc1a5e6e2f6ab4b897770.mp4?t=1761760147\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >高保真画面</p><p class=\"bb_paragraph\" >探索画面无比精细的阿卡迪亚。《天外世界2》在兼容的设备上支持真4K分辨率，在PC上支持无限制帧率，无论是广阔的外星地貌、霓虹闪烁的都市还是破碎的太空环境，都能带来流畅的游戏体验。</p><p class=\"bb_paragraph\" > </p><p class=\"bb_paragraph\" >光线追踪</p><p class=\"bb_paragraph\" >光线追踪为阿卡迪亚增添深度和真实感。无论是发光的裂隙异象，还是霓虹指示牌在潮湿路面上的倒影，先进的光照、阴影和反射技术将营造出更加动态的沉浸式体验。</p><p class=\"bb_paragraph\" >支持DLSS、FSR和XeSS</p><p class=\"bb_paragraph\" >畅享全方位优化的游戏体验。《天外世界2》支持NVIDIA DLSS、AMD FSR和Intel XeSS画质提升技术，无论你使用何种显卡，都能确保画面清晰且帧率更高。 </p><p class=\"bb_paragraph\" >HDR</p><p class=\"bb_paragraph\" >在高动态范围的支持下畅游阿卡迪亚。在兼容的显示器上，HDR能强化对比度和色深，让霓虹灯光、外星天空和武器火光更加鲜活逼真。</p><p class=\"bb_paragraph\" > </p><p class=\"bb_paragraph\" >超宽屏支持</p><p class=\"bb_paragraph\" >本游戏支持21:9超宽屏，让你将阿卡迪亚的风光尽收眼底。从巨大的企业设施到无垠的太空，更广阔的视野将以电影级观感呈现殖民地破碎的未来。</p><p class=\"bb_paragraph\" ></p>', '动作,冒险,角色扮演', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,自定义音量控制,可调整难度,随时保存,立体声,字幕选项,环绕声,Steam 云,统计数据,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 西班牙语 - 拉丁美洲<br><strong>*</strong>具有完全音频支持的语言', '', '', 0, '', '1449110');
INSERT INTO `game_info` VALUES (33, 'Monster Hunter Wilds', 38.49, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/7ad42e4bfbbffc4d554c2116efe53b4d7f0c0985/header_alt_assets_5.jpg?t=1766102696', 'CAPCOM Co., Ltd.', '2025 年 2 月 27 日', '狂野凶猛的大自然，袭来。 时刻都在动态变化的原野。 这是个关于生活在具有两面性的世界中的怪物与人们的故事。 进化的狩猎动作，寻求连续不断的沉浸感，究极的狩猎体验正等待你的到来。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_31b5597fecf2d9a2904bc9bbf8011aacb18143db.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_9245c1ede8c3fc2cd65e4890a74a01ef50b726da.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_c722e74607ffda9a1472093ccc934f1bdde10ab8.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_d439e321350c004c4d866c248019aefdc6a50600.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_fa2361b39da8e8bde3cbc282879de97f2f3e57af.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_c1dd8c8af4e0fd3bb233888b4155d94f7988debd.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_c1c31d5252307bd9e331f21509c8fd8dc39c2d61.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_5b3101fafdfb638384a5cc0024a9801fe6af06b6.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_e78e739e1cd881de7a7c4cbec0b3ada2df155678.1920x1080.jpg?t=1766102696\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2246340/ss_d17898c5f18d4e081fe185d28fd0eb8155fbf529.1920x1080.jpg?t=1766102696\"]', '狂野凶猛的大自然，袭来。<br />\r<br><br />\r<br>时刻都在动态变化的原野。<br />\r<br>这是个关于生活在具有两面性的世界中的怪物与人们的故事。<br />\r<br><br />\r<br>你会化为以狩猎强大怪物为生的“猎人”，使用狩猎获得的素材打造更强的武器防具，并逐渐解明这个世界与人们之间的关联。<br />\r<br><br />\r<br>进化的狩猎动作，寻求连续不断的沉浸感，究极的狩猎体验正等待你的到来。<br />\r<br><br />\r<br>故事<br />\r<br>数年前，在公会还没有调查过的未踏足领域“封禁之地”的边境上，一名少年“纳塔”获救。<br />\r<br>公会根据从神秘怪物袭击中独自逃脱的他所提供的线索，组成调查队。<br />\r<br>为了调查名为“白色孤影”的怪物并营救遭受袭击的守护者一族，由猎人们组成的“调查队”之旅程就此展开。<br />\r<br><br />\r<br>活着的世界<br />\r<br>原野具有两面性，经历饥肠辘辘的肉食性怪物结群斗争的残酷“荒芜期”与“异常气象”之后，会迎来满溢富饶生机的“丰饶期”，哪怕身处同一个地点，也会在不同的时候展现截然不同的面貌。<br />\r<br><br />\r<br>怪物<br />\r<br>登场的怪物均极具特色。它们会活用各自的特征适应环境。<br />\r<br>若发生异常气象，控制该生态系统顶点的强大怪物也会现身。<br />\r<br><br />\r<br>狩猎<br />\r<br>丰富多彩的武器与装备，以及新的动作。需看透怪物行动与环境的狩猎实现了新的进化。<br />\r<br>新动作集中模式可以瞄准要攻击或防御的目标。<br />\r<br>通过瞄准怪物的要害，还可转变为特殊衍生并造成大量伤害。<br />\r<br><br />\r<br>登场人物<br />\r<br>可靠的搭档与调查队的伙伴等等，<br />\r<br>为猎人狩猎生活提供帮助的多位角色也会登场。', '动作,冒险,角色扮演', 1, '单人,多人,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,Steam 集换式卡牌,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,可以仅用键盘,立体声,字幕选项,环绕声,Steam 云,可用 HDR,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 阿拉伯语, 西班牙语 - 拉丁美洲, 葡萄牙语 - 巴西<strong>*</strong>, 波兰语, 俄语<strong>*</strong>, 简体中文<strong>*</strong>, 繁体中文, 日语<strong>*</strong>, 韩语<br><strong>*</strong>具有完全音频支持的语言', '©CAPCOM<br />\r<br>MONSTER HUNTER WILDS is a trademark and/or registered trademark of CAPCOM CO., LTD. and/or its subsidiaries in the U.S. and/or other countries.', 'https://www.monsterhunter.com/wilds/', 0, '', '2246340');
INSERT INTO `game_info` VALUES (34, '《猎人：荒野的召唤™》', 1.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/0a901dae9ccc4b881d7dc6247593e7bb0adc1073/header_alt_assets_24_schinese.jpg?t=1765292276', 'Expansive Worlds', '2017 年 2 月 16 日', '在这个风光旖旎又栩栩如生的开放世界中，体验与众不同、身临其境的狩猎游戏——我们与游戏社区携手合作，定期更新。可选择投身于单人狩猎模式，或与好友分享终极狩猎体验。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_1d4862325cbb1b929af51d5bd3c91160797565d1.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_318b270747b228975c4a6e3e06d9133db6a72f1b.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_dce419f29e299c1966b361ff24f6b2d6e1130e82.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_6a42297e92da375520a1cadc9e369ca3429bc280.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_8a75aa823fbaa3a8ec3cf844d1340308250737a4.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_9e1405661f3a1e697ae7d5a8017fa4141cb6fe4f.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_4aecf6ed4e1cbacb97800650f928d80f364487bc.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_dbdc601a21f16bde11cbcbad1e4bf2bfdf6f5eac.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_8b37275966bdc78f2779141b9f418779cf07d6e3.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_63de30126624f031b7d81a841d5cd33d18d67aa0.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_c36c49958679fdf7fa590b78780ae52107def60d.1920x1080.jpg?t=1765292276\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/ss_b7dd2e7d70a1e84a5b845b646952ed231032db1c.1920x1080.jpg?t=1765292276\"]', '在这个风光旖旎又栩栩如生的开放世界中，体验与众不同、身临其境的狩猎快感——我们与游戏社区携手合作，定期更新。可选择投身于单人狩猎模式，或与好友分享终极狩猎体验。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/33cc3c9553cf5f8a0afbd8fbec7c4de9.poster.avif?t=1765292276\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/33cc3c9553cf5f8a0afbd8fbec7c4de9.webm?t=1765292276\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/33cc3c9553cf5f8a0afbd8fbec7c4de9.mp4?t=1765292276\" type=\"video/mp4\"></video></span><br><strong>在开放世界中体验身临其境的狩猎快感</strong><br><br>自由徜徉于精心制作的游戏环境中，探索不同地区和生物群落中的独特动植物。体验复杂的动物行为、变化无常的天气、日夜更迭交替、模拟弹道效果、栩栩如生的叫声及随风飘来的猎物气息。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/9367e42a8f5822bbb0fbfab8625bc5ec.poster.avif?t=1765292276\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/9367e42a8f5822bbb0fbfab8625bc5ec.webm?t=1765292276\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/9367e42a8f5822bbb0fbfab8625bc5ec.mp4?t=1765292276\" type=\"video/mp4\"></video></span><br><strong>狩猎体验，由你做主</strong><br><br>自主选择各种武器、弹药和装备，创造终极狩猎体验。游戏中有白尾兔、绿头鸭、黑熊、麋鹿和驼鹿等各种各样的野生动物，你需要制定策略，选择合适的武器，根据各种动物的独特行为和所处环境追踪、引诱和伏击它们。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/219e48da00d82591cdb2cb85676310b6.poster.avif?t=1765292276\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/219e48da00d82591cdb2cb85676310b6.webm?t=1765292276\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/219e48da00d82591cdb2cb85676310b6.mp4?t=1765292276\" type=\"video/mp4\"></video></span><br><strong>通过玩家合作不断升级</strong><br><br>theHunter: Call of the Wild与社区紧密合作，提供丰富的付费和免费内容，包括保护区、狩猎装备、武器包和战利品小屋。随着新内容的定期更新，玩家可收获不断升级的游戏体验。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/66c857577882eeb7416bbee29a314361.poster.avif?t=1765292276\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/66c857577882eeb7416bbee29a314361.webm?t=1765292276\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/66c857577882eeb7416bbee29a314361.mp4?t=1765292276\" type=\"video/mp4\"></video></span><br><strong>战利品荣耀</strong><br><br>每次狩猎都蕴含是一个独一无二的故事。在宽敞的战利品小屋中展示最珍贵的收获，或者在壁炉边放松，计划下一次狩猎之旅，让自己在莱顿湖和赫希费尔登保护区的胜利故事永远流传。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/6a2666db5ee3b6109808f23a480d9e5d.poster.avif?t=1765292276\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/6a2666db5ee3b6109808f23a480d9e5d.webm?t=1765292276\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/518790/extras/6a2666db5ee3b6109808f23a480d9e5d.mp4?t=1765292276\" type=\"video/mp4\"></video></span><br><strong>合作无间</strong><br><br>在精彩纷呈的狩猎区中体验最多8人的在线多人游戏。与好友通力合作制定狩猎策略，或奋力拼搏竞争，一举拿下最令人印象深刻的战利品。只要你的狩猎队伍中有一位成员拥有任一付费版DLC保护区权限，你就可以进入该保护区中游戏。', '冒险,模拟,体育', 1, '单人,多人,合作,在线合作,Steam 成就,完全支持控制器,应用内购买,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 德语, 葡萄牙语 - 巴西, 俄语, 西班牙语 - 西班牙, 捷克语, 波兰语, 简体中文, 日语<br><strong>*</strong>具有完全音频支持的语言', '猎人：荒野的召唤 © 2020 Avalanche Studios Group AB。版权所有。本游戏由 Avalanche Studios Group AB 发行，Expansive Worlds（Avalanche Studios Group 旗下）开发。Expansive Worlds 与《猎人：荒野的召唤》 (theHunter: Call of the Wild) 及其对应标识均为 Avalanche Studios Group AB 在瑞典、美国及其他国家/地区的注册商标或商标。本游戏采用 Apex - Avalanche 开放世界引擎。Apex - Avalanche 开放世界引擎及其标识系 Fatalist Development AB（Avalanche Studios Group 旗下公司）在瑞典、美国及其他国家/地区的注册商标或商标。保留所有权利。', 'http://callofthewild.thehunter.com/', 0, '', '518790');
INSERT INTO `game_info` VALUES (35, 'Warframe 星际战甲', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/64cb9deaae9055003fd76ba0f6df6b6fba38e808/header.jpg?t=1765508208', 'Digital Extremes', '2013 年 3 月 25 日', '在这个以故事为导向的免费在线动作游戏中，觉醒为一个不可阻挡的战士，并与你的朋友一起战斗。跟随着神秘的 Lotus 的指引，在广阔的星系中直面敌对的派系，提升你的战甲等级，建立一个具有毁灭性火力的军械库；并在这个惊心动魄，且拥有独特第三人称战斗体验的庞大开放世界中发挥你的真正潜力。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/ss_2e4077f215eccde84171a4b8e0f2bc8a3264c776.1920x1080.jpg?t=1765508208\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/ss_0a541a8bf59e212870ea8d82260ac1b3ae2d0354.1920x1080.jpg?t=1765508208\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/ss_ce00a212a29e9a6c1fc37b16dbd802b2844a901d.1920x1080.jpg?t=1765508208\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/ss_29355e0546599c72002b34b42fe952329df61c2e.1920x1080.jpg?t=1765508208\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/ss_e1d1bfebe225c85ea284ffecfe279c2695ddf3a5.1920x1080.jpg?t=1765508208\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/ss_b06620c448c51d2bc5b71144a184da9ba37b703e.1920x1080.jpg?t=1765508208\"]', '在这个以故事为导向的免费在线动作游戏中，觉醒为一个不可阻挡的战士，并与你的朋友一起战斗。跟随着神秘的 Lotus 的指引，在广阔的星系中直面敌对的派系，提升你的战甲等级，建立一个具有毁灭性火力的军械库；并在这个惊心动魄，且拥有独特第三人称战斗体验的庞大开放世界中发挥你的真正潜力。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/ce3ea991a5c4960aac40fca31ecca140.poster.avif?t=1765508208\" width=600 height=172 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/ce3ea991a5c4960aac40fca31ecca140.webm?t=1765508208\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/ce3ea991a5c4960aac40fca31ecca140.mp4?t=1765508208\" type=\"video/mp4\"></video></span><br><br><strong>成为一名强大的战士</strong><br>进入你的战甲——一个拥有无尽力量的生化金属套装。释放它的技能并使用众多的毁灭性武器，毫不费力地歼灭成群的敌人。杀戮结束后，你可以获得或立即解锁 40 多种不同的战甲——每款战甲都有一套独特的技能——供你以任何你想要的方式重新体验投入战斗。<br><br><strong>与朋友并肩作战</strong><br>与朋友组成一个小队，当你通过高度合作的游戏玩法完成任务时，将会获得宝贵的奖励。利用你的战甲技能治疗盟友，偏转敌人火力方向，并完成你的任务目标。被一个特定的挑战卡住了？游戏中的匹配机制可以让你在需要帮手的时候很容易地与友好的 Tenno 进行联系。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/4214d6d6c8f0a68159b7e5b4d65f5d78.poster.avif?t=1765508208\" width=600 height=126 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/4214d6d6c8f0a68159b7e5b4d65f5d78.webm?t=1765508208\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/4214d6d6c8f0a68159b7e5b4d65f5d78.mp4?t=1765508208\" type=\"video/mp4\"></video></span><br><br><strong>探索庞大的星系</strong><br>利用你战甲令人着迷的跑酷技能来灵巧地完成地面任务，或进入太空，使用你自己专属的战舰参与大规模的舰对舰战斗。在神秘的开放世界景观中迷失自己，发现一个充斥着迷人生命体的星系——无论是友好的还是敌对的。<br><br><strong>探索一个史诗级故事</strong><br>通过《Warframe》错综复杂的电影式故事情节，5 个独立的扩展内容和 30 多个剧情系列任务任务，惊叹于始源星系的悠久历史。在开始你的旅程、发展你的技能和寻求你觉醒背后的真相之前，请先通过 3 款原始战甲之一发现你的内在力量，并开始你在《Warframe》之中的初体验。<br><br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/f0014fea1ffda57eb2310c31ae80ef8e.poster.avif?t=1765508208\" width=600 height=126 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/f0014fea1ffda57eb2310c31ae80ef8e.webm?t=1765508208\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/230410/extras/f0014fea1ffda57eb2310c31ae80ef8e.mp4?t=1765508208\" type=\"video/mp4\"></video></span><br><br><strong>增强你的军械库</strong><br>你的初始武器只是踏上旅程的一个起点。制作数以百计的破坏性武器，此外还有载具、同伴和更多精彩内容。提升它们的等级并进行试验，直到你找到适合你独特游戏风格的装备组合。为你的武器设计一款可怕的外观，以此来呈现出你自定义装备的时尚风格。<br><br><strong>无限自定义</strong><br>进入始源星系意味着加入友好的 Tenno 军团，每个人都有自己的定制战甲、武器和装备。由于有大量的自定义选项可用于强化你的装备，因此为你的战甲设计完美的外观，对你和你的小队来说将会是一个无尽的挑战。', '动作,角色扮演,免费开玩', 1, '单人,多人,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,Steam 集换式卡牌,Steam 创意工坊,应用内购买,视角舒适度,可选颜色,自定义音量控制,无需应对快速反应事件,立体声,环绕声,在手机上远程畅玩,在平板上远程畅玩,在电视上远程畅玩,可用 HDR', '英语<strong>*</strong>, 德语, 法语, 意大利语, 韩语, 西班牙语 - 西班牙, 简体中文, 俄语, 日语, 波兰语, 葡萄牙语 - 巴西, 繁体中文, 土耳其语, 乌克兰语, 泰语<br><strong>*</strong>具有完全音频支持的语言', 'Warframe and the Warframe logo are registered trademarks of Digital Extremes Ltd.', 'http://www.warframe.com', 69, 'https://www.metacritic.com/game/pc/warframe?ftag=MCD-06-10aaa1f', '230410');
INSERT INTO `game_info` VALUES (36, 'Steam Deck', 399.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1675200/header.jpg?t=1763066602', 'Valve', '2025 年 1 月 17 日', '您的游戏，随身携带 功能强大、便于携带、操控舒适，体验堪比主机的 PC 游戏设备', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1675200/ss_5535edca3f6c9844d301de3a88ed16a4575376ac.1920x1080.jpg?t=1763066602\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1675200/ss_585b19a7e2eb7e2784c6148fe44782acab73229c.1920x1080.jpg?t=1763066602\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1675200/ss_6d25000e7a7badd44ec9f38e6264c7f400db649c.1920x1080.jpg?t=1763066602\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1675200/ss_fa9f7868b7ae1b0b6fc98876bd03c1bfd5a73030.1920x1080.jpg?t=1763066602\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1675200/ss_9452b8b23fc9f3fa95165ea4535b75cae585886e.1920x1080.jpg?t=1763066602\"]', '', '', 1, '', '', '', 'https://www.steamdeck.com/', 0, '', '1675200');
INSERT INTO `game_info` VALUES (37, 'Hollow Knight: Silksong', 15.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/7983574d464e6559ac7e24275727f73a8bcca1f3/header.jpg?t=1764916587', 'Team Cherry', '2025 年 9 月 4 日', '探索《空洞骑士：丝之歌》中庞大的诅咒王国！在这个由丝线与歌声统治的地下世界中，攀登顶峰的旅途，亦是探索、战斗与生存的考验。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/26950369fe4b03c2268620eb9815c8a246aa0b06/ss_26950369fe4b03c2268620eb9815c8a246aa0b06.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/09ccaa6c16f158f9df8298feb5d196098506a028/ss_09ccaa6c16f158f9df8298feb5d196098506a028.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/d1a893ec6357b347a55ed929833ba793b57a79d2/ss_d1a893ec6357b347a55ed929833ba793b57a79d2.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/856e33e755a0b9a785c645d116036516ea08812b/ss_856e33e755a0b9a785c645d116036516ea08812b.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/8e09f2b2eedd3fa9b4479dd5c26d8bdf60562478/ss_8e09f2b2eedd3fa9b4479dd5c26d8bdf60562478.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/d907d0cc2b10b5ea4788b8d502cc27787d520c1d/ss_d907d0cc2b10b5ea4788b8d502cc27787d520c1d.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/1b93e8131cb6f4bd9e3791a606d0da8f9ee78276/ss_1b93e8131cb6f4bd9e3791a606d0da8f9ee78276.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/d20b045b8bdaea6ace2ca4170875772b439c2c2e/ss_d20b045b8bdaea6ace2ca4170875772b439c2c2e.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/2ebe4dc35ad33ad86b672f7cf9faed44c9b1668e/ss_2ebe4dc35ad33ad86b672f7cf9faed44c9b1668e.1920x1080.jpg?t=1764916587\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/dae3a0798c6c59a433b46de8f5ec18b21ad97fcd/ss_dae3a0798c6c59a433b46de8f5ec18b21ad97fcd.1920x1080.jpg?t=1764916587\"]', '<h2 class=\"bb_tag\" >化身公主骑士</h2><p class=\"bb_paragraph\" >扮演致命猎手“大黄蜂”，踏入丝与歌统治的王国！被俘至陌生世界，在致命的朝圣之路上迎战强敌、破解远古谜团，直抵王国巅峰。 </p><p class=\"bb_paragraph\" >《空洞骑士：丝之歌》是动作冒险游戏《空洞骑士》的史诗续作。探索全新世界，觉醒未知力量，在虫兽狂潮中杀出血路，揭开与你身世交织的远古秘辛。 </p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/extras/79511c52afc0965cd49ece2f9db57fb1.avif?t=1764916587\" width=776 height=192 /></span></p><h2 class=\"bb_tag\" >游戏特色</h2><ul class=\"bb_ul\"><li><p class=\"bb_paragraph\" >探索陷落的虫之王国“纺络”！穿越苔藓洞窟、镀金城邦与雾锁荒原，攀向世界之巅的闪耀圣堡。</p></li><li><p class=\"bb_paragraph\" >体验生死一线的战斗！在行云流水的华丽厮杀中，施展丰富且致命的招式穿梭于敌阵之中。</p></li><li><p class=\"bb_paragraph\" >锻造强力工具！掌握不断进化的武器、陷阱与机关，横扫敌人，探索新高处。</p></li><li><p class=\"bb_paragraph\" >完成震撼任务！狩猎珍奇异兽，破解远古谜题，实现受难者夙愿，重燃王国希望。</p></li><li><p class=\"bb_paragraph\" >迎战200+凶残敌人！野兽与猎人，怪物与骑士，以勇气与技艺斩尽来敌！</p></li><li><p class=\"bb_paragraph\" >征服40+传奇头目！在史诗对决中挑战传说英雄与堕落君王，执掌王国命运。</p></li><li><p class=\"bb_paragraph\" >挑战钢铁之魂模式！征服王国后，在更高难度的新模式中淬炼技艺。</p></li><li><p class=\"bb_paragraph\" >沉浸绝美交响乐章！《空洞骑士》获奖作曲家Christopher Larkin倾力打造忧郁旋律、恢弘弦乐与直击灵魂的头目战主题曲。</p></li></ul><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/extras/b258a2e2154510be60044988ada30824.webp?t=1764916587\" width=614 height=41 /></span></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/extras/7343b986a4c1e4f76d75078f9acabf0c.avif?t=1764916587\" width=776 height=192 /></span><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/extras/ca3b9d4b06c52fc85590c7bc886fe36b.avif?t=1764916587\" width=776 height=192 /></span><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1030300/extras/5a1d9a433bdc14dcb7f74afe0be61022.avif?t=1764916587\" width=776 height=192 /></span></p>', '动作,冒险,独立', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,视角舒适度,可以仅用键盘,无需应对快速反应事件,随时保存,立体声,Steam 云,在电视上远程畅玩,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 日语<strong>*</strong>, 韩语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 俄语<strong>*</strong>, 简体中文<strong>*</strong><br><strong>*</strong>具有完全音频支持的语言', 'Hollow Knight is © Copyright Team Cherry 2025', 'http://hollowknightsilksong.com/', 90, 'https://www.metacritic.com/game/pc/hollow-knight-silksong?ftag=MCD-06-10aaa1f', '1030300');
INSERT INTO `game_info` VALUES (38, '方舟: 生存飞升', 22.49, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/d934813ca531af0fce69fe36bc972f1c90d1aa19/header.jpg?t=1764880598', 'Studio Wildcard', '2023 年 10 月 25 日', '《方舟》采用虚幻引擎 5 重新设计，并融入了下一代电子游戏技术！组建一个部落, 驯养和繁殖数百种恐龙和其他原始生物，探索、制作、建造，并一路进发至食物链的顶端. 快来加入这场新的冒险吧!', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_44f74e86ebd6620a4f0a1f3f8ea97f73dc215e69.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_65c0de5ced28281764990a299eb3926629b2863e.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_0ce49e629369dbf9e8fba324667167568c05c66f.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_63a6ea13596a948b7873ed6de4fd4d01f9cbe57b.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_37b5cb9485658f91b856a0714a8cbdc69f1802b1.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_f9e156d2b818d808c65476997283b5da8be8324f.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_b3f20fcdde1cd0254fca51b342b27bff3d46e031.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_605387fbf8257c111978ed900f3346ef350fc236.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_7ff0f04cfc0eec97907eb5197c748576b15793ce.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_3c4d9089501a98a9dace96fd5c3ccbf1dfc7d8f4.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_35186898f5260a406f934132a211df6e53f39843.1920x1080.jpg?t=1764880598\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2399830/ss_41510c0b426ecf626b3d1efd727a9fdd7feeb5ec.1920x1080.jpg?t=1764880598\"]', '进入一场全新的恐龙生存体验，这一切将超越您最狂野的幻想……《方舟》采用虚幻引擎 5 重新设计，并融入了下一代电子游戏技术！您会在一个神秘的小岛上醒来，刺眼的阳光和绚丽的色彩将使你目不暇接. 蔚蓝的海水拍打着您赤裸的双脚。迷雾丛林中回荡的低沉吼叫让你猛然惊醒，你站了起来--却不是因为害怕，而是因为好奇。你准备好组建一个部落，驯养和繁殖数百种恐龙和其他原始生物，探索、制作、建造，并一路进发至食物链的顶端了吗？一场新的冒险在等待着你......不要再犹豫了,快来加入这个世界吧！<br><br><strong>游戏特色</strong><br><br>●	《方舟：生存飞升》重新构思了ARK的艺术设计和游戏世界，采用了最新的虚幻引擎5游戏技术，包括高端图形功能技术，例如全局动态照明（Lumen），使光线在物体表面形成逼真的反射效果，以及由数亿个三角形组成的高级网格流技术（Nanite），以呈现出极致的细节。<br><br>●	游戏还采用了全新的物理系统，例如动态水体，使每个生物在液体中移动时都会产生波浪、涟漪、飞溅和气泡，同时还有完全交互式的物理植被，每片草、灌木和树都会对角色、爆炸、抛射物和物理物体做出反应。你甚至可以砍倒一棵树，看着它撞向其他树木，并在砸向下方草地的途中扰乱所有树叶。你甚至可以通过观察草丛的动向来确定偷偷移动的敌人的位置——因为草丛会随着敌人而移动摇摆。当你拆毁一栋建筑时，可以看到碎片更真实地四分五裂，并在落下时与草地和水体发生交互。<br><br>●	通过《方舟：生存飞升》可以访问所有方舟世界，包括焦土、畸变、灭绝、创世纪1、ARK 创世纪第二部分等等。孤岛地图现已发布，后续扩展地图将定期免费添加。<br><br>●	这次的终极生存体验将开启更加精彩的新篇章：自定义你的幸存者，组建一个部落，在一个真实的生态系统中驯化、训练、繁衍和驾驭各种恐龙。你需要时刻关注食物、水源、温度和天气变化。在采集、建造建筑、种植农作物、自定义游戏世界的过程中，慢慢扩张领土，探索并揭开孤岛及其他世界的真面目。<br><br>●	在游戏体验的各个方面都进行了更彻底的改进：重新设计的用户界面，智能生物寻路的动态导航，野生恐龙宝宝，照片模式，新的相机系统，新的地图系统，追踪系统，全新的建筑、物品和生物等等。<br><br>●	跨平台模组：您可以直接在游戏中通过专门的模组浏览器 下载并畅玩由其他玩家创造的新内容，包括新地图、生物、物品以及游戏模式！随着社群的创造力和才能首次在各个平台上得到充分释放，您将源源不断地享受到有关方舟新玩法！<br><br>●	跨平台多人游戏：与不同游戏平台上的其他玩家一起组建在线部落，共同在方舟的世界中生存和发展！<br><br>●	游戏支持多达70名玩家的公共在线多人游戏，最多可容纳8名玩家的私人游戏，以及2至4名玩家的本地分屏模式。', '动作,冒险,独立,大型多人在线,角色扮演', 1, '单人,多人,大型多人在线,玩家对战,线上玩家对战,局域网玩家对战,合作,在线合作,局域网合作,同屏/分屏合作,同屏/分屏,跨平台多人,Steam 成就,完全支持控制器,包含关卡编辑器,远程同乐,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 丹麦语, 荷兰语, 日语, 韩语, 葡萄牙语 - 巴西, 葡萄牙语 - 葡萄牙, 俄语, 简体中文, 西班牙语 - 拉丁美洲, 繁体中文, 土耳其语<br><strong>*</strong>具有完全音频支持的语言', '©2024 Wildcard Properties LLC. Published by Studio Wildcard. Developed by Studio Wildcard. ARK: Survival Ascended is a trademark of Studio Wildcard. All rights reserved.<br />\r<br><br />\r<br>TM & ©2024 SCG Power Rangers LLC and Hasbro. Power Rangers and all related logos, characters, names, and distinctive likenesses thereof are the exclusive property of SCG Power Rangers LLC. All Rights Reserved. Used Under Authorization.', 'http://www.playark.com', 0, '', '2399830');
INSERT INTO `game_info` VALUES (39, '彩虹六号：围攻X', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/fc38e8f28513ee76b7e68240490abe608b07d0d4/header_alt_assets_17_schinese.jpg?t=1766176190', 'Ubisoft Montreal', '2015 年 12 月 1 日', '《彩虹六号：围攻X》是战术团队射击游戏中的典范，明智的策略和行动力是制胜关键。 免费体验快速对战、非排名战和双重战线游戏模式，同时解锁精选特勤干员。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/8363519296edec1c056176f25e20767da3e6e853/ss_8363519296edec1c056176f25e20767da3e6e853.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/5467c59d8651c801aab376432cb96f7a49803d7a/ss_5467c59d8651c801aab376432cb96f7a49803d7a.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/9cf06e0c66b41f0ce84bd786eb85c98c0611a5e5/ss_9cf06e0c66b41f0ce84bd786eb85c98c0611a5e5.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/da91efa551babf2763eca628a0bf88cb6e86aa93/ss_da91efa551babf2763eca628a0bf88cb6e86aa93.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/1ee529c1c835164b7a2949a8b52ce79016d45690/ss_1ee529c1c835164b7a2949a8b52ce79016d45690.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/2e76d6f8373d28a16d214d67565198fd2f729b69/ss_2e76d6f8373d28a16d214d67565198fd2f729b69.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/0f805e2ed6861ff29cab1da2d774e4444d779e57/ss_0f805e2ed6861ff29cab1da2d774e4444d779e57.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/eab99354ab088430336268f021ad14267257d293/ss_eab99354ab088430336268f021ad14267257d293.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/a57be17cdd490f7cc5689d1c3690022d8beb27c9/ss_a57be17cdd490f7cc5689d1c3690022d8beb27c9.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/7775eaf638491a92f348f2add394828deb77c125/ss_7775eaf638491a92f348f2add394828deb77c125.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/a53bff65f0f16fb38967d850e9fd0b0f9f8fa8d3/ss_a53bff65f0f16fb38967d850e9fd0b0f9f8fa8d3.1920x1080.jpg?t=1766176190\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/9d09f2d6a272e97d5799d4d8442e291bfbada867/ss_9d09f2d6a272e97d5799d4d8442e291bfbada867.1920x1080.jpg?t=1766176190\"]', '《彩虹六号：围攻X》是战术团队射击游戏中无可争议的典范，明智的策略和行动力是制胜关键。<br>免费体验快速对战、非排名战和双重战线游戏模式，同时解锁精选特勤干员。<br><br>排名战对战列表、围攻杯、额外特勤干员和装饰物品等特定内容和功能需要付费解锁。<br><br>《彩虹六号：围攻X》是本游戏史上最大规模的变革，带来改进的视觉效果、增强的玩家体验以及提升游戏策略深度的玩法变动。<br><br>x<h2 class=\"bb_tag\" >高风险5v5对战 进攻方VS防守方</h2><br>攻防交替互换，针对特勤干员和地图调整战术。感受行动的紧张与刺激，充分运用强大的破坏工具和各式设备，用智慧和技巧压制对手，赢得每一局对战。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/108bbc04e3a0b0f6d0834a21b6e0d3e0.poster.avif?t=1766176190\" width=600 height=430 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/108bbc04e3a0b0f6d0834a21b6e0d3e0.webm?t=1766176190\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/108bbc04e3a0b0f6d0834a21b6e0d3e0.mp4?t=1766176190\" type=\"video/mp4\"></video></span><br>- 如果你加入防守方，就将四周环境变成坚不可摧的堡垒。加固墙壁，设置路障，布置陷阱，控制敌人的动向。利用监控工具收集情报，预判敌人的进攻路线。坚守阵地，针对敌方的战术灵活应变，以技巧取胜。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/a0f899fa2deea4f4381733bee3088420.poster.avif?t=1766176190\" width=600 height=430 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/a0f899fa2deea4f4381733bee3088420.webm?t=1766176190\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/a0f899fa2deea4f4381733bee3088420.mp4?t=1766176190\" type=\"video/mp4\"></video></span><br>- 如果你加入进攻方，则要操控观测无人机侦察敌方位置和炸弹点情报。与团队通力合作，突破重重防线，巧妙避开陷阱，出其不意地开辟突破口。通过垂直突袭，从上方或下方攻其不备，或是绳降突入窗户，发起英勇进攻。制定周密的进攻计划，果断执行，化解一切危机。<h2 class=\"bb_tag\" >破坏工具</h2><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/57ea50201df93a9b9e03d409dbfa0aa0.poster.avif?t=1766176190\" width=600 height=430 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/57ea50201df93a9b9e03d409dbfa0aa0.webm?t=1766176190\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/57ea50201df93a9b9e03d409dbfa0aa0.mp4?t=1766176190\" type=\"video/mp4\"></video></span><br>破坏不仅是制造混乱，更是通往胜利的关键武器。巧妙利用周围环境，将厚重的墙壁变成通向机遇的窗口。运用最尖端的技术追踪敌方动向，打破墙壁开拓视野，操控环境，牢牢掌握战术优势。<h2 class=\"bb_tag\" >双重战线：动态战术6v6作战</h2><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/945c9c1577e2d957f85252d6320ffeb9.poster.avif?t=1766176190\" width=600 height=430 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/945c9c1577e2d957f85252d6320ffeb9.webm?t=1766176190\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/359550/extras/945c9c1577e2d957f85252d6320ffeb9.mp4?t=1766176190\" type=\"video/mp4\"></video></span><br>在《彩虹六号：围攻》最大的地图上参与激烈的6v6战斗，争夺敌方队伍领地，同时保卫己方领地。一路闯进敌方基地，夺取胜利。<br><br>从精选的进攻方和防守方阵容中选择干员，与队友并肩作战，解锁全新的战术机遇。在重新部署时，调整特勤干员人选和策略，改变战局的走势，或者前往中立区执行高风险、高回报的分支任务，赢取足以瞬间扭转局势的奖励。<br><br>双重战线是体验《彩虹六号：围攻》战术深度与强度的震撼新方式。<h2 class=\"bb_tag\" >从新兵到精英</h2><br>这趟充实的入门之旅专为新玩家量身打造，设有全新的等级路径，帮助你循序渐进地掌握基础操作、提升技巧、建立自信，让你以最佳状态投入紧张刺激的实战。<h2 class=\"bb_tag\" >持续扩展</h2><br>每个赛季都带来重大更新，新增内容和功能，为战术和玩法注入新活力。<h2 class=\"bb_tag\" >高度竞技性</h2><br>加入超过8,000万玩家的庞大在线社区。观看顶级职业战队在久负盛名的《彩虹六号：围攻》电竞赛事中展开巅峰对决。<br><br><br>免费进入游戏，部分内容和功能（包括竞技游戏模式、特勤干员和装饰物品）需要付费解锁。', '动作,免费开玩', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,完全支持控制器,Steam 集换式卡牌,应用内购买,在手机上远程畅玩,在平板上远程畅玩', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 捷克语, 荷兰语, 日语<strong>*</strong>, 韩语, 波兰语, 葡萄牙语 - 巴西<strong>*</strong>, 俄语<strong>*</strong>, 繁体中文, 简体中文, 土耳其语, 泰语<br><strong>*</strong>具有完全音频支持的语言', '©2015 Ubisoft Entertainment. All Rights Reserved. Tom Clancy’s, Rainbow Six, The Soldier Icon, Ubisoft and the Ubisoft logo are trademarks of Ubisoft Entertainment in the U.S. and/or other countries.', 'http://rainbow6.ubi.com/', 0, '', '359550');
INSERT INTO `game_info` VALUES (40, 'Grand Theft Auto V 增强版', 14.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/header_schinese.jpg?t=1765479644', 'Rockstar North', '2025 年 3 月 4 日', '体验娱乐业轰动一时的 Grand Theft Auto V 和 Grand Theft Auto 在线模式：现已更新换代，拥有震撼的视觉效果、更快的加载速度、3D 音效及更多内容，另有为 GTA 在线模式玩家提供的独家内容。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/d61184a98c1cf2db2b08b2999c04b0519e3615bb/ss_d61184a98c1cf2db2b08b2999c04b0519e3615bb.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/8340fd391012e12be7e4c02e65801a2648a6b60e/ss_8340fd391012e12be7e4c02e65801a2648a6b60e.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/f2e70b5823510daa062293ff0b03821e1dee2d37/ss_f2e70b5823510daa062293ff0b03821e1dee2d37.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/6959cc5d64cce82cb68a27457cfa46fb4d50f897/ss_6959cc5d64cce82cb68a27457cfa46fb4d50f897.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/0231bf16835cd4f6d83523d76aa8d91cb2dfef9b/ss_0231bf16835cd4f6d83523d76aa8d91cb2dfef9b.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/60690d0fdbd022ab1527ba6d080d42c1812a0f39/ss_60690d0fdbd022ab1527ba6d080d42c1812a0f39.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/cf15774bb38c9b74f9e98c985228510c97df80b6/ss_cf15774bb38c9b74f9e98c985228510c97df80b6.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/808b550269f898e227dae2c64b5e026f90da85f9/ss_808b550269f898e227dae2c64b5e026f90da85f9.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/85ff4a9fad2064c201c00c27d8d28c28fa03c481/ss_85ff4a9fad2064c201c00c27d8d28c28fa03c481.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/f4a6f63b4f7cb42262152449ba5e5c5837f20ff4/ss_f4a6f63b4f7cb42262152449ba5e5c5837f20ff4.1920x1080.jpg?t=1765479644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3240220/7f432690bd6365e871a2463a5db9cc4e7ebe1151/ss_7f432690bd6365e871a2463a5db9cc4e7ebe1151.1920x1080.jpg?t=1765479644\"]', '<h2 class=\"bb_tag\" >欢迎来到洛圣都</h2><p class=\"bb_paragraph\" >一个初涉江湖的街头新丁、一个洗手多年的银行劫匪和一个丧心病狂的杀人狂魔被卷入地下世界、美国政府与娱乐业最为恐怖、最为疯狂的元素之中时，他们必须齐心协力，接连完成一系列的惊天劫案，才能在这个冷血无情的城市中苟延残喘。他们谁也不能相信，尤其是身边的同伙。</p><p class=\"bb_paragraph\" >现在 PC 平台的玩家可以使用一次性迁移将 GTA 5 故事模式进度和 GTA 在线模式角色和进度从传承版的 GTA 5 迁移到 GTA 5 增强版。 </p><h2 class=\"bb_tag\" >震撼的视觉效果</h2><p class=\"bb_paragraph\" >通过新的图形模式提高保真度和性能水平，包括光线追踪功能（例如环境光遮蔽和全局照明）、光线追踪阴影和反射、对 AMD FSR 和 NVIDIA DLSS 的支持及更多内容。*</p><h2 class=\"bb_tag\" >更快的加载</h2><p class=\"bb_paragraph\" >通过在支持的设备上使用 SSD 和 DirectStorage 更快地展开行动，因为洛圣都和布莱恩郡世界的加载速度比以往任何时候都快。</p><h2 class=\"bb_tag\" >沉浸式控制</h2><p class=\"bb_paragraph\" >通过 DualSense™ 无线控制器上的自适应扳机，利用动态阻力感受新水平的响应能力，从定向伤害到天气影响、粗糙路面到爆炸，及更多精彩内容。*</p><h2 class=\"bb_tag\" >3D 音效</h2><p class=\"bb_paragraph\" >增强的音效，支持 Dolby Atmos（杜比全景声），并能提高语音、电影特效和音乐的保真度。*以精确的定位聆听世界的声音：被盗超级跑车的油门声、邻近的枪声、头顶上直升机的轰鸣声及更多内容。</p><h2 class=\"bb_tag\" >Grand Theft Auto 在线模式</h2><p class=\"bb_paragraph\" >体验 GTA 在线模式，一个动态的、不断发展的在线宇宙，可容纳多达 30 名玩家，您可以在其中从街头新丁步步攀升，直至成为您自己犯罪帝国的至尊霸主。</p><p class=\"bb_paragraph\" >享受全新的高性能载具升级、诸如职业建设器之类的提升，以及所有 GTA 在线模式的游戏内容升级、拓展内容，和自从游戏发售以来推出的内容，可单人游玩，也可与好友同乐。完成石破天惊的合作抢劫、参与酣畅淋漓的特技竞速、在独一无二的对抗模式中竞争，或者在包括夜总会、游戏厅、空中别墅派对、车友会等社交场合中享受闲暇时光，及更多精彩内容。</p><h2 class=\"bb_tag\" >专属内容</h2><p class=\"bb_paragraph\" >在洛圣都车友会中迈入阿浩特别工坊，这里有全新精英升级和独家改装。然后将这些高性能载具带入阿浩特别工坊竞速、全新时间挑战赛，及更多内容。</p><h2 class=\"bb_tag\" >全新的职业建设器</h2><p class=\"bb_paragraph\" >利用行业必备工具开始 GTA 在线模式。从四种生意中快速选择一种：摩托帮首领、总裁、夜总会所有者或军火走私贩，并选择购置资产、马力强劲的载具和武器以建立您自己的帝国。</p><h2 class=\"bb_tag\" >全新的菜单设计</h2><p class=\"bb_paragraph\" >通过主菜单立即访问 GTA 在线模式所提供的一切，包括最新和最热门的更新。</p><h2 class=\"bb_tag\" >畅享现有和先前的全部更新</h2><p class=\"bb_paragraph\" >深入 40 多个大规模更新，还有更多内容将在未来推出。内容应有尽有，从在《破坏行动探员》中运营秘密行动基地，到在《油水榨干赏金生意》中追踪和抓捕南圣安地列斯的头号通缉犯；从在《当当钟农场突袭》中揭露腐败警察和杀手贩毒集团，到在《赃车店》中大胆抢劫洛圣都最令人垂涎的载具。除了这些，您还能单人或与好友共同游玩各种各样的竞速、模式、活动和社交空间，包括夜总会、游戏厅、空中别墅派对、车友会，及更多内容。</p>', '动作,冒险,竞速', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,Steam 成就,应用内购买,部分支持控制器,在手机上远程畅玩,在平板上远程畅玩', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 西班牙语 - 拉丁美洲, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', 'Rockstar Games, Inc. 622 Broadway, New York, NY, 10012. © 2001–2025. Rockstar Games, Rockstar North, Grand Theft Auto, GTA Five, Grand Theft Auto Online, and R* Logo are marks/logos/copyrights of Take-Two Interactive. Dolby and the double-D symbol are trademarks of Dolby Laboratories. © 1999-2004 Dolby Laboratories. Separate cables may be required for HDTV and Dolby Digital; sold separately. Uses Bink Video. © 1997–2022 by Epic Games Tools, LLC. This game includes Autodesk Scaleform™ software, © 2013 Autodesk, Inc. The ratings icon is a trademark of the Entertainment Software Association. All other marks and trademarks are properties of their respective owners. All rights reserved. <br />\r<br><br />\r<br>Purchase grants a license to the digital product subject to the Terms of Service and Privacy Policy in game and at https://www.rockstargames.com/legal and https://www.rockstargames.com/privacy. Some features may require Rockstar Games account registration (minimum age varies) and/or internet connection. Rockstar Games does not guarantee the availability of online features, such as in-game purchases, or downloadable content, at any time including at launch. Additional bonus content may require single-use serial code or additional fees. Rockstar Games account and bonus content are non-transferrable. Rockstar Games reserves the right to modify or discontinue the availability of any game features as provided under the Terms of Service. Violation of the Terms of Service, Code of Conduct, or other policies may result in restriction or termination of access to game or online account. For info, customer service, and tech support, visit www.rockstar.com/support. The content of this videogame is purely fictional, and is not intended to represent or depict any actual event, person, or entity; any similarities to real-world places, people, or entities is coincidental and does not imply any third-party sponsorship or endorsement of game content.  The makers and publishers of this videogame do not in any way endorse or condone engaging in any conduct depicted in this videogame.', 'https://www.rockstargames.com/V/', 0, '', '3240220');
INSERT INTO `game_info` VALUES (41, '赛博朋克 2077', 20.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/4e88035f3a75e69e0ef2005fc611a7fafcb227e1/header_schinese.jpg?t=1766141193', 'CD PROJEKT RED', '2020 年 12 月 9 日', '《赛博朋克 2077》是一款开放世界动作冒险 RPG 游戏。故事发生在暗黑未来的夜之城，一座五光十色、危机四伏的超级大都会，权力更迭和无尽的身体改造是这里不变的主题。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_2f649b68d579bf87011487d29bc4ccbfdd97d34f.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_0e64170751e1ae20ff8fdb7001a8892fd48260e7.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_af2804aa4bf35d4251043744412ce3b359a125ef.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_7924f64b6e5d586a80418c9896a1c92881a7905b.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_4eb068b1cf52c91b57157b84bed18a186ed7714b.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_b529b0abc43f55fc23fe8058eddb6e37c9629a6a.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_8640d9db74f7cad714f6ecfb0e1aceaa3f887e58.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_9284d1c5b248726760233a933dbb83757d7d5d95.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_4bda6f67580d94832ed2d5814e41ebe018ba1d9e.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_e5a94665dbfa5a30931cff2f45cdc0ebea9fcebb.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_429db1d013a0366417d650d84f1eff02d1a18c2d.1920x1080.jpg?t=1766141193\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/ss_872822c5e50dc71f345416098d29fc3ae5cd26c1.1920x1080.jpg?t=1766141193\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/bc6661c7ff3744a669a1dbe1df46e23b.poster.avif?t=1766141193\" width=1170 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/bc6661c7ff3744a669a1dbe1df46e23b.webm?t=1766141193\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/1c256b705c717fdb795af513ae705ef4.poster.avif?t=1766141193\" width=1170 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/1c256b705c717fdb795af513ae705ef4.webm?t=1766141193\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/a8528a83c6af6685523850efe8df8c01.poster.avif?t=1766141193\" width=1170 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/a8528a83c6af6685523850efe8df8c01.webm?t=1766141193\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/f44cd0d347fb2d7476a6fb09f2cd9fcc.poster.avif?t=1766141193\" width=1170 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/f44cd0d347fb2d7476a6fb09f2cd9fcc.webm?t=1766141193\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/f23c014c7a41805721e7c39335cad60b.poster.avif?t=1766141193\" width=1170 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/f23c014c7a41805721e7c39335cad60b.webm?t=1766141193\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/ba2db44b37abac30286ae9340232ca86.poster.avif?t=1766141193\" width=1170 height=450 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1091500/extras/ba2db44b37abac30286ae9340232ca86.webm?t=1766141193\" type=\"video/webm; codecs=vp9\"></video></span></p>', '角色扮演', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,可调整难度,无需应对快速反应事件,随时保存,立体声,字幕选项,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 阿拉伯语, 捷克语, 匈牙利语, 日语<strong>*</strong>, 韩语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 俄语<strong>*</strong>, 简体中文<strong>*</strong>, 西班牙语 - 拉丁美洲, 泰语, 繁体中文, 土耳其语, 乌克兰语<br><strong>*</strong>具有完全音频支持的语言', 'CDPROJEKT®，Cyber​​punk®，Cyber​​punk2077® 是 CD PROJEKT S.A.的注册商标。© CD PROJEKT S.A.保留所有权利。所有其他版权和商标均为其各自所有者的财产。', 'https://www.cyberpunk.net', 86, 'https://www.metacritic.com/game/pc/cyberpunk-2077?ftag=MCD-06-10aaa1f', '1091500');
INSERT INTO `game_info` VALUES (42, '漫威争锋', 0.00, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/aa6ef4179dafa038c6f73df2bd6eb75d675e399c/header_schinese.jpg?t=1765778992', 'NetEase Games', '2024 年 12 月 5 日', '《漫威争锋》是一款由漫威IP正版授权、网易自研的超级英雄PVP团队射击游戏。玩家可以从丰富多样的超级英雄阵容中挑选心仪角色，组成全明星队伍，在漫威多元宇宙中各种可破坏的地图上，利用独特的超能力进行精彩激战。成为超级英雄，集结你的小队，一起战斗吧！', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/f439edbd061f256e1c9956351ac3580b1c3ccafd/ss_f439edbd061f256e1c9956351ac3580b1c3ccafd.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/4c7620ed61604b252a698aca99f201a36d514768/ss_4c7620ed61604b252a698aca99f201a36d514768.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/2863571a710e40420b448228e4ce90aed67ed84a/ss_2863571a710e40420b448228e4ce90aed67ed84a.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/dc7ed78b9e05b48a38946e2a3e8460e5e633d629/ss_dc7ed78b9e05b48a38946e2a3e8460e5e633d629.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/ef2d770c98f3d543285693c47d78f418fd09d2dd/ss_ef2d770c98f3d543285693c47d78f418fd09d2dd.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/5a4553ed4ba60b41ec503a9cf7a07715c05ba794/ss_5a4553ed4ba60b41ec503a9cf7a07715c05ba794.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/18825994ebe8d92f0700bcd1ecc118f48b4fc6a6/ss_18825994ebe8d92f0700bcd1ecc118f48b4fc6a6.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/03aad4fa01e6e6b58e7bec9bd0ef8c0d32b06d5b/ss_03aad4fa01e6e6b58e7bec9bd0ef8c0d32b06d5b.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/fb1d494145281f01c7b437e0b72a71977eb55894/ss_fb1d494145281f01c7b437e0b72a71977eb55894.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/8619630319804a1c233e704f6b84dd07c647e15e/ss_8619630319804a1c233e704f6b84dd07c647e15e.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/13b2d3e5ebe12e003c2718fb5e550351ad274c8d/ss_13b2d3e5ebe12e003c2718fb5e550351ad274c8d.1920x1080.jpg?t=1765778992\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/47c19fb55fa8765b375deb5f9fc69a5543ac3b2c/ss_47c19fb55fa8765b375deb5f9fc69a5543ac3b2c.1920x1080.jpg?t=1765778992\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/7b14c698e3cae345068fc2496c6ea627.poster.avif?t=1765778992\" width=610 height=160 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/7b14c698e3cae345068fc2496c6ea627.webm?t=1765778992\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/7b14c698e3cae345068fc2496c6ea627.mp4?t=1765778992\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" >《漫威争锋》是一款超级英雄团队PVP射击游戏。你可以从丰富多样的超级英雄和超级反派阵容中，挑选心仪的角色组成全明星队伍，在漫威多元宇宙各种可破坏的地图上，利用独特的超能力进行精彩激战。 </p><p class=\"bb_paragraph\" ></p><h2 class=\"bb_tag\" >漫威正版授权，首款超级英雄团队PVP射击大作</h2><p class=\"bb_paragraph\" >你可以从复仇者联盟、银河护卫队、X战警等漫威多元宇宙中的诸多团队中，选择脍炙人口、别具一格的漫威超级英雄或反派进行战斗，如黑豹、蜘蛛侠、万磁王和魔剑客等经典角色。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/1be995dac6209fcc82602a115764654c.poster.avif?t=1765778992\" width=614 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/1be995dac6209fcc82602a115764654c.webm?t=1765778992\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/1be995dac6209fcc82602a115764654c.mp4?t=1765778992\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >6v6快节奏刺激对战，战局瞬息万变</h2><p class=\"bb_paragraph\" >错综复杂的战场设计，节奏紧张的游戏体验，变幻莫测的战斗局势，你在《漫威争锋》中永远不会感到无聊！成为拥有惊人能力的超级英雄或超级反派，利用你的力量，瞬间就能将战场变得趣味横生。化身为你最爱的超级英雄踏上战场，享受一场酣畅淋漓的决斗吧！</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/8ec4dc05a3bf2df290124c12824b22b7.poster.avif?t=1765778992\" width=614 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/8ec4dc05a3bf2df290124c12824b22b7.webm?t=1765778992\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/8ec4dc05a3bf2df290124c12824b22b7.mp4?t=1765778992\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >经典英雄独特技能，团战配合协作无间</h2><p class=\"bb_paragraph\" >英雄间的羁绊将触发连携技能，多样协作赋予战术更多可能：你可化身为火箭浣熊站在格鲁特背上，对敌人进行猛烈进攻；或化身为浩克释放伽马能量为钢铁侠的盔甲充能，超能力激发巨大战斗力，赢得战场先机......还有更多精彩等着你去探索！</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/90fdb5ea1bab4a9c2b96106ff8da1786.poster.avif?t=1765778992\" width=614 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/90fdb5ea1bab4a9c2b96106ff8da1786.webm?t=1765778992\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/90fdb5ea1bab4a9c2b96106ff8da1786.mp4?t=1765778992\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >地图场景可破坏，颠覆既定战场规则</h2><p class=\"bb_paragraph\" >战场不再死气沉沉，它将成为制胜武器！你将在经典的漫威世界场景中战斗，穿行于千变万化的战场之中，使用超能力改变环境，重新塑造地形，并在战场上创造战略优势；你还可以寻找掩体躲避攻击，或是将现场物品化作临时武器反击敌人。在这里，你有无数种空间场景战术来赢得胜利！</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/29f2b96912254738fe25502020fa8ef7.poster.avif?t=1765778992\" width=614 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/29f2b96912254738fe25502020fa8ef7.webm?t=1765778992\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/29f2b96912254738fe25502020fa8ef7.mp4?t=1765778992\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >不断扩张的漫威宇宙，丰富的赛季更新</h2><p class=\"bb_paragraph\" >漫威多元宇宙也为《漫威争锋》带来了无尽的可能性。故事将会跟随赛季逐步推进，每个赛季更新都会有新的超级英雄或反派加入战场，并引入更多充满挑战性的地图。你可以在各种玩法策略中运用每个赛季解锁的新内容，体验到惊险刺激的战斗和无限可能性！</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/6dd8472b5c36421702cd90af0742a75a.poster.avif?t=1765778992\" width=614 height=344 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/6dd8472b5c36421702cd90af0742a75a.webm?t=1765778992\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2767030/extras/6dd8472b5c36421702cd90af0742a75a.mp4?t=1765778992\" type=\"video/mp4\"></video></span></p>', '动作,免费开玩', 1, '多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,应用内购买', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 俄语, 土耳其语, 泰语, 简体中文<strong>*</strong>, 繁体中文, 西班牙语 - 拉丁美洲, 韩语, 日语<strong>*</strong>, 波兰语, 葡萄牙语 - 巴西, 阿拉伯语<br><strong>*</strong>具有完全音频支持的语言', '©2024 MARVEL<br />\r<br>©1997-2024 NetEase, lnc. All Rights Reserved.', 'https://www.marvelrivals.com', 0, '', '2767030');
INSERT INTO `game_info` VALUES (43, 'Fallout 76', 3.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/90623cf0c24df8941b1e43e7aaa4d73175b440fd/header.jpg?t=1764951803', 'Bethesda Game Studios', '2020 年 4 月 14 日', 'Bethesda Game Studios邀您进入《Fallout 76》的世界。在核弹爆炸的25年后，你和其他避难所居民进入了核爆之后的美国。加入《Fallout》系列故事选新推出的开放世界多人游戏，探索核战争留下的广阔废土。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/5165184416a48a0f4e2739f16b25a597a096c221/ss_5165184416a48a0f4e2739f16b25a597a096c221.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/9c317dc153b2905648f0b78301cde36f17001327/ss_9c317dc153b2905648f0b78301cde36f17001327.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/0fecafd8ecd749f1e1d6b37e0137d36f237ad4ea/ss_0fecafd8ecd749f1e1d6b37e0137d36f237ad4ea.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/7b7aa311795c79554a0d8c95386beb37b30b021e/ss_7b7aa311795c79554a0d8c95386beb37b30b021e.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/1376bdab9a129e0f56a04059b3d490a1aacb4e85/ss_1376bdab9a129e0f56a04059b3d490a1aacb4e85.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/1d93d54320f1ba83a89b5ac944381a5edaf3ef18/ss_1d93d54320f1ba83a89b5ac944381a5edaf3ef18.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/2e2d22d2c71b8c4b3f2e1b08a00486775c6edfcf/ss_2e2d22d2c71b8c4b3f2e1b08a00486775c6edfcf.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/bc94723dc071ae824000d704abb8375edbc3d29e/ss_bc94723dc071ae824000d704abb8375edbc3d29e.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/ss_b26198ba17c91980b932b7f54a28036828d928e6.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/ss_3820f55642a38473932c7214b03afa87daae7c0b.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/ss_04e6e9189d075b7bd6eada426babb754e48957b0.1920x1080.jpg?t=1764951803\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1151340/ss_283292296af1e5b3e3c64a0baa07eba452ac51ae.1920x1080.jpg?t=1764951803\"]', '<p class=\"bb_paragraph\" >《Skyrim》和《Fallout 4》的获奖开发者Bethesda Game Studios欢迎你进入《Fallout 76》的世界。核弹爆炸的25年后，你和其他避难所居民——全国最优秀聪明的佼佼者——在2102年收复日踏入了末日之后的美国。孤身冒险，或者与人合作，按照你喜欢的方式在这片废土世界中探索、追寻、建设，并战胜最大的威胁吧。加入《Fallout》系列故事中新推出的开放世界多人游戏，探索核战争留下的广阔废土。</p><p class=\"bb_paragraph\" > </p><h2 class=\"bb_tag\" >丰富的任务线和引人入胜的人物</h2><p class=\"bb_paragraph\" >从你离开76号避难所的那一刻开始，通过沉浸式的主线任务来揭开西弗吉尼亚的秘密。与前来重建的新邻居交朋友或背叛他们，通过居民的角度体验阿帕拉契亚。 </p><h2 class=\"bb_tag\" >S.c.o.r.e.赛季棋盘</h2><p class=\"bb_paragraph\" >通过完成有限时间的挑战，在赛季中取得进展，获得完全免费的奖励，如消耗品、C.A.M.P.物品和更多。 </p><h2 class=\"bb_tag\" >多人角色扮演</h2><p class=\"bb_paragraph\" >用S.P.E.C.I.A.L.系统创造你的角色，在全新的未开垦废土中开创属于你的道路和声誉，有数百个地点等你来探索。这个全新的、独一无二的《Fallout》世界欢迎你单独探险，或是与好友共同出击。 </p><h2 class=\"bb_tag\" >辉煌山地</h2><p class=\"bb_paragraph\" >故事在《Fallout 76》的世界里总是生生不息，六个不同的西弗吉尼亚地区带来了生机。从阿帕拉契亚的森林到红莓沼泽的有毒深红地带，每个地区都有自己的风险和回报。 </p><h2 class=\"bb_tag\" >全新的美国梦</h2><p class=\"bb_paragraph\" >使用全新建造与组装行动平台（C.A.M.P.），可随时随地进行建造和制作。你的C.A.M.P.会为你提供急需的住宿、物资，并确保你的安全。你甚至可通过建立商店，与其他幸存者交易物品。 </p><h2 class=\"bb_tag\" >Fallout世界</h2><p class=\"bb_paragraph\" >用Fallout世界在阿帕拉契亚进行独特的冒险，这套功能不断发展，使玩家能够以独特的方式和可定制的设置玩《Fallout 76》。</p>', '角色扮演', 1, '多人,大型多人在线,玩家对战,线上玩家对战,合作,在线合作,Steam 成就,应用内购买,无需应对快速反应事件,立体声,环绕声,部分支持控制器,在平板上远程畅玩', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 日语<strong>*</strong>, 韩语, 波兰语, 繁体中文, 俄语, 简体中文, 西班牙语 - 拉丁美洲, 葡萄牙语 - 巴西<br><strong>*</strong>具有完全音频支持的语言', 'Fallout® 76: Atlantic City - Boardwalk Paradise ©2023 ZeniMax Media Inc. Bethesda, Bethesda Softworks, Bethesda Game Studios, ZeniMax, Pip-Boy, Vault-Tec, Vault Boy, and related logos are registered trademarks or trademarks of ZeniMax Media Inc. in the U.S. and/or other countries. All Rights Reserved.<br />\r<br><br />\r<br>Persistent broadband internet connection required. Acceptance of end user license agreement, Terms of Service, and Privacy Policy, and account registration required to play. Product may be discontinued, and content modified, disabled, suspended or removed, at any time. Product requires installation of application software. See Terms of Service, Privacy Policy and additional details at bethesda.net.', 'https://fallout.bethesda.net/en/', 0, '', '1151340');
INSERT INTO `game_info` VALUES (44, '博德之门3', 44.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/48a2fcbda8565bb45025e98fd8ebde8a7203f6a0/header.jpg?t=1765505948', 'Larian Studios', '2023 年 8 月 3 日', '《博德之门3》是一款以“龙与地下城”世界为背景的、强调故事与队伍冒险体验的角色扮演游戏。你的每一个选择，都会编织出一段关于同伴与背叛、生存与牺牲，以及至上力量诱惑的故事。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_c73bc54415178c07fef85f54ee26621728c77504.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_73d93bea842b93914d966622104dcb8c0f42972b.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_cf936d31061b58e98e0c646aee00e6030c410cda.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_b6a6ee6e046426d08ceea7a4506a1b5f44181543.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_6b8faba0f6831a406ce015648958da9612d14dbb.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_8fc5eba770b4a1639b31666908bdd2bbc1aa2ae4.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_31c13d137706fb4d9a4210513274a3ed9c3c7c96.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_0efa1a469a37beeb4fd0cb8e16dc99bd36357dc4.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_387896248b42a15239a9256844b3e4bff72fbc2a.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_44d30da9c3e4622b46f2978e89bda0515856eaf4.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_f8c7fcdeccbfe77e3fad33330a464fed30f781f0.1920x1080.jpg?t=1765505948\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/ss_332cd26db210d4b10df744485ecf0a9b3f2e9024.1920x1080.jpg?t=1765505948\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/589651a71c5264bfc62c250ee044a18a.avif?t=1765505948\" width=616 height=516 /></span></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >召集你的队伍，重返被遗忘的国度。这里有友情与背叛，牺牲与求生，还有那股让人难以抗拒的——至上力量的诱惑。 </p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >神秘的力量正在你体内苏醒，而这一切都来源于夺心魔种在你大脑里的寄生虫。反抗吧，将黑暗的力量转为己用；你也可以任由腐化蔓延，亲手把自己塑造成终极邪恶。</p><p class=\"bb_paragraph\" > </p><p class=\"bb_paragraph\" >由《神界：原罪2》原班人马倾力打造，这款基于《龙与地下城》规则的次世代 RPG 巨作，等你来战。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/18a8497fb84c3a38c835f70f7a167874.avif?t=1765505948\" width=616 height=378 /></span></p><p class=\"bb_paragraph\" >你可以从《龙与地下城玩家手册》中的12个职业与11个种族中进行选择，或者直接扮演一位拥有量身打造背景故事的“起源英雄”。如果你渴望更深层的挣扎，那就化身“邪念”——一个完全可定制的起源英雄，拥有独一无二的机制和黑暗故事线，去与你内心的腐化欲望纠缠。无论你选择成为谁，你都可以在“被遗忘的国度”及更广阔的世界中冒险、搜刮、战斗，甚至谈一场轰轰烈烈的恋爱。召集你的队伍，既可以独自上路，也支持最多四人在线联机。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/0868f9081ab000162091263c6dd2963b.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" >被劫持，被感染，陷入迷失。你正在变成一个怪物，但随着你体内腐化力量的增长，你的力量也在增强。这力量或许能助你幸存下来，但在接受它的同时，你必将付出某种代价。然而，超越任何异能，你与队友之间建立的信任纽带可能会成为你最大的力量。在恶魔、诸神以及种种不可名状的异界力量交织的冲突中，你们将一起决定“被遗忘的国度”的最终命运。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/9fbf71df6f359d0947fd0e2d48dc2fea.avif?t=1765505948\" width=616 height=378 /></span></p><p class=\"bb_paragraph\" >利用全新的Divinity4.0引擎打造，《博德之门3》将为你提供前所未有的自由度，让在这个充满角色、危险和欺诈的鲜活世界中探索、尝试、互动，并获取即时的反馈。宏大的电影级叙事将拉近你与角色的距离。从幽影诅咒笼罩的森林，到幽暗地域的魔法洞穴，再到博德之门熙攘的街头——你的行动定义了这段冒险，而你的选择将定义你的传奇。你，终将被世人铭记。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/e0b8bee7924b29eb8887799aad1e884a.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" >“被遗忘的国度”是一个宏大、细节繁多、充满丰富多样性的世界，到处都有等待被你发现的秘密——你可以潜行、通过蘸取借力、推击敌人、攀爬高地、极限跳跃。这些行动会贯穿你的整个旅途，从大地深处的幽暗地域，到地表城市熠熠生辉的屋顶。你的每一个选择都会推动故事向前发展，每一个决定都会在世界上留下你的印记。你可以提升自己的角色、培养盟友或树立宿敌，用你自己的方式解决问题。绝不会有两次完全相同的游戏体验。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/48fd1530a4e9b0c2d64bedf73019fe33.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" >你可以将你的团队联合起来，同时攻击敌人；也可以将队伍分开，各自执行自己的任务和计划。你们可以共同制定完美的作战方案……或者在队友最意想不到的时候制造一点混乱（比如搞点小动作）。人际关系本就复杂，更何况你们每个人脑子里都塞着一条寄生虫。</p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/2499741b1ca063def455a797fe081b2d.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" >7位独一无二的起源角色为你带来精雕细琢的体验，每个人都有自己独特的特质、思想和世界观。他们的故事与主线叙事相互交错，而你的选择将决定这些故事的结局是救赎、拯救、统治，还是其他更多结局。你可以直接扮演他们，或者招募他们并肩作战。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/8c89ae19c5dab4bb38042f474085cef9.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" >基于第五版《龙与地下城》规则。你可以体验基于团队战斗的先攻机制，以及在战斗中不同决策带来的优势和劣势。我们增添了战术视角的掷骰修正，也拓展了环境之间的交互性，增添了新的、更加灵活的战术和奖励，以让你在战斗中尽情展现你的策略和远见。游戏提供三档难度，你可以随时调整挑战强度：想轻松一点，可以开启“业力骰子”，让运气别太折腾你；想找点虐，就把难度拉高，体验一把真正硬核的战斗。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/09e5dce63dbd232d2337d45be6650ef4.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" >在11个种族（人类、吉斯洋基人、半兽人、矮人、精灵、卓尔、提夫林、半身人、半精灵、侏儒、龙裔）的基础上又增加了31个亚种，并从12个职业中分支出46个子职。在这个精雕细琢的世界中，600多种法术和行动提供了近乎无限的互动自由。在这个世界中，探索是一种奖励，而玩家的主动性决定了旅程。我们独特的“角色创建器”具有前所未有的深度，其丰富的互动选项可确保无论你是谁，都会为你打造出独特的角色，一直到12级。超过174小时的过场动画，保证无论你怎么选，每一轮游戏都有一套完整的“专属电影”陪你讲述一切。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/dcb49ec0044eab08258649212505cccf.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" > 战争的威胁迫在眉睫地向博德之门袭来，夺心魔的入侵也即将降临。旅途之中，你注定会和形形色色的人结下羁绊——哪怕你一开始只想独来独往。你将与沿途遇到的人建立起真实、充满活力的关系，这些关系会走向何方，全看你的选择。你会和同伴发展出鲜活而真实的关系：他们都有自己的道德准则，会对你一路上的每一次抉择做出反应。你愿意为坚持原则付出多大代价？你会让爱意左右你的行动吗？通往博德之门路上的羁绊，不仅是营地里的片刻慰藉，更为你冒险中的每一个决定增添了情感的重量。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/33bf90808ad7a3e59f7a85da4d3317f9.webp?t=1765505948\" width=616 height=27 /></span></p><p class=\"bb_paragraph\" >当你点击“开始直播”时，你的直播将不会被如某只熊、突然爆出的粗口或忘穿内裤等意外所打断。《博德之门3》提供了3种不同级别的适合直播的定制选项。你可以分别（或同时）禁用裸体和露骨的内容，并可以启用Twitch集成功能，直接与观众互动，正如我们在《地狱飨宴》展示会上所做的一样！有了这些选项，无论你选择以何种方式进行游戏，《博德之门3》都能保证你的直播顺利进行。  <br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/250678836d14352d980c5556003957e2.poster.avif?t=1765505948\" width=600 height=400 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/250678836d14352d980c5556003957e2.webm?t=1765505948\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1086940/extras/250678836d14352d980c5556003957e2.mp4?t=1765505948\" type=\"video/mp4\"></video></span></p>', '冒险,角色扮演,策略', 1, '单人,多人,合作,在线合作,局域网合作,跨平台多人,Steam 成就,完全支持控制器,Steam 集换式卡牌,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,可调整难度,无需应对快速反应事件,随时保存,立体声,字幕选项,环绕声,Steam 云,在电视上远程畅玩,远程同乐,家庭共享', '英语<strong>*</strong>, 法语, 德语, 西班牙语 - 西班牙, 波兰语, 俄语, 简体中文, 土耳其语, 葡萄牙语 - 巴西, 意大利语, 西班牙语 - 拉丁美洲, 繁体中文, 乌克兰语, 韩语, 日语<br><strong>*</strong>具有完全音频支持的语言', '©2019威世智，保留所有权利。博德之门，龙与地下城，及其标志，均为威世智有限责任公司的注册商标。<br />\r<br>©2019拉瑞安工作室，保留所有权利。拉瑞安工作室，是Arrakis nv，拉瑞安游戏有限公司旗下的注册商标。', 'https://baldursgate3.game', 96, 'https://www.metacritic.com/game/pc/baldurs-gate-3?ftag=MCD-06-10aaa1f', '1086940');
INSERT INTO `game_info` VALUES (45, 'Warhammer 40,000: 星际战士2', 23.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/f3e914a39f7b2a303b18d422dc5f923da3bdeeea/header_alt_assets_9_schinese.jpg?t=1764091362', 'Saber Interactive', '2024 年 9 月 9 日', '体验星际战士的超人技能和残暴手段。利用致命能力和毁灭性武器消灭无穷无尽的泰伦虫群。选择单人或多人模式，在恢弘的第三人称战斗中保卫帝国。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/f1c59fddf81f1c27e3b6e5065dd5550037806dd8/ss_f1c59fddf81f1c27e3b6e5065dd5550037806dd8.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_ee88e4cc463b7b25b0ec03930a9e58e5fba2afdf.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_66a8bc13bf868d0e2a76e4d797e6408c271065d7.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_20a753725a34225553c5873fbc079626451ebcf9.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_712fe9d84a853d1d2fc1faee8cd8efb36e2278d0.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_4751069d565b92fadeb6bfd507cef64cf6c70cd1.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_d0bc9105e638b0f8a5364c4a6c70c6ccaf9268bb.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_90d54dd35ea29f80ccbe7618682d36736c2697df.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_f3cb28be981ddf20fccd4e8d827d0e340cf12b75.1920x1080.jpg?t=1764091362\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/ss_dec108d540d8ae215ef9f558e4642ef4bee10e86.1920x1080.jpg?t=1764091362\"]', '<p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/f1870f76f228ea18e47d342dd96b9113.poster.avif?t=1764091362\" width=616 height=225 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/f1870f76f228ea18e47d342dd96b9113.webm?t=1764091362\" type=\"video/webm; codecs=vp9\"></video></span><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/ae4f9c47c04610ebde57cab0f6505fa5.avif?t=1764091362\" width=616 height=95 /></span></p><p class=\"bb_paragraph\" style=\"text-align: center\">体验星际战士的超人技能和残暴手段。利用致命技能和毁灭性武器消灭无穷无尽的泰伦虫群。选择单人或多人模式，在恢弘的第三人称战斗中保卫帝国。  </p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/9869fcc04bcd02aee7f382e5a30982d7.avif?t=1764091362\" width=616 height=104 /></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/ca8f7a4a27030ad6daf47aef22f9b075.poster.avif?t=1764091362\" width=616 height=225 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/ca8f7a4a27030ad6daf47aef22f9b075.webm?t=1764091362\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" style=\"text-align: center\">在这款2011年《Space Marine》的续作中，扮演回归极限战士队列的副官德米特里安·泰图斯，对战人类的敌人，再次证明你的忠诚。在遥远星球上展开史诗级战斗，镇压银河系的恐怖乱象，并揭露黑暗秘密，击退永夜威胁。 </p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/316d36fecc238a88a7f5530d5faf3078.avif?t=1764091362\" width=616 height=104 /></span><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/00235ebe7022261023c1166460b5937a.poster.avif?t=1764091362\" width=616 height=225 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/00235ebe7022261023c1166460b5937a.webm?t=1764091362\" type=\"video/webm; codecs=vp9\"></video></span> </p><p class=\"bb_paragraph\" style=\"text-align: center\">投入星系级战争中！享受激烈而血腥的快节奏第三人称动作玩法，对战屏幕上成百上千的敌人，并清除无穷无尽的泰伦虫群——Saber的专有Swarm引擎让一切成为可能。  </p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/119969d2a3487f1a4cc5f5d4b8e0fafc.avif?t=1764091362\" width=616 height=104 /></span></p><p class=\"bb_paragraph\" style=\"text-align: center\">化身为自己的星际战士，畅享残酷不止的可重玩PvE和PvP模式，不断解锁新的技能和装扮！六种独特职业可供选择，每种职业最多可解锁25项特长，将特长添加到你的每种武器上，还有海量装甲和武器装扮用来自定义外观！  </p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/b63ef5f76f554e2d4e6e15285c778f9a.avif?t=1764091362\" width=616 height=207 /></span></p><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/ee234173e7db4fe11ff8950ab36ac15b.avif?t=1764091362\" width=616 height=104 /></span></p><p class=\"bb_paragraph\" style=\"text-align: center\"><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/f5ec2b6f71789b7aae7a252f19ac439d.poster.avif?t=1764091362\" width=610 height=224 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/f5ec2b6f71789b7aae7a252f19ac439d.webm?t=1764091362\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2183900/extras/f5ec2b6f71789b7aae7a252f19ac439d.mp4?t=1764091362\" type=\"video/mp4\"></video></span></p><p class=\"bb_paragraph\" style=\"text-align: center\">在艰难的3人围攻模式下杀穿一波又一波的泰伦虫族和混沌大军，击退无穷无尽的敌袭，尽量存活更长时间。每打败一波敌人均可积累点数，用于购买弹药、装备充能、医疗激励剂等等……或者用于召唤增援，例如卡迪亚士兵、星际战士，甚至是强大的无畏机甲。</p>', '动作,冒险,角色扮演', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,Steam 集换式卡牌,可调整文字大小,视角舒适度,可选颜色,自定义音量控制,可调整难度,立体声,字幕选项,环绕声,Steam 云,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 俄语<strong>*</strong>, 捷克语, 日语<strong>*</strong>, 韩语, 波兰语, 葡萄牙语 - 巴西, 简体中文<strong>*</strong>, 西班牙语 - 拉丁美洲, 繁体中文, 土耳其语, 乌克兰语, 泰语<br><strong>*</strong>具有完全音频支持的语言', 'Warhammer 40,000: Space Marine 2 © Games Workshop Limited 2024. Space Marine, the Space Marine logo, GW, Games Workshop, Space Marine, 40K, Warhammer, Warhammer 40,000, 40,000, the ‘Aquila\' Double-headed Eagle logo, and all associated logos, illustrations, images, names, creatures, races, vehicles, locations, weapons, characters, and the distinctive likeness thereof, are either ® or TM, and/or © Games Workshop Limited, variably registered around the world, and used under license. Focus Entertainment and its logos are trademarks, registered or not, of Focus Entertainment. Saber Interactive and its logos are trademarks, registered or not, of Saber Interactive. All rights reserved to their respective owners.', 'https://community.focus-entmt.com/focus-entertainment/space-marine-2?utm_source=Steam&amp;utm_medium=StorePage_SM2&amp;utm_campaign=Organic', 82, 'https://www.metacritic.com/game/pc/warhammer-40000-space-marine-ii?ftag=MCD-06-10aaa1f', '2183900');
INSERT INTO `game_info` VALUES (46, '数码宝贝物语 时空异客', 55.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/9090c4666985f0752bc6b5f7f406d9d4cb7d3efd/header_alt_assets_0_schinese.jpg?t=1766126651', 'Media.Vision Inc.', '2025 年 10 月 2 日', '《数码宝贝物语》系列最新作品终于登场。本作是一款角色扮演游戏，您可以在追查世界毁灭谜团的过程中，一面享受横跨人类世界与异世界的冒险，以及搜集培育种类丰富的数码宝贝。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/ss_8ab66d309e4a8b18eb41c4eeb2eb88b668ab01cc.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/ss_c059f9ac96893b96e5c6d49f89c0524d09c67689.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/ss_03fb47c9748f11cbc99216abe88a37f17c390686.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/ss_31cc952aabddc3a88c4a84c90147801d3f067b86.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/ss_e119215706a1b0469c756c0ec2148d09777e447c.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/d2b9893fda3014a80fde2747c9091187a38c7fb7/ss_d2b9893fda3014a80fde2747c9091187a38c7fb7.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/784edf007abb879cbbeb4812bc574f419be22022/ss_784edf007abb879cbbeb4812bc574f419be22022.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/ddc85d83670b9fe9d416180fb73004006275a844/ss_ddc85d83670b9fe9d416180fb73004006275a844.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/48670016a733f732bce3921be4b5b1b767d03542/ss_48670016a733f732bce3921be4b5b1b767d03542.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/514080f6473a0f7a69a3a56da51069b2f174a07e/ss_514080f6473a0f7a69a3a56da51069b2f174a07e.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/ss_460d1a133eb448788235258eaa799d9d079282c4.1920x1080.jpg?t=1766126651\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/8b282584d4e1656a95cd6150c4e633d5c74e6c21/ss_8b282584d4e1656a95cd6150c4e633d5c74e6c21.1920x1080.jpg?t=1766126651\"]', '■跨越“时空”与“世界”，描绘人类与数码宝贝之间的羁绊的故事<br>故事舞台在日本东京。<br>身为秘密组织探员的主角与未知生物“数码宝贝”邂逅，被卷进了神秘爆炸。<br>等主角回过神来时，发现世界已回溯至8年前──<br>本作中，“过去与未来”、“人类世界与异世界”、“人类与数码宝贝”──两种截然不同的要素，交织成羁绊、纠葛的壮阔故事，等您体验。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/686464f94c10e0a401b201ed3256d990.poster.avif?t=1766126651\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/686464f94c10e0a401b201ed3256d990.webm?t=1766126651\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/686464f94c10e0a401b201ed3256d990.mp4?t=1766126651\" type=\"video/mp4\"></video></span><br><br>■在人类世界与独具一格的数码世界展开冒险<br>本作有2个世界：人类世界与数码宝贝居住的异世界“数码世界・伊利亚斯”。玩家能在故事中穿梭不同世界，体验活用各个世界特性的机制与委托。数码世界则使用了过往数码宝贝游戏所没有的细腻表现手法来呈现，而且会有多个深具特色的地图登场。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/672d53cdbf5b32476a646786c6fdc840.poster.avif?t=1766126651\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/672d53cdbf5b32476a646786c6fdc840.webm?t=1766126651\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/672d53cdbf5b32476a646786c6fdc840.mp4?t=1766126651\" type=\"video/mp4\"></video></span><br><br>■以超过450只的数码宝贝的搜集与培育、战略性编组作为关键的回合制战斗<br>数码宝贝对玩家而言是对等的伙伴。在你们挺身面对困难时，你与数码宝贝的羁绊会受到考验。<br>在冒险中将数码宝贝收纳为伙伴，通过进化、退化、自定义，培育出自己专属的数码宝贝吧。本作网罗了系列熟悉的数码宝贝与在游戏初次登场的数码宝贝，请您享受与形形色色的数码宝贝的邂逅。<br><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/55285906bf0d8182cd1e938ca26a7d78.poster.avif?t=1766126651\" width=616 height=256 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/55285906bf0d8182cd1e938ca26a7d78.webm?t=1766126651\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1984270/extras/55285906bf0d8182cd1e938ca26a7d78.mp4?t=1766126651\" type=\"video/mp4\"></video></span>', '角色扮演', 1, '单人,Steam 成就,完全支持控制器,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 西班牙语 - 拉丁美洲, 葡萄牙语 - 巴西, 简体中文, 繁体中文, 日语<strong>*</strong>, 韩语<br><strong>*</strong>具有完全音频支持的语言', '©Akiyoshi Hongo, Toei Animation<br />\r<br>©Bandai Namco Entertainment Inc.', 'https://digimonstory-ts.bn-ent.net/', 0, '', '1984270');
INSERT INTO `game_info` VALUES (47, '雨中冒险 2', 8.24, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/header_schinese.jpg?t=1763490165', 'Hopoo Games', '2020 年 8 月 11 日', '浴血奋战杀出暴戾的怪物重围，在混乱的外星球上逃过劫难。可孤军奋战，也可与好友并肩作战。 以出人意料的方式合成战利品，同时精通每个角色，直至您自己成为初次迫降时曾颇为惧怕的恐怖威胁。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/ss_2bb49071317f7b241a527cf6e7aabd2cb6af055b.1920x1080.jpg?t=1763490165\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/ss_a3f57f281813cb51cb5d919701470acb962ff297.1920x1080.jpg?t=1763490165\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/ss_328d6fcb223f848c2a1047bb86702c4175d92317.1920x1080.jpg?t=1763490165\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/ss_dc777b5c583794c99440b196cd1d26884fb1720b.1920x1080.jpg?t=1763490165\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/ss_0377ff24b4d60db6a38ddc0824b7f308890b9231.1920x1080.jpg?t=1763490165\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/ss_85548e86c50ff654c6a49235ea686a956f8ee9ec.1920x1080.jpg?t=1763490165\"]', '<strong><u><br>在外星球上生存下来</u></strong><br>十几个亲手打造的场景地点恭候大驾，那里满是等待挑战的怪物和巨型Boss，力图将您剪除消灭。 奋力搏杀，直至抵达终极Boss并躲过其击杀，或无限继续游戏，看您最终能坚持生存多久。 独特的调整系统可让您和敌人的力量在一场游戏中无限增长。<br><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/extras/508f18a3257291dac1b41351d1590881.webp?t=1763490165\" width=643 height=16 /></span><br><strong><u><br>发现威力巨大的新物品</u></strong><br>超过110件物品可让每轮游戏都充满新鲜感和刺激的挑战。 收集的物品越多，合成的效力越强，其中一些合成品也就越惊艳。 发现的物品越多，您能通过日志发掘的传说或策略也就会越多。<br><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/extras/508f18a3257291dac1b41351d1590881.webp?t=1763490165\" width=643 height=16 /></span><br><strong><u><br>解锁新玩法 </u></strong><br>解锁十一位可游玩幸存者，其中的每一位都拥有着独一无二的战斗风格以及可替换的技能。了解神器的奥秘以切换友方误伤、随机幸存者生成、物品选择等游戏调整器内容。随机的关卡、敌人和物品将带来绝不重复的游戏体验。<br><br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/632360/extras/508f18a3257291dac1b41351d1590881.webp?t=1763490165\" width=643 height=16 /></span><br><strong><u><br>单人游戏或合作游戏</u></strong><br>您可以独自踏上冒险，也可以与最多三名好友一同线上合作冒险，还可以前往棱镜试炼的车轮挑战进行战斗。 船长和MUL-T等全新的幸存者将加入工程师、女猎人还有指挥官等经典幸存者之列。', '动作,独立', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,完全支持控制器,Steam 集换式卡牌,可调整文字大小,视角舒适度,自定义音量控制,可调整难度,立体声,环绕声,Steam 云,Steam 排行榜,在手机上远程畅玩,在平板上远程畅玩,在电视上远程畅玩,家庭共享', '英语, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 葡萄牙语 - 巴西, 俄语, 简体中文, 土耳其语', '© 2023 Gearbox. Published and distributed by Gearbox Publishing. Gearbox and Risk of Rain, and the Gearbox Publishing logo, are registered trademarks of Gearbox. Developed by Hopoo Games, LLC.', 'https://www.riskofrain.com/', 85, 'https://www.metacritic.com/game/pc/risk-of-rain-2?ftag=MCD-06-10aaa1f', '632360');
INSERT INTO `game_info` VALUES (48, '污痕圣杯：阿瓦隆的陨落', 31.49, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/45ac7e8a15b7b6c28f89acd88a4f9cc7d4df2a57/header.jpg?t=1765827566', 'Questline', '2025 年 5 月 23 日', '踏入一个将亚瑟王传说重新诠释的黑暗世界，体验这款第一人称开放世界的角色扮演游戏。探索一个永远沐浴在秋意中的大陆，见证传奇的衰落，并在错综复杂的故事中做出深远的选择。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/9c00e471d2cb0690a3f2b0959dc64593ef0e1eb8/ss_9c00e471d2cb0690a3f2b0959dc64593ef0e1eb8.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/64b45ad0eaf748ff8d4c9bfef51e622638902e37/ss_64b45ad0eaf748ff8d4c9bfef51e622638902e37.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/b2e345c1faf84b3027e811a51bdb439b5f231863/ss_b2e345c1faf84b3027e811a51bdb439b5f231863.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/6fffc34d2d3591a87dfaef0d083d8edf547e2ca2/ss_6fffc34d2d3591a87dfaef0d083d8edf547e2ca2.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/4045328e2c54c9045bca6c5d1e75d1ca0a035995/ss_4045328e2c54c9045bca6c5d1e75d1ca0a035995.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/161818c6233dc3408b5d9273982d4a24a66625bb/ss_161818c6233dc3408b5d9273982d4a24a66625bb.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/0acb866762c09a6ff6ea9cfcc8518563a9ff6ca6/ss_0acb866762c09a6ff6ea9cfcc8518563a9ff6ca6.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/eb1d4b1e16524068f8b6f7f6e5976f2f8ae8e01b/ss_eb1d4b1e16524068f8b6f7f6e5976f2f8ae8e01b.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/6a8463e179592f04e3e48dfa5d9c7b5003fc5eaa/ss_6a8463e179592f04e3e48dfa5d9c7b5003fc5eaa.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/4606c27bc02e4117b765cf3dfd0e0a172c2dbe7d/ss_4606c27bc02e4117b765cf3dfd0e0a172c2dbe7d.1920x1080.jpg?t=1765827566\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/3f13aedb47197ef5f8f817173d30b90bbd3a72c5/ss_3f13aedb47197ef5f8f817173d30b90bbd3a72c5.1920x1080.jpg?t=1765827566\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/7e957d8959d2a2ca2ac67bc19ec31e5d.avif?t=1765827566\" width=700 height=246 /></span></p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/3dbfd602b3966646b3931ccb5a32c754.avif?t=1765827566\" width=1232 height=706 /></span></p><h2 class=\"bb_tag\" ><strong>欢迎来到《污痕圣杯：阿瓦隆的陨落》</strong></h2><p class=\"bb_paragraph\" >走进<strong>《污痕圣杯：阿瓦隆的陨落》</strong>的世界，在这款成熟的第一人称视角开放世界RPG中，你将见证一个将亚瑟王传说重新诠释的黑暗世界。</p><p class=\"bb_paragraph\" >探索错综复杂的主线剧情，自亚瑟王的统治结束已经过去整整600年，在经历了无尽的纷争之后，你终于有机会重塑他留下的遗产。</p><p class=\"bb_paragraph\" >这款游戏是一封献给开放世界第一人称RPG的情书。<br><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/b0f72262a160955ec04154b65de8b902.avif?t=1765827566\" width=700 height=280 /></span></p><h2 class=\"bb_tag\" ><strong>关键特色</strong></h2><p class=\"bb_paragraph\" >●     <strong>海量游戏内容</strong>：在三个广阔区域中享受50-70小时的精彩游戏。</p><p class=\"bb_paragraph\" >●     <strong>错综复杂的叙事</strong>：引人入胜的主线剧情与数百个支线任务，结交无数个性鲜明而且拥有配音对话的角色。</p><p class=\"bb_paragraph\" >●     <strong>自由的游玩风格</strong>：利用不计其数的搭配来打造属于自己的独特游玩体验：组合不同的属性、技能与装备来创造你最喜欢的配置。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/45f65f6c2e54bd71e3b1101058cafcc4.avif?t=1765827566\" width=700 height=273 /></span></p><p class=\"bb_paragraph\" >阿瓦隆将带来数不尽的奥秘与发现：隐藏物品、秘密剧情、独特敌人、地牢、任务以及有趣的NPC角色。随心所欲自由探索，在这款货真价实的开放世界中开辟自己的道路。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/b7c07063b503c5dab520df9df09072af.avif?t=1765827566\" width=700 height=273 /></span></p><p class=\"bb_paragraph\" >无缝切换近战武器、盾牌、弓箭、魔法、投掷物、混合物以及其他物品。利用闪避、招架与格挡化险为夷。尝试独一无二的终极战斗风格，想方设法战胜各方强敌。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/db3a721771e24918f1aa08709bda8f20.avif?t=1765827566\" width=700 height=273 /></span></p><p class=\"bb_paragraph\" >凭借琳琅满目的属性、特性、装备以及制作选择，随心所欲塑造想要的游戏风格。</p><p class=\"bb_paragraph\" >想成为一个疯狂的炼金术士狂战士，近身肉搏？行。<br>或者是一个可以召唤亡灵大军的神秘铁匠法师？没问题。<br>还是一名徘徊于阴影之中的潜行弓箭手？无需多言，这款游戏绝对能让所有潜行弓狂热分子心满意足。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/c7f050308040db53fc53527c20d11174.avif?t=1765827566\" width=700 height=273 /></span></p><p class=\"bb_paragraph\" >领略三个截然不同的区域，见证形形色色的任务、NPC角色、物品以及更多内容：</p><p class=\"bb_paragraph\" >●     迷雾重重的<strong>Horns of the South</strong></p><p class=\"bb_paragraph\" >●     阳光明媚的<strong>Cuanacht Village</strong></p><p class=\"bb_paragraph\" >●     <strong>Forlorn Swords</strong>冰封山巅</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/00b8e6d14368ebaf9881ea4c8ea4816d.avif?t=1765827566\" width=700 height=273 /></span></p><p class=\"bb_paragraph\" >在阿瓦隆这个栩栩如生的世界中，你的旅程永远不会乏味。参与包括素描绘画、钓鱼、耕种、锻造、炼金、烹饪、采矿、房屋管理在内的丰富活动。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/d4eebeab0711e20908f547ea8caafdb1.avif?t=1765827566\" width=700 height=273 /></span></p><p class=\"bb_paragraph\" >夜幕降临，混沌的原始力量Wyrdness踏上这片大地，扭曲现实的本质。敌人们将变得更加凶狠，一旦犹豫犯错便会追悔莫及。杀戮或者被杀。</p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1466060/extras/9262011317315c32fec88ac9cd74ae4b.avif?t=1765827566\" width=700 height=273 /></span></p><p class=\"bb_paragraph\" >面对难以抉择的道德困境，这些艰难的抉择将考验你的人格并塑造阿瓦隆的未来。跟随亚瑟·潘德拉贡国王的黑暗遗产，结交超过250名NPC角色并挑战200+支线任务。</p><p class=\"bb_paragraph\" ></p>', '动作,冒险,独立,角色扮演', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,Steam 云,家庭共享', '英语<strong>*</strong>, 波兰语, 法语, 德语, 繁体中文, 简体中文, 意大利语, 西班牙语 - 西班牙, 捷克语, 日语, 葡萄牙语 - 巴西, 俄语<br><strong>*</strong>具有完全音频支持的语言', '', 'https://www.taintedgrail.com/#home', 80, 'https://www.metacritic.com/game/pc/tainted-grail-the-fall-of-avalon?ftag=MCD-06-10aaa1f', '1466060');
INSERT INTO `game_info` VALUES (49, 'Hades II', 22.49, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/91ac334a2c137d08968ccc0bc474a02579602100/header.jpg?t=1765831644', 'Supergiant Games', '2025 年 9 月 25 日', '在冥界之外奋勇战斗，驾驭黑暗巫术，与时间泰坦一决高下。在这款屡获殊荣的类 Rogue 地城探索游戏的续作中，踏上一场摄人心魄的神话之旅。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_ef0f63061d0a0a9a7e46f3b84f125d25330e8f19.1920x1080.jpg?t=1765831644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_ae77a1a82d045d44aa13cdf89681e069bd149884.1920x1080.jpg?t=1765831644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_b88cb7b48a86f07a7288bf37141f6558279f9bfc.1920x1080.jpg?t=1765831644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_c8d2b18451a2cc4d5b4fdd78ed84a5e64e051eac.1920x1080.jpg?t=1765831644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_f366f20f4fb699f6735581e04c7c45a1ef7bd1b8.1920x1080.jpg?t=1765831644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_47e14ed55ac7f1bf9266b2cd5b3e4d9eae0ca54c.1920x1080.jpg?t=1765831644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_774010d215102679e9e0ec7b414e29273679ffc2.1920x1080.jpg?t=1765831644\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/ss_93a2b3a39788031aac115b2edeebcefe585d6c8d.1920x1080.jpg?t=1765831644\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline crossorigin=\"anonymous\" poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/extras/9eb5e37616fb72c42d6c8e8e918f78d8.poster.avif?t=1765831644\" width=780 height=439 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/extras/9eb5e37616fb72c42d6c8e8e918f78d8.webm?t=1765831644\" type=\"video/webm; codecs=vp9\"></video></span></p><p class=\"bb_paragraph\" >作为 Supergiant Games 的首款续作，这款游戏汲取了前作神话级类 rogue 地城探索游戏的精髓，扎根于希腊神话中的冥界和关于巫术的传说，注定会为你带来酣畅淋漓、无穷无尽的全新体验。</p><h2 class=\"bb_tag\" >在冥界之外奋勇战斗</h2><p class=\"bb_paragraph\" >作为冥界王女，你将探索更为宏大深邃的神话世界，在奥林匹斯众神的鼎力相助下与时间泰坦抗衡。随着你的每一次失利、每一次成功，这个扣人心弦的故事也将逐渐展开……</p><h2 class=\"bb_tag\" >驾驭巫术与黑暗魔法</h2><p class=\"bb_paragraph\" >为你的传奇暗夜武器注入远古的魔法，助你在冒险中无往不利。从阿波罗到宙斯，奥林匹斯众神都会赐你祝福，让你的力量再上一层楼。构建角色能力的可能性几近无限。 </p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><video class=\"bb_img\" autoplay muted loop playsinline poster=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/extras/8a5bf90edfe16a94bdd084ab783d82dd.poster.avif?t=1765831644\" width=600 height=338 ><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/extras/8a5bf90edfe16a94bdd084ab783d82dd.webm?t=1765831644\" type=\"video/webm; codecs=vp9\"><source src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/extras/8a5bf90edfe16a94bdd084ab783d82dd.mp4?t=1765831644\" type=\"video/mp4\"></video></span></p><h2 class=\"bb_tag\" >与（更多的）神明、幽灵和怪物相遇</h2><p class=\"bb_paragraph\" >与众多完全配音的神奇角色相遇，其中既有很多新面孔，又有一些老朋友。通过一系列全新的互动方式与他们增进感情，并随着旅行的进展体验无数独特的故事。 </p><h2 class=\"bb_tag\" >每场冒险都独一无二</h2><p class=\"bb_paragraph\" >随着你一次又一次深入变幻莫测的冥界，你会发现新的地点、挑战、升级系统和众多惊喜在等着你。揭示阿卡那祭坛的秘密，驯养巫蛊宠物，并用隐形者的工具收集炼金材料，助你一步步接近终极目标。 </p><h2 class=\"bb_tag\" >永生特权</h2><p class=\"bb_paragraph\" >多亏了一系列永久升级选项，以及回归的神力模式，你不需要成为游戏之神也能体验<strong>《Hades II》</strong>中的一切。但如果你真的是游戏之神，你可以直面不断升级的挑战，赢取更多奖励，证明你的神威。 </p><p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/1145350/extras/47ef79096a49518c61cc4a7e6aaae855.avif?t=1765831644\" width=1170 height=410 /></span></p><h2 class=\"bb_tag\" >Supergiant 的标志性风格</h2><p class=\"bb_paragraph\" >Supergiant 工作室向来以恢宏大气的画面、引人入胜的叙事和电光石火的动作闻名。生动的全新手绘场景、比前作更为流畅的实时 3D 角色，以及动感十足的原声音乐让这个神话世界充满勃勃生机。</p>', '动作,独立,角色扮演', 1, '单人,Steam 成就,完全支持控制器,Steam 集换式卡牌,支持字幕,Steam 云,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 希腊语, 日语, 韩语, 波兰语, 葡萄牙语 - 巴西, 俄语, 简体中文, 繁体中文, 土耳其语, 乌克兰语<br><strong>*</strong>具有完全音频支持的语言', '© Supergiant Games, LLC 2025. All rights reserved.', 'http://www.supergiantgames.com', 94, 'https://www.metacritic.com/game/pc/hades-ii?ftag=MCD-06-10aaa1f', '1145350');
INSERT INTO `game_info` VALUES (50, '《上古卷轴®OL》', 4.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/306130/bc362fb62c4f2ad548ffebb9bf3a9817fd5701f9/header_schinese.jpg?t=1758307361', 'ZeniMax Online Studios', '2017 年 5 月 22 日', '每段传说都有它的起点，而在《上古卷轴OL》中， 你将从头开始谱写属于自己的传说。加入数百万玩家的行列， 投身于广阔而生动的《上古卷轴》世界，体验这款在线奇幻角色扮演游戏。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/306130/ss_2b797e150cbb9ad7eec9d9089e79df4adc21e797.1920x1080.jpg?t=1758307361\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/306130/ss_ae00343059f2751a9bd1385383bc065d6d72a8df.1920x1080.jpg?t=1758307361\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/306130/ss_5d7230643e643f1d1e87320f93edf109f91c2d6c.1920x1080.jpg?t=1758307361\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/306130/ss_c21f3be1e15e5fcd0244c6857acd22d37b04d161.1920x1080.jpg?t=1758307361\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/306130/ss_def2f4813a04b9ab7e8683e4dd9cfdd5326bdb2d.1920x1080.jpg?t=1758307361\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/306130/ss_8782eebb5fc397cf245c7c366e898bf2ae26b1a0.1920x1080.jpg?t=1758307361\"]', '<p class=\"bb_paragraph\" >每段传说都有它的起点，而在《上古卷轴OL》中， 你将从头开始谱写属于自己的传说。将你的故事书写成泰姆瑞尔遥远过去的生动篇章，见证发生在经典之作《TES V: Skyrim》近千年前的历史，探索充满冒险与无限可能的恢弘世界。</p><p class=\"bb_paragraph\" ><strong>丰富多彩的奇幻世界</strong>——穿越泰姆瑞尔的广阔土地，体验冒险元素满满的世界。探索晨风省的蘑菇森林，在湮灭界域与魔族敌人作战，或是前往PvP战场直面对抗其他玩家。</p><p class=\"bb_paragraph\" ><strong>众多精彩故事，在同一张画卷上演绎</strong>——沉浸式体验屡获殊荣的剧情叙事，所有情节均按照你喜欢的《上古卷轴》系列精髓精心打造。独自一人或与好友共同探索这片不断拓展的天地。</p><p class=\"bb_paragraph\" ><strong>条条大路通罗马</strong>——游戏将为你提供一系列任务和挑战，而你可以按照自己的喜好和目标随意选择：拿起钓竿在海边放松，寻找失落的宝藏，征服组队地下城或制作全新的护甲。在《上古卷轴》世界中打造你的天地——这是你的世界。</p>', '动作,冒险,大型多人在线,角色扮演', 1, '多人,大型多人在线,玩家对战,线上玩家对战,合作,在线合作,Steam 集换式卡牌,支持字幕,应用内购买,视角舒适度,自定义音量控制,无需应对快速反应事件,立体声,环绕声,部分支持控制器,可用 HDR,家庭共享', '英语<strong>*</strong>, 法语<strong>*</strong>, 德语<strong>*</strong>, 俄语, 西班牙语 - 西班牙, 简体中文<br><strong>*</strong>具有完全音频支持的语言', '©2015 ZeniMax Media Inc. The Elder Scrolls® Online: Tamriel Unlimited™ developed by ZeniMax Online Studios LLC, a ZeniMax Media company. ZeniMax, The Elder Scrolls, ESO, ESO Plus, Tamriel Unlimited, Bethesda, Bethesda Softworks and related logos are registered trademarks or trademarks of ZeniMax Media Inc. in the US and/or other countries. All Rights Reserved.<br><br>Privacy Policy', 'http://www.elderscrollsonline.com', 80, 'https://www.metacritic.com/game/pc/the-elder-scrolls-online-tamriel-unlimited?ftag=MCD-06-10aaa1f', '306130');
INSERT INTO `game_info` VALUES (51, 'NBA 2K26', 27.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/21edd260352a3a5a8aac0e340091d94c87a01287/header_alt_assets_1.jpg?t=1764346316', 'Visual Concepts', '2025 年 9 月 4 日', '辉煌生涯、梦幻球队、篮球联盟、女篮生涯和快速比赛，荣耀之争一触即发！借助由ProPLAY™提供支持的超真实技术动作施展全能攻势，在《NBA 2K26》所有竞技模式中挑战好友或劲敌，巅峰无悔，圣冠加冕。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/01e6e3303fb15a4abd013403ee80ae89b08e493a/ss_01e6e3303fb15a4abd013403ee80ae89b08e493a.1920x1080.jpg?t=1764346316\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/1c03f2ef9f5a77a360fbf8ba27319be4d2ff6629/ss_1c03f2ef9f5a77a360fbf8ba27319be4d2ff6629.1920x1080.jpg?t=1764346316\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/dc8acb92de608bd8e4468aee8cb64db7eed61027/ss_dc8acb92de608bd8e4468aee8cb64db7eed61027.1920x1080.jpg?t=1764346316\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/72b771692371a91d795a993e3d5dd7375c1ae536/ss_72b771692371a91d795a993e3d5dd7375c1ae536.1920x1080.jpg?t=1764346316\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/5cc8a288be2f322da786b9a9f5d410e6e76e465f/ss_5cc8a288be2f322da786b9a9f5d410e6e76e465f.1920x1080.jpg?t=1764346316\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/extras/2738cc57fc11c688429b85b0ad8c33a9.avif?t=1764346316\" data-big-src=https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/3472040/extras_big/2738cc57fc11c688429b85b0ad8c33a9.avif?t=1764346316 width=800 height=458 /></span></p><p class=\"bb_paragraph\" ></p><p class=\"bb_paragraph\" >辉煌生涯、梦幻球队、篮球联盟、女篮生涯和快速比赛，荣耀之争一触即发！借助由ProPLAY™提供支持的超真实技术动作施展全能攻势，在《NBA 2K26》所有竞技模式中挑战好友或劲敌，巅峰无悔，圣冠加冕。</p><h2 class=\"bb_tag\" ><strong>由ProPLAY™提供支持</strong></h2><p class=\"bb_paragraph\" >将真实NBA比赛影像直接转化为逼真赛场表现，借助技术革新沉浸式统治每个回合。全新ProPLAY技术支持焕新的花式运球动作，带来更细腻的运球手感，打造快节奏动态移动体验。</p><h2 class=\"bb_tag\" ><strong>组队称霸篮球之城</strong></h2><p class=\"bb_paragraph\" >在全新辉煌生涯旅程中打造传奇自创球员，冲击NBA巨星巅峰。在更流畅更精彩的篮球之城与好友组队，提升声望值，积累个人口碑，决战宿敌球队争夺公园场地霸主地位。</p><h2 class=\"bb_tag\" ><strong>梦幻球队传奇集结</strong></h2><p class=\"bb_paragraph\" >在梦幻球队模式中收集历代篮球传奇人物并展开对决。组建全明星阵容，在全新单人/多人游戏模式中检验梦之队实力，收集球员卡牌将梦幻球队终极构想化为现实。</p><h2 class=\"bb_tag\" ><strong>执掌球队，书写传奇</strong></h2><p class=\"bb_paragraph\" >在篮球联盟中以经理身份执掌NBA球队。30支球队随心选择，体验30条以现实事件为灵感的独特传奇经理故事线，并追逐终极目标：问鼎总冠军。左右篮球运动未来，在联盟青史留名。</p><p class=\"bb_paragraph\" >*兑换和使用奖励内容须有互联网连接和2K账户。条款适用。</p><p class=\"bb_paragraph\" >快速比赛、篮球联盟和女篮生涯可离线游玩。所有其他游戏模式和功能都须有互联网连接，并且可能需要在线注册账户（年龄要求13岁以上）。</p>', '体育', 1, '单人,多人,玩家对战,线上玩家对战,同屏/分屏玩家对战,合作,在线合作,同屏/分屏合作,同屏/分屏,Steam 成就,应用内购买,部分支持控制器,Steam 云,远程同乐,家庭共享', '英语<strong>*</strong>, 法语, 意大利语, 德语, 西班牙语 - 西班牙, 日语, 韩语, 简体中文, 繁体中文<br><strong>*</strong>具有完全音频支持的语言', '© 2005-2025 Take-Two Interactive Software, Inc.。由2K发布。2K、T2和相关标志均为Take-Two Interactive Software, Inc.的商标和/或注册商标。本产品上复制的NBA和各个NBA成员球队标识是商标和版权设计，和/或其他形式的知识产权，是NBA Properties, Inc.和各自NBA成员球队的专有财产，未经NBA Properties, Inc.事先书面同意，不得全部或部分使用。© 2025 NBA Properties, Inc.保留所有权利。NBPA标识是美国国家篮球协会球员工会的知识产权，未经National Basketball Players Incorporated © 2025 美国国家篮球协会球员工会事先书面同意，不得使用。保留所有权利。© 2025 Sony Interactive Entertainment LLC。“PlayStation Family Mark”、“PlayStation”、“PS5标志”、“PS5”、“PS4 标志”、“PS4”、“PlayStation Shapes标志”和“Play Has No Limits”是Sony Interactive Entertainment Inc.的注册商标或商标。Microsoft、Xbox Sphere标识、Series X标志、Series S标志、Series X|S标志、Xbox One、Xbox Series X、Xbox Series S和Xbox Series X|S是微软集团公司的商标。Nintendo Switch是任天堂的商标。ESRB评级图标是娱乐软件协会的商标。所有其他标志和商标均属于其各自所有者的财产。保留所有权利。', 'https://www.nba2k.com/', 0, '', '3472040');
INSERT INTO `game_info` VALUES (52, '《暗黑破坏神® IV》', 29.99, 'https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2344520/80f21a42e378b93e8fbb68ee43103be8ab84891b/header.jpg?t=1766192109', 'Blizzard Entertainment, Inc.', '2023 年 10 月 17 日', '进入《暗黑破坏神® IV》的世界，在捍卫庇护之地的战斗中，开启前所未有的动作 RPG 冒险。亲身体验备受赞誉的游戏剧情和全新赛季内容。', '[\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2344520/11d4a9be127719b22681d823b83b0c6b4798bf1f/ss_11d4a9be127719b22681d823b83b0c6b4798bf1f.1920x1080.jpg?t=1766192109\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2344520/ee4b4e1ba9c8a07f40aff563c7c43fd04477d60c/ss_ee4b4e1ba9c8a07f40aff563c7c43fd04477d60c.1920x1080.jpg?t=1766192109\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2344520/7905f91c07b2cb828d0036389bc783300350fb74/ss_7905f91c07b2cb828d0036389bc783300350fb74.1920x1080.jpg?t=1766192109\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2344520/73d3413a15dcde01592a1e8e3c998ec128ef9676/ss_73d3413a15dcde01592a1e8e3c998ec128ef9676.1920x1080.jpg?t=1766192109\", \"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2344520/14880a1527241507d9a2d26f439f83fac20c4246/ss_14880a1527241507d9a2d26f439f83fac20c4246.1920x1080.jpg?t=1766192109\"]', '<p class=\"bb_paragraph\" ><span class=\"bb_img_ctn\"><img class=\"bb_img\" src=\"https://shared.akamai.steamstatic.com/store_item_assets/steam/apps/2344520/extras/98c93b5d83e64a62b2e8949a7de8ea54.avif?t=1766192109\" width=616 height=352 /></span></p><p class=\"bb_paragraph\" >作为一款卓越的动作角色扮演游戏，Diablo® IV 能为你带来无与伦比的游戏体验——无穷无尽的恶魔等你屠戮，数不胜数的技能待你掌握，还有梦魇重重的地下城和传奇战利品供你探索与发掘。独自或与好友一同踏上战役征程，在壮美而又暗黑的世界中结交令人难忘的角色，体验扣人心弦的故事；在广阔的终极游戏中自由探索：对战经典首领获取丰厚的战利品；击退地狱狂潮中不断逼近的恶魔攻势；或者成为锻造大师，打造终极武器，培养强大角色。所有可用平台游戏互通、进度共享。 </p><h2 class=\"bb_tag\" >欢迎来到地狱</h2><p class=\"bb_paragraph\" >《暗黑破坏神》系列堪称动作角色扮演游戏的一座不朽丰碑，广受全球玩家赞誉。赶快进入本系列的最新力作，开始你的冒险吧。随着天堂和地狱之间的战争愈演愈烈，仇恨吞噬了庇护之地。邪恶开始蔓延，邪教徒和恶魔的膜拜者又开始兴风作浪，企图迎接莉莉丝的到来。只有少数勇敢者才有胆量面对这种威胁--他们誓要一扫这吞噬万物的黑暗，为庇护之地带来光明。 </p><h2 class=\"bb_tag\" >广阔的开放世界</h2><p class=\"bb_paragraph\" >在庇护之地的腐败土地上开辟出一条属于自己的道路。请记住，这里是一个不断变化的、可以完全探索的世界，充满了挑战、任务、敌人，当然也有丰厚的战利品。你可以和冒险者同伴并肩战斗，拯救被围困的城镇，也可以深入梦魇般的地下城，在抗击邪恶的过程中逐步揭开失落已久的秘密。另外，庇护之地上不仅拥有大量可供探索的区域，还有像世界首领和地狱狂潮这样的极端危险区域，会按照一定的时间周期出现在不同的地方。可以说，危险无处不在。 </p><h2 class=\"bb_tag\" >沉浸式的角色养成</h2><p class=\"bb_paragraph\" >创建并自定义你的角色，化身为游戏中的勇者，直面不断逼近的黑暗。在五个职业中做出选择，用技能、能力和美化道具打造专属于你的英雄，彰显独一无二的个人风格。在强大的技能树上不断投入，增强自身的实力，并利用巅峰面板开发出更为独特的终局游戏专长，构建独树一帜的玩法流派。 </p><h2 class=\"bb_tag\" >传奇装备，史诗战斗</h2><p class=\"bb_paragraph\" >发现并利用数量庞大的强力武器、盔甲等装备，与威胁庇护之地的恶魔展开厮杀。探索独特的装备组合，舍生忘死地投入战斗；同时不断升级物品，收集海量的强大道具，在对抗最致命的敌人时也能游刃有余。 </p><h2 class=\"bb_tag\" >赛季与事件</h2><p class=\"bb_paragraph\" >庇护之地的故事仍在继续。创建赛季角色，投身新的冒险。掌握全新机制，在每个新赛季中面临前所未有的威胁。完成赛季旅程的艰难挑战并斩获新的战令奖励。此外，当限时事件降临庇护之地时，也别忘了磨砺宝剑，共襄盛举。 </p><h2 class=\"bb_tag\" >终局游戏的无尽追求</h2><p class=\"bb_paragraph\" >在等级提高之后，你将踏足更为恐怖的世界层级，探索《暗黑破坏神® IV》更多精彩内容，迎接前所未有的巨大挑战。你可以深入魔渊，挑战最为强大的恶魔；可以勇闯难关，击败超级首领，斩获超级暗金奖励；可以趁地狱狂潮出现之际，在庇护之地各处抗击蜂拥而至的地狱大军；可以消灭世界首领，书写自己的传奇；也可以成为锻造大师，打造出登峰造极的武器，为你的英雄提供助力。</p>', '动作,角色扮演', 1, '单人,多人,玩家对战,线上玩家对战,合作,在线合作,跨平台多人,Steam 成就,完全支持控制器,应用内购买,可调整文字大小,视角舒适度,可选颜色,可调整难度,可以仅用键盘,可以仅用鼠标,朗读游戏菜单,无需应对快速反应事件,随时保存,立体声,字幕选项', '英语<strong>*</strong>, 法语<strong>*</strong>, 意大利语<strong>*</strong>, 德语<strong>*</strong>, 西班牙语 - 西班牙<strong>*</strong>, 日语<strong>*</strong>, 韩语<strong>*</strong>, 波兰语<strong>*</strong>, 葡萄牙语 - 巴西<strong>*</strong>, 俄语<strong>*</strong>, 西班牙语 - 拉丁美洲<strong>*</strong>, 繁体中文<strong>*</strong>, 简体中文<strong>*</strong>, 土耳其语<br><strong>*</strong>具有完全音频支持的语言', '*必须先在游戏中解锁坐骑或宠物功能（如适用），才能使用相应物品。<br />\r<br><br />\r<br>升级版本后，无法获得帐户中已解锁的游戏内物品。<br />\r<br><br />\r<br>需要《暗黑破坏神® IV》主游戏才能游玩。需要战网®通行证。需要互联网连接。<br />\r<br><br />\r<br>如需了解更多信息，请访问 Diablo.com。<br />\r<br><br />\r<br>© 2024 暴雪娱乐有限公司。暗黑破坏神、暗黑破坏神：不朽和 Blizzard Entertainment 是暴雪娱乐有限公司在美国和/或其他国家的商标或注册商标。', 'https://diablo4.blizzard.com/', 0, '', '2344520');

-- ----------------------------
-- Table structure for sys_user
-- ----------------------------
DROP TABLE IF EXISTS `sys_user`;
CREATE TABLE `sys_user`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `username` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '用户名',
  `password` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '密码',
  `nickname` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '昵称',
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '头像地址',
  `email` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '邮箱',
  `role` int NULL DEFAULT 0 COMMENT '角色: 0-普通用户, 1-管理员',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_username`(`username` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '用户表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of sys_user
-- ----------------------------
INSERT INTO `sys_user` VALUES (1, 'admin', '123456', '超级管理员', NULL, 'admin@steam.com', 1, '2025-12-24 22:17:58');
